                    translate([head_radius * 0.7, side * head_radius * 0.6, head_radius * 0.2]) {
                        difference() {
                            sphere(r=2);
                            sphere(r=1.5);
                        }
                    }
                }
            }
        }

        // Wings
        for(side = [-1, 1]) {
            translate([body_length/4, side * body_width/2, body_height/3]) {
                rotate([0, 0, side * 20]) {
                    wing(side);
                }
            }
        }

        // Tail segments
        translate([-body_length/2, 0, -body_height/4]) {
            tail();
        }

        // Legs
        for(side = [-1, 1]) {
            translate([body_length/4, side * body_width/2, -body_height/2]) {
                leg();
            }
        }

        // Luminous spine ridges
        color(glow_color) {
            for(pos = [-20:10:20]) {
                translate([pos, 0, body_height/2 - 2]) {
                    rotate([90, 0, 0]) {
                        difference() {
                            cylinder(h=5, r1=3, r2=1);
                            cylinder(h=5, r1=2, r2=0);
                        }
                    }
                }
            }
        }
    }
}

// Wing module with membrane structure
module wing(side) {
    color(body_color) {
        // Main wing arm
        translate([0, 0, 0]) {
            rotate([0, 90, 0]) {
                difference() {
                    cylinder(h=wing_depth/2, r=2);
                    cylinder(h=wing_depth/2, r=1);
                }
            }
        }

        // Wing membrane - hollow framework
        for(i = [0:30:120]) {
            rotate([0, 0, i * side]) {
                translate([wing_depth/2, 0, 0]) {
                    scale([1, 0.1, 0.8]) {
                        difference() {
                            sphere(r=wing_span/3 - i/3);
                            sphere(r=wing_span/3 - i/3 - wall_thickness);
                        }
                    }
                }
            }
        }

        // Wing struts
        for(strut = [30, 60, 90]) {
            rotate([0, 0, strut * side]) {
                translate([wing_depth/4, 0, 0]) {
                    rotate([0, 90, 0]) {
                        difference() {
                            cylinder(h=wing_span/3 - strut/3, r=1);
                            cylinder(h=wing_span/3 - strut/3, r=0.5);
                        }
                    }
                }
            }
        }
    }
}

// Tail module with segmented structure
module tail() {
    color(body_color) {
        for(i = [0:tail_segments-1]) {
            translate([-i * tail_length/tail_segments, 0, 0]) {
                rotate([0, 0, sin(i * 15) * 10]) {
                    scale([1 - i * 0.08, 1 - i * 0.08, 1 - i * 0.08]) {
                        difference() {
                            sphere(r=body_width/3 - i);
                            sphere(r=body_width/3 - i - wall_thickness);
                        }
                    }
                }
            }
        }
    }
}

// Horn module with spiral texture
module horn() {
    color(accent_color) {
        difference() {
            cylinder(h=horn_length, r1=2, r2=0.5);
            cylinder(h=horn_length, r1=1.5, r2=0);
        }

        // Spiral ridges
        for(i = [0:30:360]) {
            rotate([0, 0, i]) {
                translate([1.8, 0, i/360 * horn_length]) {
                    difference() {
                        cube([0.4, 0.2, horn_length/12]);
                        cube([0.3, 0.1, horn_length/12]);
                    }
                }
            }
        }
    }
}

// Leg module
module leg() {
    color(body_color) {
        // Upper leg
        difference() {
            cylinder(h=body_height/2, r=3);
            cylinder(h=body_height/2, r=2);
        }

        // Lower leg
        translate([0, 0, -body_height/2]) {
            rotate([15, 0, 0]) {
                difference() {
                    cylinder(h=body_height/3, r=2.5);
                    cylinder(h=body_height/3, r=1.5);
                }
            }
        }

        // Foot
        translate([0, 0, -body_height/2 - body_height/3]) {
            difference() {
                scale([1.5, 1, 0.5]) sphere(r=3);
                scale([1.5, 1, 0.5]) sphere(r=2);
            }

            // Claws
            for(claw = [-1, 0, 1]) {
                translate([claw * 2, 0, -1]) {
                    rotate([0, 0, claw * 15]) {
                        difference() {
                            cylinder(h=3, r1=0.8, r2=0.3);
                            cylinder(h=3, r1=0.5, r2=0);
                        }
                    }
                }
            }
        }
    }
}

