// Revenant - Undead Warrior Returned from the Grave
// Medium complexity, realistic style

$fn=32;

// Main module
module revenant() {
    color("dimgray")
    union() {
        // Torso
        translate([0, 0, 20])
        scale([1.2, 0.8, 1.5])
        sphere(r=10);

        // Chest armor plates
        difference() {
            translate([0, 0, 25])
            scale([1.3, 1, 1.8])
            cube([20, 15, 25], center=true);
            translate([0, 0, 25])
            scale([1.31, 1.01, 1.81])
            cube([19, 14, 24], center=true);
        }

        // Head
        translate([0, 0, 45])
        scale([0.8, 0.7, 1])
        sphere(r=10);

        // Jawbone exposed
        difference() {
            translate([0, -2, 38])
            cube([12, 8, 8], center=true);
            translate([0, -3, 38])
            cube([11, 6, 7], center=true);
        }

        // Eye sockets (glowing)
        color("red")
        for(x=[-3, 3]) {
            translate([x, -5, 47])
            sphere(r=2);
        }

        color("dimgray")
        // Spine protrusions
        for(i=[0:4]) {
            translate([0, 0, 20-i*5])
            rotate([90, 0, 0])
            cylinder(h=8, r1=3, r2=0.5);
        }

        // Arms
        for(side=[-1, 1]) {
            // Upper arm
            translate([side*15, 0, 35])
            rotate([0, 0, side*30])
            cylinder(h=15, r=4);

            // Lower arm
            translate([side*20, 0, 25])
            rotate([0, 0, side*45])
            cylinder(h=12, r=3);

            // Hand with bone fingers
            translate([side*26, 0, 22])
            scale([1, 0.5, 0.3])
            sphere(r=5);

            // Fingers
            for(f=[-1.5, -0.5, 0.5, 1.5]) {
                translate([side*28, f*2, 20])
                cylinder(h=6, r=1);
            }
        }

        // Legs
        for(side=[-1, 1]) {
            // Upper leg
            translate([side*8, 0, 10])
            rotate([0, 0, side*5])
            cylinder(h=20, r=5);

            // Lower leg
            translate([side*8, 0, -5])
            rotate([0, 0, side*5])
            cylinder(h=18, r=4);

            // Foot
            translate([side*8, 3, -15])
            scale([1.5, 3, 0.5])
            sphere(r=5);
        }

        // Tattered cape
        color("darkslategray")
        difference() {
            translate([0, 10, 35])
            rotate([90, 0, 0])
            cylinder(h=25, r1=20, r2=25);
            translate([0, 11, 35])
            rotate([90, 0, 0])
            cylinder(h=26, r1=19, r2=24);
        }

        // Rusted sword
        color("saddlebrown")
        translate([30, 0, 25])
        rotate([0, 0, -45])
        union() {
            // Blade
            translate([0, 0, -10])
            scale([0.3, 3, 0.1])
            cube([5, 20, 2], center=true);

            // Handle
            translate([0, 0, 2])
            cylinder(h=8, r=1.5);

            // Pommel
            translate([0, 0, 6])
            sphere(r=2.5);
        }

        // Chain fragments
        color("dimgray")
        for(i=[0:5]) {
            rotate([0, 0, i*60])
            translate([12, 0, 20-i*3])
            scale([0.3, 0.3, 1])
            sphere(r=3);
        }
    }
}

// Generate the revenant
revenant();

// Specter - Ghostly Mythical Creature
// Medium complexity realistic style

// Overall dimensions: ~80mm height, 60mm width, 40mm depth

// Main colors
specter_color = [0.9, 0.9, 1.0, 0.7];
glow_color = [0.3, 0.3, 0.8, 0.5];
bone_color = [0.9, 0.9, 0.8, 1.0];

// Main module
module specter() {
    // Base body
    color(specter_color) {
        // Lower ethereal body
        translate([0, 0, 0]) {
            scale([1, 0.7, 2]) {
                sphere(15);
            }
        }

        // Upper torso
        translate([0, 0, 25]) {
            scale([0.8, 0.6, 1.5]) {
                sphere(12);
            }
        }

        // Head
        translate([0, 0, 45]) {
            scale([0.7, 0.7, 0.9]) {
                sphere(10);
            }
        }
    }

    // Hood/cowl
    color(specter_color) {
        translate([0, 0, 42]) {
            rotate([90, 0, 0]) {
                cylinder(h=15, r1=12, r2=20, $fn=16);
            }
        }
    }

    // Skull face
    color(bone_color) {
        translate([0, -5, 45]) {
            scale([0.6, 0.4, 0.7]) {
                sphere(8);
            }
        }

        // Eye sockets
        for(x = [-3, 3]) {
            translate([x, -8, 47]) {
                color([0, 0, 0, 1])
                sphere(2);
            }
        }

        // Nasal cavity
        translate([0, -8, 44]) {
            color([0, 0, 0, 1])
            rotate([90, 0, 0])
            cylinder(h=5, r1=1, r2=2, $fn=6);
        }
    }

    // Arms
    for(side = [-1, 1]) {
        color(specter_color) {
            translate([side * 15, 0, 30]) {
                rotate([0, side * 10, 0]) {
                    scale([0.3, 0.3, 1.8]) {
                        sphere(10);
                    }
                }
            }

            // Hands
            translate([side * 20, 0, 15]) {
                scale([0.4, 0.2, 0.6]) {
                    sphere(5);
                }
            }
        }

        // Bones in sleeves
        color(bone_color) {
            translate([side * 18, 0, 25]) {
                scale([0.1, 0.1, 0.8]) {
                    sphere(10);
                }
            }
        }
    }

    // Flowing ethereal tail
    color(specter_color) {
        for(i = [0:5]) {
            translate([0, 0, -i * 5]) {
                scale([1 - i * 0.1, 1 - i * 0.1, 0.5]) {
                    sphere(15 - i * 2);
                }
            }
        }
    }

    // Ghostly chains
    color([0.3, 0.3, 0.3, 0.8]) {
        chain_links();
    }

    // Ethereal glow particles
    color(glow_color) {
        for(i = [0:12]) {
            rotate([0, 0, i * 30]) {
                translate([15 + sin(i * 2) * 5, 0, 20 + cos(i * 3) * 10]) {
                    sphere(1);
                }
            }
        }
    }
}

// Chain links module
module chain_links() {
    for(i = [0:3]) {
        translate([8 + i * 4, 0, 10 - i * 3]) {
            rotate([90, 0, 45 + i * 30]) {
                torus(2, 0.5, $fn=8);
            }
        }

        translate([-8 - i * 4, 0, 10 - i * 3]) {
            rotate([90, 0, -45 - i * 30]) {
                torus(2, 0.5, $fn=8);
            }
        }
    }
}

// Torus helper
module torus(r1, r2, fn=16) {
    rotate_extrude($fn=fn) {
        translate([r1, 0, 0]) {
            circle(r2, $fn=fn);
        }
    }
}

// Render the specter
specter();

// Shade mythical creature - realistic style, medium complexity
// A shadowy humanoid figure with ethereal wings and dark magical aura

// Color definitions
shadow = [0.1, 0.1, 0.15, 0.9];
dark = [0.05, 0.05, 0.1, 0.95];
ethereal = [0.2, 0.15, 0.3, 0.7];

// Main module
module shade_creature() {
    union() {
        // Body core
        color(shadow)
        translate([0, 0, 10])
        scale([1.2, 0.8, 2])
        sphere(10, $fn=20);

        // Head
        color(dark)
        translate([0, 0, 35])
        scale([0.8, 0.7, 1])
        sphere(8, $fn=20);

        // Hood/cowl
        color(dark)
        translate([0, 0, 37])
        rotate([0, 180, 0])
        cylinder(h=12, r1=12, r2=6, $fn=30);

        // Arms
        for(side = [-1, 1]) {
            // Upper arms
            color(shadow)
            translate([side * 12, 0, 25])
            rotate([0, 0, side * 15])
            scale([0.5, 0.5, 1.5])
            sphere(6, $fn=15);

            // Lower arms
            color(dark)
            translate([side * 18, 0, 15])
            rotate([0, 0, side * 25])
            scale([0.4, 0.4, 1.2])
            sphere(5, $fn=15);

            // Hands
            color(dark)
            translate([side * 22, 0, 8])
            scale([0.8, 0.3, 0.3])
            sphere(4, $fn=10);
        }

        // Legs
        for(side = [-1, 1]) {
            color(shadow)
            translate([side * 5, 0, 5])
            scale([0.6, 0.5, 1.8])
            sphere(7, $fn=15);

            // Feet
            color(dark)
            translate([side * 5, 0, -2])
            scale([1, 0.8, 0.5])
            sphere(6, $fn=10);
        }

        // Ethereal wings
        for(side = [-1, 1]) {
            wing(side);
        }

        // Magical aura particles
        for(i = [0:35]) {
            aura(i);
        }
    }
}

// Wing module with ethereal membrane
module wing(side) {
    translate([side * 15, 0, 25]) {
        rotate([0, side * 20, 0]) {
            // Wing bone structure
            for(j = [0:4]) {
                color(dark)
                rotate([0, 0, j * 15 * side])
                translate([j * 3, 0, 0])
                scale([0.3, 0.3, 8 - j])
                cylinder(h=8 - j, r=0.5, $fn=8);
            }

            // Wing membrane
            color(ethereal)
            translate([0, 0, -2])
            rotate([90, 0, 0])
            linear_extrude(height=0.5)
            polygon(points=[
                [0, 0],
                [side * 15, 5],
                [side * 12, 15],
                [side * 8, 20],
                [side * 4, 22],
                [0, 20]
            ]);
        }
    }
}

// Magical aura particles
module aura(index) {
    angle = index * 10;
    radius = 25 + (index % 3) * 5;
    height = 15 + sin(index * 15) * 10;

    color(ethereal)
    translate([
        cos(angle) * radius,
        sin(angle) * radius,
        height
    ])
    sphere(0.8 + (index % 2) * 0.5, $fn=6);
}

// Create the creature
shade_creature();

// Wight Mythical Creature - Realistic Style
// Medium complexity OpenSCAD model

// Main dimensions (mm)
body_height = 60;
body_width = 25;
head_size = 15;
wing_span = 70;
leg_length = 25;
arm_length = 30;

// Color definitions
$fa = 1;
$fs = 0.4;

module wight_creature() {
    color([0.7, 0.75, 0.85]) // Pale bluish-gray

    // Main body assembly
    union() {
        // Torso
        translate([0, 0, leg_length]) {
            difference() {
                sphere(d = body_width);
                translate([0, 0, -body_width/2]) 
                    cube([body_width*1.2, body_width, body_width], center = true);
            }
        }

        // Head
        translate([0, 0, leg_length + body_height/2 + head_size/2]) {
            sphere(d = head_size);

            // Eye sockets
            for(x = [-head_size/4, head_size/4])
                translate([x, head_size/3, 0]) {
                    rotate([0, 90, 0])
                        cylinder(h = head_size/2, d = head_size/5, center = true);
                }

            // Jaw
            translate([0, head_size/2 - 2, -head_size/4]) {
                rotate([30, 0, 0])
                    cube([head_size*0.8, head_size*0.6, 3], center = true);
            }
        }

        // Arms
        for(side = [-1, 1]) {
            translate([side * body_width/2, 0, leg_length + body_height/3]) {
                rotate([0, side * 20, 0]) {
                    // Upper arm
                    cylinder(h = arm_length/2, d = body_width/3, center = true);

                    // Forearm
                    translate([0, arm_length/2, 0]) {
                        rotate([0, 60, 0])
                            cylinder(h = arm_length/2, d = body_width/4, center = true);
                    }

                    // Hand
                    translate([0, arm_length - 2, 0]) {
                        sphere(d = body_width/3);

                        // Fingers
                        for(f = [-1, 0, 1]) {
                            translate([f * 2, body_width/4, 0]) {
                                rotate([90, 0, 0])
                                    cylinder(h = 8, d = 2);
                            }
                        }
                    }
                }
            }
        }

        // Legs
        for(side = [-1, 1]) {
            translate([side * body_width/3, 0, leg_length/2]) {
                // Thigh
                cylinder(h = leg_length/2, d = body_width/3);

                // Lower leg
                translate([0, 0, leg_length/4]) {
                    rotate([15, 0, 0])
                        cylinder(h = leg_length/2, d = body_width/4);
                }

                // Foot
                translate([0, body_width/3, -leg_length/4]) {
                    cube([body_width/2, 10, 4], center = true);
                }
            }
        }

        // Wings
        color([0.6, 0.65, 0.8, 0.8]) {
            for(side = [-1, 1]) {
                translate([side * body_width/2, -body_width/4, leg_length + body_height*0.7]) {
                    rotate([0, 0, side * -30]) {
                        // Wing base
                        cylinder(h = 5, d = body_width/2);

                        // Wing membrane supports
                        for(i = [1:3]) {
                            rotate([0, 0, side * -i * 25]) {
                                translate([i * wing_span/6, 0, 0]) {
                                    cube([3, wing_span/4, 1]);
                                }
                            }
                        }

                        // Wing membrane
                        translate([wing_span/4, 0, 0]) {
                            scale([1, 0.3, 1])
                                sphere(d = wing_span/2);
                        }
                    }
                }
            }
        }

        // Ragged clothing remnants
        color([0.3, 0.3, 0.35]) {
            translate([0, body_width/2, leg_length + body_height/4]) {
                for(i = [1:5]) {
                    rotate([0, 0, i * 72]) {
                        translate([0, 0, -i]) {
                            cube([body_width/1.5, 2, body_height/2], center = true);
                        }
                    }
                }
            }
        }
    }
}

// Display the creature
wight_creature();

// Mummy Mythical Creature - Medium Complexity
// Realistic style with Egyptian details and magical elements
// Size: ~80mm tall, 3D printable with no overhangs

// Color definitions
bandage_color = [0.85, 0.8, 0.7];
gold_color = [0.8, 0.7, 0.2];
emerald_color = [0.1, 0.7, 0.4];

// Main module
module mummy_creature() {
    // Base platform
    translate([0, 0, -2]) {
        color(gold_color)
        cylinder(h=2, r1=22, r2=25, $fn=64);
    }

    // Body - wrapped torso
    color(bandage_color)
    union() {
        // Main torso
        translate([0, 0, 12])
            scale([1.2, 0.8, 1])
            sphere(10, $fn=32);

        // Chest bandages
        for(i = [0:15]) {
            rotate([0, 0, i * 24])
            translate([0, 0, 10 + i * 1.5])
            rotate_extrude($fn=32)
            translate([8, 0, 0])
            square([2, 1]);
        }

        // Lower body
        translate([0, 0, 2])
            cylinder(h=20, r1=8, r2=6, $fn=32);
    }

    // Head with bandages
    translate([0, 0, 28]) {
        color(bandage_color)
        difference() {
            sphere(8, $fn=32);
            // Eye sockets
            translate([-3, 4, 2])
                sphere(1.5, $fn=16);
            translate([3, 4, 2])
                sphere(1.5, $fn=16);
        }

        // Bandage wraps on head
        for(i = [0:7]) {
            rotate([0, 90 + i * 12, 0])
            translate([0, 0, 6])
            rotate_extrude($fn=32)
            translate([7, 0, 0])
            square([1.5, 0.5]);
        }

        // Magical scarab amulet
        color(emerald_color)
        translate([0, 8, 0])
            rotate([90, 0, 0])
            cylinder(h=2, r1=3, r2=2, $fn=6);
    }

    // Arms wrapped in bandages
    module wrapped_arm(side) {
        translate([side * 12, 0, 24]) {
            // Upper arm
            rotate([0, 0, side * -30])
            cylinder(h=10, r=2, $fn=16);

            // Lower arm
            translate([side * 9, 0, -4])
            rotate([0, 0, side * 30])
            cylinder(h=12, r=1.8, $fn=16);

            // Hand
            translate([side * 19, 0, -4])
            sphere(2.5, $fn=16);

            // Bandage wraps
            for(i = [0:5]) {
                translate([side * (3 + i * 2.5), 0, 22 - i * 2])
                rotate([90, 0, 0])
                cylinder(h=0.5, r1=2.5, r2=2, $fn=16);
            }
        }
    }

    wrapped_arm(1);
    wrapped_arm(-1);

    // Legs wrapped in bandages
    module wrapped_leg(side) {
        translate([side * 6, 0, 8]) {
            // Upper leg
            cylinder(h=15, r=3, $fn=16);

            // Lower leg
            translate([0, 0, -15])
            cylinder(h=15, r=2.5, $fn=16);

            // Foot
            translate([0, 2, -29])
            cube([5, 8, 3], center=true);

            // Bandage details
            for(i = [0:8]) {
                translate([0, 0, 8 - i * 2.5])
                rotate([0, 90, 0])
                cylinder(h=0.5, r1=3.5, r2=3, $fn=16);
            }
        }
    }

    wrapped_leg(1);
    wrapped_leg(-1);

    // Magical wings (wrapped)
    module wing(side) {
        translate([side * 15, 0, 35]) {
            rotate([0, side * 20, 0])
            color(bandage_color)
            difference() {
                // Wing shape
                scale([1, 0.3, 0.8])
                sphere(12, $fn=32);

                // Cutout pattern
                for(i = [1:3]) {
                    translate([0, 0, -i * 4])
                    scale([0.7, 1, 0.6])
                    sphere(8 - i, $fn=32);
                }
            }

            // Magical runes
            color(gold_color)
            for(i = [0:2]) {
                translate([side * (8 - i * 3), 0, 30 - i * 5])
                scale([0.3, 0.3, 0.1])
                sphere(3, $fn=6);
            }
        }
    }

    wing(1);
    wing(-1);

    // Ankh staff in right hand
    translate([19, 0, -4]) {
        color(gold_color)
        // Staff
        cylinder(h=35, r=1, $fn=16);

        // Ankh top
        translate([0, 0, 35])
        union() {
            // Loop
            rotate_extrude($fn=32)
            translate([2, 0, 0])
            square([1, 2]);

            // Cross bar
            translate([0, -2, 0])
            cube([4, 1, 2]);
        }
    }
}

// Render the mummy
mummy_creature();

// Phantom Dragon - A spectral wyrm with ethereal wings and ghostly features
// Designed for 3D printing at ~80mm length

// Main colors
phantom_body = [0.9, 0.9, 1, 0.7];
phantom_wing = [0.8, 0.8, 1, 0.5];
phantom_eyes = [0.5, 0.5, 1, 1];

// Overall scale factor
scale_factor = 0.8;

// Main phantom body
module phantom_dragon() {
    scale([scale_factor, scale_factor, scale_factor]) {
        union() {
            // Ghostly body
            color(phantom_body)
            translate([0, 0, 20])
                scale([1.2, 0.8, 2])
                sphere(15, $fn=32);

            // Head with snout
            color(phantom_body)
            union() {
                translate([-25, 0, 35])
                    scale([1.5, 1, 1])
                    sphere(10, $fn=32);

                // Snout
                translate([-40, 0, 35])
                    rotate([0, 20, 0])
                    cylinder(h=15, r1=8, r2=5, $fn=16);

                // Horns
                translate([-30, 8, 45])
                    rotate([30, 0, 30])
                    cylinder(h=12, r1=3, r2=1, $fn=8);
                translate([-30, -8, 45])
                    rotate([30, 0, -30])
                    cylinder(h=12, r1=3, r2=1, $fn=8);
            }

            // Ethereal wings
            for(side = [-1, 1]) {
                color(phantom_wing)
                translate([-10, side*20, 40])
                    rotate([0, 0, side*20])
                    phantom_wing(side);
            }

            // Tail
            color(phantom_body)
            translate([20, 0, 15])
                rotate([0, -20, 0])
                tail_segment(1);

            // Legs with claws
            for(side = [-1, 1]) {
                color(phantom_body)
                translate([-5, side*12, 10])
                    rotate([0, 0, side*20])
                    phantom_leg();

                translate([15, side*10, 5])
                    rotate([0, 0, side*15])
                    phantom_leg();
            }

            // Glowing eyes
            color(phantom_eyes)
            for(side = [-1, 1]) {
                translate([-38, side*5, 38])
                    sphere(3, $fn=16);
            }
        }
    }
}

// Phantom wing module
module phantom_wing(side) {
    union() {
        // Main wing membrane
        difference() {
            hull() {
                translate([0, 0, 0])
                    sphere(2, $fn=8);
                translate([0, 30, 0])
                    sphere(1, $fn=8);
                translate([0, 15, 15])
                    sphere(1, $fn=8);
            }

            // Cutouts for ethereal effect
            for(i = [1:3]) {
                translate([i*5, i*8, 5])
                    rotate([0, 10, 0])
                    sphere(4, $fn=16);
            }
        }

        // Wing bones
        for(i = [0:3]) {
            rotate([0, 0, i*20])
                translate([0, 5, 0])
                    cylinder(h=25, r=1, $fn=8);
        }
    }
}

// Tail segments
module tail_segment(level) {
    if(level <= 5) {
        translate([level*8, 0, 0])
            scale([1 - level*0.15, 1 - level*0.15, 1 - level*0.15])
            union() {
                sphere(8, $fn=24);
                tail_segment(level + 1);
            }
    }
}

// Phantom leg module
module phantom_leg() {
    union() {
        // Upper leg
        cylinder(h=15, r=4, $fn=16);

        // Lower leg
        translate([0, 0, -15])
            rotate([30, 0, 0])
            cylinder(h=12, r=3, $fn=16);

        // Foot with claws
        translate([0, 0, -27])
            rotate([60, 0, 0])
            union() {
                cylinder(h=8, r=3, $fn=16);

                // Claws
                for(angle = [0:120:240]) {
                    rotate([0, 0, angle])
                        translate([4, 0, -4])
                        rotate([0, -30, 0])
                        cylinder(h=5, r1=2, r2=0.5, $fn=8);
                }
            }
    }
}

// Create the phantom dragon
phantom_dragon();

// Poltergeist - Realistic Style, Medium Complexity
// Floating ghostly figure with chains, glowing eyes, and ethereal wisps

// Overall scale factor
scaleFactor = 0.8;

// Main body module
module poltergeist() {
    // Translucent ghostly body
    color([0.9, 0.9, 1.0, 0.6]) {
        // Head
        translate([0, 0, 40]) {
            sphere(8);

            // Glowing eyes
            color([1, 0.3, 0, 1]) {
                translate([3, 4, 2]) sphere(1.5);
                translate([-3, 4, 2]) sphere(1.5);
            }

            // Hollow mouth
