            for (i = [0:5:20]) {
                translate([sin(i * side) * 8, 0, -i])
                scale([1 - i/30, 0.1, 1])
                sphere(8 - i/2);
            }

            // Wing tears
            for (i = [5:10:25]) {
                translate([sin(i * side) * 6, 0, -i])
                color("#1a1a1a")
                sphere(2);
            }
        }
    }

    wing(1);
    wing(-1);

    // Tail - long and whip-like
    color("#4a3f45")
    translate([0, 0, -15])
    union() {
        for (i = [0:2:20]) {
            translate([0, 0, -i])
            scale([1 - i/30, 1 - i/30, 1])
            sphere(2);
        }

        // Tail spike
        color("#f5f5dc")
        translate([0, 0, -22])
        rotate([0, 0, 0])
        cylinder(h = 5, r1 = 2, r2 = 0.5);
    }

    // Spinal ridge
    for (i = [0:2:25]) {
        translate([0, 0, 25 - i])
        rotate([90, 0, 0])
        color("#f5f5dc")
        scale([0.8, 0.8, 1])
        cylinder(h = 3, r1 = 1.5, r2 = 0.5);
    }
}

// Bonefiend - A skeletal demon with wings and horns
// Medium complexity realistic style

// Color definitions
color_bone = [0.95, 0.9, 0.8];
color_dark = [0.2, 0.2, 0.2];
color_glow = [0.8, 0.3, 0.1];

// Main creature
module bonefiend() {
    union() {
        // Skull head
        translate([0, 0, 60]) skull();

        // Spine
        translate([0, 0, 30]) spine();

        // Ribcage
        translate([0, 0, 40]) ribcage();

        // Pelvis
        translate([0, 0, 10]) pelvis();

        // Arms
        translate([-15, 0, 45]) arm(true);
        translate([15, 0, 45]) arm(false);

        // Legs
        translate([-8, 0, 5]) leg(true);
        translate([8, 0, 5]) leg(false);

        // Wings
        translate([0, 0, 50]) wings();

        // Tail
        translate([0, -15, 15]) tail();
    }
}

// Skull module
module skull() {
    color(color_bone) {
        // Main skull
        difference() {
            sphere(d=20);
            // Eye sockets
            translate([-5, 7, 2]) sphere(d=6);
            translate([5, 7, 2]) sphere(d=6);
            // Nose cavity
            translate([0, 8, -2]) sphere(d=4);
        }

        // Horns
        translate([-5, 0, 8]) horn(true);
        translate([5, 0, 8]) horn(false);

        // Jaw
        translate([0, 12, -8]) {
            rotate([0, 20, 0]) cube([12, 4, 8], center=true);
        }
    }
}

// Horn module
module horn(left=true) {
    color(color_bone) {
        for(i=[0:5]) {
            translate([0, 0, i*2]) {
                rotate([0, left ? 10 : -10, left ? 5 : -5]) 
                cylinder(h=2, d1=4-i*0.3, d2=3.5-i*0.3);
            }
        }
    }
}

// Spine module
module spine() {
    color(color_bone) {
        for(i=[0:9]) {
            translate([0, 0, i*3]) {
                scale([0.8, 0.8, 1]) 
                cylinder(h=3, d=8-i*0.4);
            }
        }
    }
}

// Ribcage module
module ribcage() {
    color(color_bone) {
        for(i=[-2:2]) {
            rotate([0, 0, i*30]) {
                translate([0, 5, 0]) {
                    scale([1, 1.2, 0.3]) 
                    cylinder(h=20, d=2);
                }
            }
        }
    }
}

// Pelvis module
module pelvis() {
    color(color_bone) {
        union() {
            cube([20, 10, 8], center=true);
            translate([0, 5, 0]) cube([30, 5, 6], center=true);
        }
    }
}

// Arm module
module arm(left=true) {
    color(color_bone) {
        // Upper arm
        translate([left ? -5 : 5, 0, 0]) {
            rotate([0, 0, left ? -20 : 20]) 
            cylinder(h=15, d=4);

            // Forearm
            translate([0, 0, -15]) {
                rotate([0, 0, left ? -30 : 30]) 
                cylinder(h=12, d=3);

                // Hand
                translate([0, 0, -12]) {
                    // Palm
                    cube([6, 3, 8], center=true);

                    // Fingers
                    for(i=[-2:2]) {
                        translate([i*2, 2, -3]) {
                            scale([0.4, 0.4, 1]) 
                            cylinder(h=6, d=2);
                        }
                    }
                }
            }
        }
    }
}

// Leg module
module leg(left=true) {
    color(color_bone) {
        // Upper leg
        cylinder(h=15, d=5);

        // Lower leg
        translate([0, 0, -15]) {
            cylinder(h=12, d=4);

            // Foot
            translate([0, 3, -12]) {
                cube([5, 8, 3], center=true);

                // Toes
                for(i=[-1.5:1.5]) {
                    translate([i*1.5, 5, 0]) {
                        scale([0.3, 1, 0.5]) 
                        cube([2, 4, 2]);
                    }
                }
            }
        }
    }
}

// Wings module
module wings() {
    color(color_bone) {
        // Wing bones
        for(side=[-1, 1]) {
            translate([side*10, 0, 0]) {
                rotate([0, 0, side*30]) {
                    // Main wing bone
                    cylinder(h=25, d=3);

                    // Wing fingers
                    for(i=[0:3]) {
                        rotate([0, 0, i*30*side]) {
                            translate([0, 0, -5]) {
                                cylinder(h=20-i*3, d=2-i*0.3);
                            }
                        }
                    }
                }
            }
        }

        // Wing membrane
        color([0.1, 0.1, 0.1, 0.5]) {
            for(side=[-1, 1]) {
                translate([side*10, 0, 0]) {
                    rotate([90, 0, side*30]) {
                        linear_extrude(height=0.5) {
                            polygon(points=[
                                [0, 0],
                                [15*side, 5],
                                [25*side, 15],
                                [20*side, 25],
                                [10*side, 20],
                                [0, 0]
                            ]);
                        }
                    }
                }
            }
        }
    }
}

// Tail module
module tail() {
    color(color_bone) {
        for(i=[0:8]) {
            translate([0, -i*2, -i*1.5]) {
                rotate([0, i*5, 0]) {
                    cylinder(h=2, d=6-i*0.5);
                }
            }
        }

        // Tail spike
        translate([0, -18, -12]) {
            rotate([0, 0, 180]) 
            cylinder(h=8, d1=0, d2=4);
        }
    }
}

