// Soulreaver - Mythical Creature
// Realistic style, medium complexity

// Main parameters
scale_factor = 0.8;
body_height = 60 * scale_factor;
body_width = 25 * scale_factor;
head_size = 15 * scale_factor;
wing_span = 70 * scale_factor;
tail_length = 40 * scale_factor;

// Colors
$fn = 32;

module soulreaver_body() {
    // Main torso
    color([0.2, 0.2, 0.3])
    union() {
        // Chest
        translate([0, 0, body_height/2])
        scale([1.2, 0.8, 1.5])
        sphere(body_width/2);

        // Abdomen
        translate([0, 0, body_height/4])
        scale([0.8, 0.6, 1.2])
        sphere(body_width/2 * 0.9);

        // Spinal ridge
        for(i = [0:5]) {
            translate([0, body_width/3, body_height/2 - i*8])
            rotate([0, 0, 45])
            scale([0.3, 0.3, 1])
            cylinder(h=5, r1=3, r2=1);
        }
    }
}

module soulreaver_head() {
    color([0.15, 0.15, 0.25])
    union() {
        // Skull
        translate([0, 0, body_height + head_size/2])
        scale([0.9, 0.7, 1.2])
        sphere(head_size);

        // Jaw
        translate([0, -head_size/2, body_height + head_size/3])
        rotate([20, 0, 0])
        cube([head_size*0.8, head_size*0.4, head_size*0.6], center=true);

        // Horns
        for(side = [-1, 1]) {
            translate([side * head_size/2, 0, body_height + head_size*1.2])
            rotate([0, side * 15, 0])
            cylinder(h=head_size*1.5, r1=2, r2=0.5);
        }

        // Eye sockets
        for(side = [-1, 1]) {
            translate([side * head_size/3, -head_size/3, body_height + head_size*0.8])
            color([0.8, 0, 0])
            sphere(2);
        }
    }
}

module soulreaver_wings() {
    color([0.1, 0.1, 0.2])
    union() {
        for(side = [-1, 1]) {
            // Wing base
            translate([side * body_width/1.5, -body_width/2, body_height*0.8])
            rotate([0, 0, side * 30])
            scale([1, 0.2, 2])
            sphere(body_width/2);

            // Wing membrane structure
            for(i = [0:3]) {
                translate([side * (body_width/2 + i*wing_span/8), -body_width/4, body_height*0.8 - i*5])
                rotate([0, 0, side * (20 + i*10)])
                scale([0.3, 0.1, 1])
                cylinder(h=wing_span/4, r1=1, r2=0.5);
            }

            // Wing claws
            translate([side * (body_width/2 + wing_span/2), -body_width/3, body_height*0.6])
            scale([0.5, 0.2, 1.5])
            sphere(3);
        }
    }
}

module soulreaver_arms() {
    color([0.25, 0.25, 0.35])
    union() {
        for(side = [-1, 1]) {
            // Upper arm
            translate([side * body_width/1.8, 0, body_height*0.7])
            rotate([0, 0, side * 30])
            cylinder(h=body_width/1.5, r1=3, r2=2);

            // Forearm
            translate([side * (body_width/1.8 + body_width/1.5*cos(30)), 
                      -body_width/1.5*sin(30)/2, body_height*0.7 - body_width/1.5*sin(30)/2])
            rotate([0, 0, side * 60])
            cylinder(h=body_width/1.3, r1=2.5, r2=2);

            // Claws
            translate([side * (body_width/1.8 + body_width/1.5*cos(30) + body_width/1.3*cos(60)), 
                      -body_width/1.5*sin(30)/2 - body_width/1.3*sin(60)/2, 
                      body_height*0.7 - body_width/1.5*sin(30)/2 - body_width/1.3*sin(60)/2])
            scale([0.8, 0.5, 2])
            sphere(2.5);
        }
    }
}

module soulreaver_legs() {
    color([0.3, 0.3, 0.4])
    union() {
        for(side = [-1, 1]) {
            // Thigh
            translate([side * body_width/3, 0, body_height/4])
            rotate([30, 0, side * 10])
            cylinder(h=body_width/1.5, r1=3.5, r2=2.5);

            // Lower leg
            translate([side * (body_width/3 + body_width/1.5*sin(10)), 
                      0, body_height/4 - body_width/1.5*cos(30)])
            rotate([60, 0, side * 10])
            cylinder(h=body_width/1.3, r1=2.5, r2=2);

            // Hoof
            translate([side * (body_width/3 + body_width/1.5*sin(10) + body_width/1.3*sin(10)), 
                      0, body_height/4 - body_width/1.5*cos(30) - body_width/1.3*cos(60)])
            scale([1.2, 0.8, 0.5])
            sphere(3);
        }
    }
}

module soulreaver_tail() {
    color([0.2, 0.2, 0.3])
    union() {
        // Tail segments
        for(i = [0:4]) {
            translate([0, -body_width/3 - i*tail_length/5, body_height/2 - i*5])
            rotate([0, 0, -i*5])
            scale([1 - i*0.15, 1 - i*0.15, 0.8])
            sphere(body_width/2 - i*1.5);
        }

        // Tail blade
        translate([0, -body_width/3 - tail_length, body_height/2 - 20])
        rotate([0, 0, -20])
        scale([0.5, 2, 0.1])
        sphere(5);
    }
}

module soulreaver_scythe() {
    color([0.4, 0.4, 0.5])
    union() {
        // Scythe handle
        translate([body_width*0.8, -body_width/2, body_height*0.9])
        rotate([0, 90, 45])
        cylinder(h=body_width*1.5, r1=2, r2=1.5);

        // Scythe blade
        translate([body_width*1.8, -body_width/2 - 5, body_height*0.9])
        rotate([0, 90, 135])
        scale([0.1, 2, 1])
        sphere(8);

        // Soul glow
        translate([body_width*1.8, -body_width/2 - 15, body_height*0.9])
        color([0.5, 0, 0.8])
        sphere(3);
    }
}

// Main assembly
union() {
    soulreaver_body();
    soulreaver_head();
    soulreaver_wings();
    soulreaver_arms();
    soulreaver_legs();
    soulreaver_tail();
    soulreaver_scythe();
}

