// Main body module
module blackwight_body() {
    // Central torso with organic shaping
    difference() {
        // Main torso - elongated teardrop shape
        scale([1, 1.2, 1.5]) sphere(d=25, $fn=16);

        // Hollow out interior for printing
        scale([0.8, 0.8, 0.9]) sphere(d=22, $fn=16);

        // Neck socket
        translate([0, -8, 12]) rotate([45, 0, 0]) 
            cylinder(h=15, d1=10, d2=8, $fn=8);
    }

    // Chest ribcage ridges
    for(i = [0:2]) {
        translate([0, -5 + i*3, 5 + i*4])
            rotate([70 - i*10, 0, 0])
            scale([0.8, 0.3, 1]) sphere(d=12, $fn=8);
    }
}

// Head module with draconic features
module blackwight_head() {
    translate([0, -15, 25]) rotate([45, 0, 0]) {
        // Skull structure
        difference() {
            scale([1, 1.3, 1.2]) sphere(d=15, $fn=16);

            // Eye sockets
            translate([5, 2, 2]) sphere(d=4, $fn=8);
            translate([-5, 2, 2]) sphere(d=4, $fn=8);

            // Nasal cavity
            translate([0, 5, -2]) rotate([0, 30, 0])
                cylinder(h=8, d1=3, d2=1, $fn=6);
        }

        // Horns - curved back
        translate([6, -3, 6]) rotate([0, 30, 15])
            scale([0.3, 0.3, 1]) cylinder(h=12, d1=4, d2=1, $fn=6);
        translate([-6, -3, 6]) rotate([0, 30, -15])
            scale([0.3, 0.3, 1]) cylinder(h=12, d1=4, d2=1, $fn=6);

        // Lower jaw
        translate([0, 3, -6]) rotate([30, 0, 0])
            scale([0.8, 0.6, 0.5]) sphere(d=12, $fn=12);
    }
}

// Ethereal wing membrane
module blackwight_wing() {
    // Wing arm structure
    translate([0, 0, 0]) rotate([0, 0, 0]) {
        // Main wing bone
        difference() {
            scale([0.5, 0.5, 1]) sphere(d=8, $fn=8);
            translate([0, 0, -1]) cube([6, 6, 2], center=true);
        }

        // Wing membrane with hole pattern
        translate([0, 0, -2]) rotate([0, 90, 0]) {
            for(i = [0:3]) {
                rotate([0, 0, i*25]) 
                    translate([15 + i*5, 0, 0]) 
                    scale([0.8, 0.3, 1]) 
                    cube([8, 0.5, 15 - i*2], center=true);
            }

            // Membrane tear patterns for ethereal look
            for(j = [0:2]) {
                translate([20 + j*5, 0, -5 + j*3])
                    rotate([0, 0, j*20])
                    cylinder(h=1, d=3 - j, $fn=6);
            }
        }
    }
}

// Tail with segmented sections
module blackwight_tail() {
    for(i = [0:4]) {
        translate([0, 0, -20 - i*8]) rotate([0, 0, i*10]) {
            scale([1 - i*0.15, 1 - i*0.15, 1]) 
                sphere(d=12 - i*2, $fn=12);

            // Tail spikes
            translate([0, 6 - i, 2]) rotate([90, 0, 0])
                scale([0.3, 0.3, 1]) cylinder(h=3, d1=2, d2=1, $fn=4);
        }
    }
}

// Limb structure
module blackwight_limb(side) {
    mirror([side, 0, 0]) {
        // Shoulder joint
        translate([12, 0, 5]) rotate([0, 30, 0])
            sphere(d=8, $fn=8);

        // Upper arm
        translate([12, -5, 0]) rotate([0, 45, 0])
            scale([0.6, 0.6, 1.5]) sphere(d=6, $fn=8);

        // Forearm
        translate([12, -10, -5]) rotate([0, 45, 0])
