// Cryptshade - Shadow Dragon of the Void
// A 75mm tall mythical creature with void-black scales and ethereal features

wall_thickness = 1.5;

module body_segment(height, radius, offset) {
    translate([0, 0, offset]) {
        difference() {
            cylinder(h=height, r1=radius, r2=radius*0.9, $fn=16);
            cylinder(h=height+1, r1=radius-wall_thickness, r2=radius*0.9-wall_thickness, $fn=16);
        }
    }
}

module spine_spikes(count, base_z) {
    for(i=[0:count-1]) {
        rotate([0, 0, i*360/count]) {
            translate([0, 12, base_z + i*2]) {
                rotate([90, 0, 0]) {
                    cylinder(h=8, r1=1.5, r2=0.5, $fn=8);
                }
            }
        }
    }
}

module wing_membrane() {
    color([0.1, 0.1, 0.15]) {
        linear_extrude(height=wall_thickness) {
            polygon(points=[
                [0, 0], [25, 15], [35, 30], [30, 45], [15, 50], [0, 45], [-15, 50], [-30, 45], [-35, 30], [-25, 15]
            ]);
        }
    }
}

module wing_structure() {
    color([0.05, 0.05, 0.1]) {
        // Main wing bone
        translate([0, 0, 0]) {
            rotate([0, 90, 0]) {
                cylinder(h=40, r=2, $fn=12);
            }
        }

        // Wing fingers
        for(i=[1:3]) {
            rotate([0, 0, i*25]) {
                translate([0, 0, 0]) {
                    rotate([0, 90-i*5, 0]) {
                        cylinder(h=25+i*5, r=1.2, $fn=8);
                    }
                }
            }
        }

        // Wing membrane
        translate([0, 0, -wall_thickness/2]) {
            wing_membrane();
        }
    }
}

module tail_segments() {
    for(i=[0:7]) {
        scale_factor = 1 - i*0.1;
        translate([0, 0, -i*8]) {
            scale([scale_factor, scale_factor, 1]) {
                body_segment(6, 8-i, 0);
            }
        }
    }
}

module horns() {
    color([0.2, 0.15, 0.25]) {
        // Main horns
        translate([5, 8, 15]) {
            rotate([30, 0, 45]) {
                cylinder(h=12, r1=3, r2=1, $fn=12);
            }
