        translate([-5, 8, 15]) {
            rotate([30, 0, -45]) {
                cylinder(h=12, r1=3, r2=1, $fn=12);
            }
        }

        // Secondary horns
        for(i=[0:2]) {
            translate([3+i*2, 6, 22-i*2]) {
                rotate([20+i*10, 0, 30+i*15]) {
                    cylinder(h=6-i, r1=1.5, r=0.5, $fn=8);
                }
            }
        }
    }
}

module legs() {
    color([0.08, 0.08, 0.12]) {
        // Front legs
        for(side=[-1, 1]) {
            translate([side*6, 5, 5]) {
                rotate([0, 20, 0]) {
                    cylinder(h=15, r=2, $fn=12);
                }
                translate([0, 0, 15]) {
                    rotate([0, 90, 0]) {
                        cylinder(h=8, r=1.5, $fn=8);
                    }
                }
            }
        }

        // Back legs
        for(side=[-1, 1]) {
            translate([side*8, -5, 0]) {
                rotate([0, -20, 0]) {
                    cylinder(h=18, r=2.5, $fn=12);
                }
                translate([0, 0, 18]) {
                    rotate([0, 90, 0]) {
                        cylinder(h=10, r=2, $fn=8);
                    }
                }
            }
        }
    }
}

module eyes() {
