// Graveghast - A spectral undead creature that haunts graveyards
// Medium complexity, realistic style, 3D printable

// Basic dimensions (mm)
$fn = 32;
scale_factor = 0.8;
body_height = 60 * scale_factor;
body_width = 30 * scale_factor;
head_size = 20 * scale_factor;
wing_span = 70 * scale_factor;

module graveghast() {
    color("DarkSlateGray") {
        // Main body - skeletal torso
        union() {
            translate([0, 0, body_height/2]) {
                // Spinal column
                for(i = [0:5]) {
                    translate([0, 0, i*5*scale_factor]) {
                        scale([0.8, 0.6, 1]) 
                            sphere(d=8*scale_factor);
                    }
                }

                // Ribcage
                for(i = [0:6]) {
                    rotate([0, 0, i*30]) {
                        translate([0, 12*scale_factor, 10*scale_factor]) {
                            scale([1, 0.2, 2]) 
                                sphere(d=8*scale_factor);
                        }
                    }
                }
            }

            // Pelvis
            translate([0, 0, 5*scale_factor]) {
                scale([1.5, 0.5, 0.5]) 
                    sphere(d=15*scale_factor);
            }

            // Head - skull-like
            translate([0, 0, body_height + head_size/2]) {
                // Skull base
                scale([0.8, 0.6, 1]) 
                    sphere(d=head_size);

                // Eye sockets
                difference() {
                    scale([0.8, 0.6, 1]) 
                        sphere(d=head_size);
                    translate([-head_size/3, head_size/3, head_size/4]) {
                        rotate([0, 30, 0]) 
                            cylinder(h=head_size/2, d1=head_size/4, d2=0);
                    }
                    translate([head_size/3, head_size/3, head_size/4]) {
                        rotate([0, -30, 0]) 
                            cylinder(h=head_size/2, d1=head_size/4, d2=0);
                    }
                }

                // Lower jaw
                translate([0, -head_size/4, -head_size/3]) {
                    scale([0.6, 0.3, 0.4]) 
                        sphere(d=head_size);
                }
            }

            // Arms - skeletal
            module arm(side) {
                mirror([side, 0, 0]) {
                    translate([body_width/2, 0, body_height*0.7]) {
                        // Upper arm
                        rotate([0, 0, -20]) {
                            cylinder(h=20*scale_factor, d=5*scale_factor);

                            // Elbow joint
                            translate([0, 10*scale_factor, -10*scale_factor]) {
                                sphere(d=6*scale_factor);

                                // Lower arm
                                rotate([0, 0, -30]) {
                                    cylinder(h=15*scale_factor, d=4*scale_factor);

                                    // Hand - claw-like
                                    translate([0, 7.5*scale_factor, -7.5*scale_factor]) {
                                        sphere(d=5*scale_factor);

                                        // Claws
                                        for(i = [0:4]) {
                                            rotate([0, 0, i*72-90]) {
                                                translate([0, 2*scale_factor, 0]) {
                                                    scale([0.3, 0.3, 2]) 
                                                        cylinder(h=5*scale_factor, d1=2*scale_factor, d2=0);
                                                }
                                            }
                                        }
                                    }
                                }
                            }
                        }
                    }
                }
            }

            arm(1);  // Right arm
            arm(-1); // Left arm

            // Legs - skeletal
            module leg(side) {
                mirror([side, 0, 0]) {
                    translate([body_width/3, 0, 0]) {
                        // Upper leg
                        cylinder(h=25*scale_factor, d=6*scale_factor);

                        // Knee joint
                        translate([0, 0, 12.5*scale_factor]) {
                            sphere(d=7*scale_factor);

                            // Lower leg
                            translate([0, 0, 12.5*scale_factor]) {
                                cylinder(h=20*scale_factor, d=5*scale_factor);

                                // Foot
                                translate([0, 0, 10*scale_factor]) {
                                    scale([1, 2, 0.5]) 
                                        sphere(d=8*scale_factor);
                                }
                            }
                        }
                    }
                }
            }

            leg(1);  // Right leg
            leg(-1); // Left leg

            // Tattered ethereal wings
            color("DarkSlateBlue", 0.6) {
                module wing(side) {
                    mirror([side, 0, 0]) {
                        translate([body_width/2, -5*scale_factor, body_height*0.9]) {
                            rotate([0, 30, 0]) {
                                // Main wing membrane
                                linear_extrude(height=1*scale_factor) {
                                    polygon(points=[
                                        [0, 0],
                                        [wing_span/2, 0],
                                        [wing_span/3, -wing_span/2],
                                        [wing_span/4, -wing_span/3],
                                        [wing_span/6, -wing_span/4],
                                        [0, -wing_span/6]
                                    ]);
                                }

                                // Wing bone structure
                                for(i = [0:3]) {
                                    rotate([0, 0, i*30-45]) {
                                        translate([0, -i*5*scale_factor, 0]) {
                                            cylinder(h=wing_span/2 - i*5*scale_factor, d=2*scale_factor);
                                        }
                                    }
                                }
                            }
                        }
                    }
                }

                wing(1);  // Right wing
                wing(-1); // Left wing
            }

            // Grave chains - wrapped around body
            color("DarkGray") {
                translate([0, 8*scale_factor, body_height/2]) {
                    rotate([90, 0, 0]) {
                        for(i = [0:4]) {
                            translate([sin(i*72)*10*scale_factor, cos(i*72)*10*scale_factor, 0]) {
                                cylinder(h=20*scale_factor, d=3*scale_factor);
                            }
                        }
                    }
                }
            }

            // Misty aura at base
            color("GhostWhite", 0.3) {
                translate([0, 0, -2*scale_factor]) {
                    scale([1.2, 1.2, 0.2]) 
                        cylinder(h=5*scale_factor, d=body_width*2);
                }
            }
        }
    }
}

// Render the graveghast
graveghast();

// Nightshade Creature - Realistic Style, Medium Complexity
