        scale([1.2, 0.8, 1.5]) sphere(10);

        // Chest armor plates
        for(i=[0:3]) {
            translate([0, -5+i*3, 8-i*2]) 
            rotate([0, 90+i*10, 0])
            scale([1.5, 1, 0.3])
            sphere(5);
        }
    }
}

// Head module
module nightshade_head() {
    color([0.05, 0.02, 0.1]) {
        // Main head - elongated
        translate([0, 0, 15])
        scale([1.3, 0.7, 1.8])
        sphere(8);

        // Snout
        translate([0, -2, 25])
        scale([0.8, 0.6, 1.2])
        sphere(6);

        // Horns - curved back
        for(side=[-1,1]) {
            translate([side*6, 0, 22])
            rotate([0, side*15, 0])
            scale([0.5, 0.5, 2])
            cylinder(12, 3, 1);

