}

// Eyes with ethereal glow
module nightshade_eyes() {
    for(side=[-1,1]) {
        color([0.4, 0.1, 0.8]) {
            translate([side*4, -5, 22])
            sphere(2);

            // Pupil - darker
            color([0.1, 0, 0.2])
            translate([side*4.5, -6, 22.5])
            sphere(1);
        }
