}

// Wings with membrane structure
module nightshade_wings() {
    wing_thickness = 1.5;

    for(side=[-1,1]) {
        color([0.08, 0.03, 0.12, 0.9]) {
            // Main wing bone
            translate([side*12, 0, 8])
            rotate([0, 0, side*20])
            scale([1, 0.3, 3])
            sphere(5);

            // Wing membrane - simplified with curves
            translate([side*15, 0, 8])
            rotate([0, 0, side*25])
            scale([2, wing_thickness, 3])
            sphere(6);

            // Wing fingers
            for(i=[0:2]) {
                translate([side*(18+i*3), 0, 8-i*2])
                rotate([0, 0, side*(25-i*5)])
                scale([0.5, 0.5, 2-i*0.5])
                cylinder(10-i*2, 1, 0.5);
            }

            // Wing claws
            translate([side*25, 0, 2])
            rotate([0, 90, 0])
            scale([0.3, 0.3, 2])
            cylinder(3, 1.5, 0.5);
        }
    }
}

// Tail with segments
module nightshade_tail() {
    color([0.08, 0.04, 0.12]) {
        for(i=[0:5]) {
            translate([0, 0, -12-i*4])
            scale([1-i*0.15, 1-i*0.15, 1.5])
            sphere(5-i);

            // Tail spikes
            if(i%2==0) {
                for(side=[-1,1]) {
                    translate([side*3, 0, -12-i*4])
                    rotate([0, side*30, 0])
                    scale([0.3, 0.3, 1])
                    cylinder(3, 1, 0);
                }
            }
        }

        // Tail tip
        translate([0, 0, -35])
        scale([0.3, 0.3, 1])
        cylinder(5, 2, 0);
    }
}

// Legs with clawed feet
module nightshade_legs() {
    for(side=[-1,1]) {
        color([0.08, 0.03, 0.1]) {
            // Upper leg
            translate([side*5, 0, -5])
            rotate([0, 0, side*15])
            scale([0.8, 0.8, 2])
            cylinder(8, 3, 2);

            // Lower leg
            translate([side*6, 0, -13])
            rotate([0, 0, side*-20])
            scale([0.6, 0.6, 1.8])
