            // Foot
            translate([side*7, 0, -23])
            rotate([0, 90, 0])
            scale([1.2, 0.8, 0.8])
            sphere(3);

            // Claws
            for(claw=[-1,0,1]) {
                translate([side*7+claw, 0, -25])
                rotate([0, 0, -90])
                scale([0.3, 0.3, 2])
                cylinder(3, 1, 0.5);
            }
        }
    }
}

// Magical essence particles
module nightshade_magic() {
    color([0.5, 0.2, 0.8, 0.6]) {
        for(i=[0:8]) {
            angle = i*45;
            radius = 15 + sin(i*30)*5;
            translate([
                cos(angle)*radius,
                sin(angle)*radius*0.5,
                10 + sin(i*45)*3
            ])
