// Blackwraith - realistic medium-complexity 3D printable model
// Approx 80mm tall, hollowed for printing

// Overall scale factor
scale_factor = 0.8;

// Basic proportions
body_height = 100 * scale_factor;
body_width = 45 * scale_factor;
body_depth = 35 * scale_factor;

// Color definitions
color_dark = [0.05, 0.05, 0.1];
color_eyes = [0.8, 0.1, 0.1];
color_claws = [0.2, 0.2, 0.2];

module blackwraith() {
    // Main body - cloaked figure
    translate([0, 0, body_height/2])
    color(color_dark)
    union() {
        // Torso base - hollow cylinder
        difference() {
            cylinder(h=body_height*0.6, r1=body_width/2, r2=body_width/1.8, $fn=32);
            translate([0, 0, 2])
            cylinder(h=body_height*0.6-4, r1=body_width/2-2, r2=body_width/1.8-2, $fn=32);
        }

        // Hood/head area
        translate([0, 0, body_height*0.6])
        difference() {
            sphere(r=body_width/2.2, $fn=32);
            translate([0, 0, -body_width/4])
            sphere(r=body_width/2.4, $fn=32);
        }

        // Eyes - glowing red slits
        translate([-body_width/4, -body_width/2.3, body_height*0.7])
        color(color_eyes)
        rotate([0, 30, 0])
        scale([1, 0.3, 3])
        sphere(2, $fn=8);

        translate([body_width/4, -body_width/2.3, body_height*0.7])
        color(color_eyes)
        rotate([0, -30, 0])
        scale([1, 0.3, 3])
        sphere(2, $fn=8);

        // Cloak flowing layers
        for(i=[0:3]) {
            rotate([0, 0, i*90+45])
            translate([0, body_width/2 + 5, body_height*0.3])
            scale([1, 0.3, 1])
            difference() {
                cylinder(h=body_height*0.5, r1=body_width/3, r2=body_width/4, $fn=3);
                translate([0, -2, 0])
                cube([body_width, 4, body_height*0.5], center=true);
            }
        }

        // Arms - skeletal
        module arm(side) {
            translate([side * body_width/2.5, 0, body_height*0.45])
            rotate([0, 0, side * 10])
            for(i=[0:2]) {
                translate([0, 0, -i*8*scale_factor])
                rotate([0, side*15, 0])
                scale([0.3, 0.3, 1])
                difference() {
                    cylinder(h=15*scale_factor, r=4*scale_factor, $fn=8);
                    translate([0, 0, 2])
                    cylinder(h=11*scale_factor, r=2.5*scale_factor, $fn=8);
                }
            }

            // Claws
            translate([side * (body_width/2.5 + 8*scale_factor), 0, body_height*0.45 - 24*scale_factor])
            color(color_claws)
            for(j=[-1:1]) {
                translate([0, j*3*scale_factor, 0])
                rotate([90, 0, 0])
                scale([0.5, 0.5, 1])
                cylinder(h=8*scale_factor, r1=2*scale_factor, r2=0.5*scale_factor, $fn=6);
            }
        }

        arm(1);
        arm(-1);

        // Ethereal wings - tattered
        module wing(side) {
            translate([side * body_width/2, 0, body_height*0.8])
            rotate([0, side*20, -20])
            color([0.05, 0.05, 0.1, 0.7])
            for(i=[0:5]) {
                translate([side*i*8*scale_factor, 0, -i*3*scale_factor])
                rotate([0, 0, i*5])
                scale([1, 0.1, 1])
                difference() {
                    cube([12*scale_factor, 1, 25*scale_factor], center=true);
                    translate([0, 0, 10*scale_factor])
                    rotate([0, 30, 0])
                    cube([15*scale_factor, 3, 15*scale_factor], center=true);
                }
            }
        }

        wing(1);
        wing(-1);

        // Lower body - tattered robe
        translate([0, 0, -body_height*0.1])
        difference() {
            cylinder(h=body_height*0.4, r1=body_width/2.2, r2=body_width/2.5, $fn=32);
            // Tattered edges
            for(i=[0:15]) {
                rotate([0, 0, i*24])
                translate([body_width/2.2-2, 0, -2])
                cube([6, body_width/2, body_height*0.4+4], center=true);
            }
        }

        // Shadow tendrils at base
        for(i=[0:7]) {
            rotate([0, 0, i*51])
            translate([body_width/2.5, 0, -body_height*0.3])
            scale([0.3, 0.3, 1])
            rotate([90, 0, 0])
            cylinder(h=15*scale_factor, r1=3*scale_factor, r2=0.5*scale_factor, $fn=6);
        }
    }
}

