module ribcage() {
    color(color_bone)
    union() {
        // Spine
        translate([0, 0, 0])
        cylinder(h=body_h, r=3, $fn=8);

        // Ribs
        for(i = [1:3:body_h-10]) {
            translate([0, 0, i])
            rotate([90, 0, 0])
            scale([1, 0.6, 1])
            cylinder(h=body_w-8, r=2, $fn=6);
        }
    }
