module wing() {
    color(color_dark)
    union() {
        // Wing arm
        translate([0, 0, 0])
        rotate([0, 0, -30])
        cylinder(h=30, r=2, $fn=8);

        // Wing membranes
        for(i = [0:30:90]) {
            translate([15 * sin(i), 0, 15 * cos(i)])
            rotate([0, i, 0])
            scale([0.3, 0.1, 1])
            sphere(8, $fn=4);
        }
    }
}

module tail() {
    color(color_dark)
    union() {
        // Tail segments
        for(i = [0:5:25]) {
            translate([0, -body_d/2 - i, -body_h/2 + i/2])
            rotate([0, 10, 0])
            scale([1 - i/30, 1 - i/30, 1])
            cylinder(h=5, r=3 - i/10, $fn=6);
        }

        // Tail spike
