        rotate([0, 0, type ? 0 : -20])
        cylinder(h=10, r=2, $fn=6);

        // Lower limb
        translate([0, type ? 0 : -3, type ? -10 : -8])
        rotate([0, 0, type ? 0 : 20])
        cylinder(h=8, r=1.5, $fn=6);

        // Claws
        for(j = [-1:1]) {
            translate([j * 1.5, type ? 0 : -3, type ? -18 : -16])
            rotate([0, type ? -20 : 0, 0])
            cylinder(h1=0, h2=3, r1=0, r2=0.8, $fn=4);
        }
    }
}

