    translate([body_w/2 - 5, 0, body_h - 8])
    rotate([0, 0, -45])
    clawed_limb(0);

    translate([-body_w/2 + 5, 0, body_h - 8])
    mirror([1, 0, 0])
    rotate([0, 0, -45])
    clawed_limb(0);

