    // Magical glow effects
    color(color_glow)
    translate([0, 0, body_h + 12])
    sphere(1, $fn=8);

    translate([0, 0, body_h/2])
    sphere(1.5, $fn=8);

    translate([0, -body_d/2 - 20, -body_h/2 + 10])
    sphere(1, $fn=8);
}

