// Fleshwraith - Medium complexity realistic style
// A shadowy undead creature with exposed muscle and bone

// Main parameters for sizing
body_length = 70;
body_width = 35;
body_height = 45;
head_size = 18;
limb_diameter = 8;

// Color definitions
$fn = 32;

module torso() {
    // Main body with exposed ribs
    difference() {
        // Outer torso shell
        scale([1.2, 1, 1]) 
            sphere(d=body_width);

        // Hollow interior for 3D printing
        scale([1.1, 0.9, 0.9]) 
            sphere(d=body_width-4);
    }

    // Exposed ribcage - 5 ribs
    for(i=[-2:2]) {
        translate([i*5, 0, 5]) {
            scale([1.2, 0.3, 0.5])
                sphere(d=body_width-5);
        }
    }

    // Muscle tissue details
    color("firebrick") {
        for(i=[-1:1]) {
            translate([i*8, 5, 0])
                scale([0.8, 0.3, 1.2])
                    sphere(d=body_width/2);
        }
    }
}

module head() {
    // Skull-like head
    translate([0, -body_width/2, body_height/2]) {
        color("antiquewhite") {
            difference() {
                sphere(d=head_size);
                // Eye sockets
                translate([-5, 0, 3])
                    sphere(d=6);
                translate([5, 0, 3])
                    sphere(d=6);
                // Mouth cavity
                translate([0, -7, -3])
                    scale([1, 0.5, 0.3])
                        sphere(d=12);
            }

            // Jaw bone
            translate([0, -head_size/2, -head_size/3])
                rotate([0, 0, 5])
                    cube([head_size-4, 5, 4], center=true);
        }

        // Glowing red eyes
        color("red") {
            translate([-5, 2, 3])
                sphere(d=3);
            translate([5, 2, 3])
                sphere(d=3);
        }
    }
}

module limb(length, angle, side) {
    // Arm with exposed bone
    rotate([0, 0, angle]) {
        translate([side*(body_width/2 + 5), 0, 0]) {
            // Upper arm muscle
            color("firebrick") {
                cylinder(h=length/2, d1=limb_diameter+2, d2=limb_diameter);
            }

            // Exposed bone forearm
            color("antiquewhite") {
                translate([0, 0, length/2])
                    cylinder(h=length/2, d=limb_diameter-2);
            }

            // Hand - clawed
            translate([0, 0, length]) {
                color("antiquewhite") {
                    sphere(d=limb_diameter);
                    // Claws
                    for(i=[-1:1]) {
                        translate([i*3, 0, 3])
                            scale([0.3, 0.3, 1])
                                cylinder(h=6, d=2);
                    }
                }
            }
        }
    }
}

module leg(length, offset_x, offset_z) {
    // Leg with exposed bone structure
    translate([offset_x, 0, offset_z]) {
        // Upper leg
        color("firebrick") {
            cylinder(h=length/2, d1=limb_diameter+3, d2=limb_diameter+1);
        }

        // Shin bone
        color("antiquewhite") {
            translate([0, 0, length/2])
                cylinder(h=length/2, d=limb_diameter);
        }

        // Foot
        translate([0, 0, length]) {
            color("antiquewhite") {
                scale([1.5, 0.5, 0.5])
                    sphere(d=limb_diameter+2);
            }
        }
    }
}

module tail() {
    // Segmented tail with exposed vertebrae
    color("firebrick") {
        for(i=[0:5]) {
            translate([0, body_width/2 + i*8, -i*2]) {
                scale([1 - i*0.1, 1 - i*0.1, 1 - i*0.1])
                    sphere(d=limb_diameter+2);
            }
        }
    }

    // Vertebrae
    color("antiquewhite") {
        for(i=[0:5]) {
            translate([0, body_width/2 + i*8 + 4, -i*2])
                scale([0.5, 0.5, 0.3])
                    sphere(d=limb_diameter);
        }
    }
}

// Main assembly
union() {
    // Central body
    torso();

    // Head attached to torso
    head();

    // Arms - positioned for printing without supports
    limb(25, -30, -1);
    limb(25, 30, 1);

    // Legs - spread for stability
    translate([0, 0, -body_height/2])
        leg(30, -12, 0);
    translate([0, 0, -body_height/2])
        leg(30, 12, 0);

    // Tail for balance
    tail();

    // Tattered cloth remnants
    color("darkslategray") {
        translate([0, 0, -5]) {
            difference() {
                cube([body_width+10, body_width/3, body_height/2], center=true);
                translate([0, 0, -1])
                    cube([body_width+12, body_width/3+2, body_height/2+2], center=true);
            }
        }
    }

    // Shadow aura base
    color("black", 0.3) {
        translate([0, 0, -body_height/2 - 8])
            cylinder(h=2, d=body_width+20);
    }
}

