    // Left wing
    translate([-8, 0, 20])
        rotate([0, 30, -20])
        wing(true);

    // Right wing
    translate([8, 0, 20])
        rotate([0, -30, 20])
        mirror([1, 0, 0])
        wing(false);
}

module wing(left=true) {
