    linear_extrude(height=0.5)
    polygon(points=[
        [0, 0],
        [-15, -5],
        [-25, 0],
        [-30, 10],
        [-25, 20],
        [-15, 25],
        [-5, 20],
        [0, 10]
    ]);

    // Wing veins - decay patterns
    for(i = [1:3]) {
        translate([-i*8, 0, 0.3])
            scale([0.5, 0.5, 1])
            cylinder(h=1, r1=1, r2=0.5, $fn=8);
