// Goreling - a small demonic familiar with curved horns, leathery wings, and glowing eyes
// Designed for 3D printing - 60mm tall, no overhangs >45 degrees

$fn = 32;

// Color definitions
color_body = "#2A1810";
color_wings = "#1A0F0A";
color_eyes = "#FF4400";
color_horns = "#E8DCC0";

// Main module
module goreling() {
    union() {
        // Body
        translate([0, 0, 15])
            color(color_body)
            scale([1.2, 1, 1.5])
            sphere(8);

        // Head
        translate([0, -6, 32])
            color(color_body)
            scale([1, 1.2, 1.1])
            sphere(7);

        // Snout
        translate([0, -12, 30])
            color(color_body)
            rotate([0, 10, 0])
            scale([0.7, 1.5, 0.6])
            sphere(4);

        // Horns - left and right
        horn(-5, -10, 35, 0);
        horn(5, -10, 35, 0);

        // Wings
        wing(-12, 0, 28, 0);
        wing(12, 0, 28, 180);

        // Arms
        arm(-6, -3, 20, -20);
        arm(6, -3, 20, 20);

        // Legs
        leg(-3, 0, 5, -10);
        leg(3, 0, 5, 10);

        // Tail
        tail();

        // Eyes
        eye(-3, -9, 33, 0);
        eye(3, -9, 33, 0);
    }
}

// Horn module with spiral texture
module horn(x, y, z, rot) {
    translate([x, y, z])
    rotate([0, 0, rot])
    union() {
        color(color_horns)
        rotate_extrude(angle = 360, $fn = 12)
            translate([2, 0, 0])
            polygon(points = [[0, 0], [1, 5], [0, 10], [-1, 5]]);
    }
}

// Wing module with membrane structure
module wing(x, y, z, rot) {
    translate([x, y, z])
    rotate([0, 0, rot])
    rotate([0, -30, 0])
    color(color_wings)
    difference() {
        // Main wing shape
        scale([1, 2, 0.5])
        sphere(8);

        // Cutout for membrane effect
        for (i = [0:2]) {
            translate([0, -i*4, 0])
            scale([0.7, 0.7, 1])
            sphere(7);
        }
    }
}

// Arm module
module arm(x, y, z, rot) {
    translate([x, y, z])
    rotate([0, 0, rot])
    union() {
        // Upper arm
        color(color_body)
        rotate([90, 0, 0])
        cylinder(h = 8, r1 = 2, r2 = 1.5);

        // Lower arm
        translate([0, 0, -8])
        rotate([90, 0, 0])
        cylinder(h = 6, r1 = 1.5, r2 = 1);

        // Hand with claws
        translate([0, -6, -2])
        union() {
            sphere(1.5);
            for (i = [-1, 0, 1]) {
                translate([i, -1, 0])
                rotate([0, 20, 0])
                scale([0.3, 0.3, 1])
                cylinder(h = 2, r = 1);
            }
        }
    }
}

// Leg module
module leg(x, y, z, rot) {
    translate([x, y, z])
    rotate([0, rot, 0])
    union() {
        // Thigh
        color(color_body)
        rotate([90, 0, 0])
        cylinder(h = 10, r1 = 2.5, r2 = 2);

        // Lower leg
        translate([0, 0, -10])
        rotate([90, 0, 0])
        cylinder(h = 8, r1 = 2, r2 = 1.5);

        // Foot with hoof
        translate([0, -8, -1])
        union() {
            sphere(2);
            translate([0, -2, -1])
            scale([1.2, 1.5, 0.5])
            sphere(2);
        }
    }
}

// Tail module with spade tip
module tail() {
    color(color_body)
    translate([0, 2, 10])
    rotate([0, 20, 0])
    union() {
        // Tail segments
        for (i = [0:3]) {
            translate([0, 0, -i*3])
            scale([1 - i*0.2, 1 - i*0.2, 1])
            sphere(2);
        }

        // Spade tip
        translate([0, 0, -12])
        rotate([90, 0, 0])
        linear_extrude(height = 0.1)
            polygon(points = [[0, 0], [3, -3], [0, -6], [-3, -3]]);
    }
}

// Eye module with glowing effect
module eye(x, y, z) {
    translate([x, y, z])
    union() {
        // Eye socket
        difference() {
            color(color_body)
            sphere(1.5);

            // Pupil
            color(color_eyes)
            translate([0, -0.5, 0])
            sphere(1);
        }
    }
}

// Generate the model
goreling();

// Boneling mythical creature - realistic style, medium complexity
// A skeletal dragon-like being with bone wings and magical crystal core

// Main parameters
$fn = 32;
scale_factor = 0.5;
wall_thickness = 1.2;

// Colors
color_bone = [0.95, 0.9, 0.85];
color_crystal = [0.3, 0.7, 1.0];
color_eyes = [0.8, 0.1, 0.1];

module skull() {
    difference() {
        // Main skull shape
        translate([0, 0, 8]) scale([1.2, 0.8, 1]) sphere(10);

        // Eye sockets
        translate([-5, 4, 8]) sphere(3);
        translate([5, 4, 8]) sphere(3);

        // Nostril holes
        translate([-2, -6, 6]) rotate([45, 0, 0]) cylinder(3, 1.5, 1.5);
        translate([2, -6, 6]) rotate([45, 0, 0]) cylinder(3, 1.5, 1.5);

        // Jaw removal for open mouth
        translate([0, -8, 0]) cube([20, 10, 8], center=true);
    }

    // Lower jaw
    translate([0, -8, -2]) rotate([10, 0, 0]) scale([1, 0.6, 0.4]) sphere(8);

    // Teeth
    for(i = [-8:2:8]) {
        translate([i, -11, 2]) rotate([90, 0, 0]) cylinder(5, 0.5, 0.5);
    }
}

module spine() {
    // Vertebrae
    for(i = [0:4:20]) {
        translate([0, 0, -i]) 
        rotate([0, 10*i, 0]) 
        scale([1.5 - i*0.03, 1, 0.8 - i*0.02]) 
        sphere(3);
    }

    // Rib cage
    for(i = [4:4:16]) {
        translate([0, 0, -i]) 
        rotate([0, 0, 0]) 
        difference() {
            union() {
                // Left ribs
                translate([-5, 0, 0]) rotate([0, 0, 30]) scale([8, 0.5, 0.5]) sphere(2);
                translate([-8, 0, 0]) rotate([0, 0, 45]) scale([6, 0.5, 0.5]) sphere(1.5);

                // Right ribs
                translate([5, 0, 0]) rotate([0, 0, -30]) scale([8, 0.5, 0.5]) sphere(2);
                translate([8, 0, 0]) rotate([0, 0, -45]) scale([6, 0.5, 0.5]) sphere(1.5);
            }
        }
    }
}

module wing() {
    // Wing bones structure
    translate([12, 0, 0]) {
        // Main wing bone
        rotate([0, 0, -30]) cylinder(30, 1.5, 1);

        // Wing finger bones
        for(i = [1:3]) {
            rotate([0, 0, i*20 - 30]) 
            translate([25, 0, 0]) 
            rotate([0, 0, -30]) 
            cylinder(20, 1, 0.5);
        }

        // Membrane support bones
        for(i = [0:30:60]) {
            rotate([0, 0, -i]) 
            translate([12, 0, 0]) 
            rotate([0, 0, -15]) 
            cylinder(8, 0.5, 0.3);
        }
    }
}

module tail() {
    // Tail vertebrae chain
    for(i = [0:3:30]) {
        translate([0, 0, -i - 20]) 
        rotate([0, 5*i, 0]) 
        scale([1 - i*0.02, 0.8 - i*0.015, 0.8 - i*0.02]) 
        sphere(2.5);
    }
}

module crystal_core() {
    // Magical essence crystal
    translate([0, 0, -10]) {
        color(color_crystal) {
            difference() {
                sphere(4);
                // Crystal facets
                for(i = [0:60:300]) {
                    rotate([0, 0, i]) 
                    translate([2, 0, 0]) 
                    cube([4, 0.1, 4], center=true);
                }
            }
        }
    }
}

module leg() {
    // Upper leg bone
    translate([5, 0, -15]) rotate([30, 0, 15]) cylinder(12, 2, 1.5);

    // Lower leg bone
    translate([5, 0, -27]) rotate([60, 0, 15]) cylinder(10, 1.5, 1);

    // Foot bones
    translate([5, 0, -37]) rotate([90, 0, 15]) cylinder(6, 1, 0.8);
    for(i = [-2:2]) {
        translate([5 + i*1.5, 0, -43]) rotate([90, 0, 0]) cylinder(3, 0.5, 0.5);
    }
}

// Main assembly
scale([scale_factor, scale_factor, scale_factor]) {
    color(color_bone) {
        union() {
            // Head and spine
            skull();
            spine();

            // Wings
            wing();
            mirror([1, 0, 0]) wing();

            // Tail
            tail();

            // Legs
            translate([3, 0, 0]) leg();
            translate([-3, 0, 0]) mirror([1, 0, 0]) leg();
            translate([0, 8, 0]) leg();
            translate([0, 8, 0]) mirror([1, 0, 0]) leg();
        }
    }

    // Eyes
    color(color_eyes) {
        translate([-5, 4, 8]) sphere(1);
        translate([5, 4, 8]) sphere(1);
    }

    // Magical core
    crystal_core();
}

// Netherling - Mythical Nether Realm Creature
// Features: insectoid limbs, glowing core, obsidian plating, ethereal wing membranes
// Size: ~80mm tall when printed

$fn = 32;  // Smooth curves for realistic print

// Color definitions
color_plating = [0.1, 0.1, 0.1];  // Dark obsidian
color_core = [0.8, 0.2, 0.9];     // Purple magical glow
color_membrane = [0.3, 0.1, 0.5]; // Translucent wing
color_bone = [0.4, 0.4, 0.4];     // Exoskeleton

// Main body module
module netherling() {
    union() {
        // Central glowing core
        translate([0, 0, 15])
            color(color_core)
            sphere(d=12);

        // Obsidian thorax plating
        translate([0, 0, 25])
            color(color_plating)
            scale([1.5, 1, 1.2])
            sphere(d=20);

        // Abdomen segments
        for(i = [1:3]) {
            translate([0, 0, 8 - i*6])
                color(color_plating)
                scale([1.2 - i*0.2, 1.2 - i*0.2, 0.8])
                sphere(d=15 - i*2);
        }

        // Head with mandibles
        translate([0, 0, 35])
            union() {
                color(color_plating)
                scale([0.8, 0.6, 1])
                sphere(d=12);

                // Mandibles
                for(side = [-1, 1]) {
                    translate([side*4, 0, 38])
                        rotate([0, 30, side*20])
                        color(color_bone)
                        cylinder(h=8, d1=3, d2=1);
                }
            }

        // Horns
        for(side = [-1, 1]) {
            translate([side*5, 0, 42])
                rotate([0, -20, side*15])
                color(color_bone)
                cylinder(h=10, d1=4, d2=1);
        }

        // Legs (6 total)
        for(i = [0:5]) {
            angle = i*60;
            translate([cos(angle)*12, sin(angle)*12, 18])
                rotate([0, 0, angle])
                leg();
        }

        // Wings
        for(side = [-1, 1]) {
            translate([side*15, 0, 30])
                rotate([0, 0, side*30])
                wing(side);
        }
    }
}

// Leg module - insectoid with 3 segments
module leg() {
    color(color_bone)
    union() {
        // Upper segment
        rotate([0, 30, 0])
            cylinder(h=8, d=3);

        // Middle segment
        translate([0, 0, -8])
            rotate([0, 60, 0])
            cylinder(h=10, d=2.5);

        // Lower segment with claw
        translate([0, 0, -17])
            rotate([0, 90, 0])
            cylinder(h=12, d=2);

        // Claw tip
        translate([0, 0, -28])
            rotate([0, 120, 0])
            cylinder(h=4, d1=2, d2=0.5);
    }
}

// Wing module - ethereal membrane
module wing(side) {
    color(color_membrane)
    linear_extrude(height=0.5)
    polygon(points=[
        [0, 0],
        [8*side, 15],
        [12*side, 25],
        [10*side, 35],
        [6*side, 40],
        [2*side, 38],
        [0, 30],
        [-2*side, 20],
        [-1*side, 10]
    ]);

    // Wing veins
    color(color_bone)
    for(i = [0:3]) {
        rotate([0, 0, i*20*side])
            translate([0, 0, 0.3])
            cylinder(h=0.5, d=0.8);
    }
}

// Create the netherling
scale(0.8)
netherling();

// Bloodling - A vampiric imp creature from the lower planes
// Wings, horned head, clawed limbs, and dripping blood sacs

$fn = 50; // Smooth curves for realistic look

// Overall scale
scale_factor = 0.4;

module bloodling() {
    scale([scale_factor, scale_factor, scale_factor]) {

        // Main body - muscular torso
        color("darkred")
        translate([0, 0, 10])
        scale([1.2, 0.8, 1.5])
        sphere(10);

        // Lower abdomen
        color("darkred")
        translate([0, 0, 0])
        scale([1, 0.9, 1.2])
        sphere(8);

        // Head module
        color("crimson")
        translate([0, 0, 25])
        head();

        // Wings - leathery bat wings
        color("maroon")
        translate([-12, 0, 18])
        rotate([0, -10, 30])
        wing();

        color("maroon")
        translate([12, 0, 18])
        mirror([1, 0, 0])
        rotate([0, -10, 30])
        wing();

        // Arms with claws
        color("darkred")
        translate([-8, 0, 15])
        rotate([0, 0, 30])
        arm();

        color("darkred")
        translate([8, 0, 15])
        mirror([1, 0, 0])
        rotate([0, 0, 30])
        arm();

        // Legs with talons
        color("darkred")
        translate([-4, 0, 0])
        rotate([0, 0, -15])
        leg();

        color("darkred")
        translate([4, 0, 0])
        mirror([1, 0, 0])
        rotate([0, 0, -15])
        leg();

        // Blood sacs - dripping magical blood
        color("red")
        translate([0, 8, 8])
        blood_sac();

        color("red")
        translate([0, -8, 8])
        blood_sac();

        // Tail - segmented with barb
        color("darkred")
        translate([0, 0, -8])
        tail();
    }
}

module head() {
    union() {
        // Skull structure
        scale([0.8, 0.8, 1])
        sphere(8);

        // Snout
        translate([0, 0, 6])
        scale([0.6, 0.5, 0.8])
        sphere(5);

        // Horns - curved back
        translate([-3, 0, 8])
        rotate([0, -20, -20])
        cylinder(8, 2, 0.5);

        translate([3, 0, 8])
        mirror([1, 0, 0])
        rotate([0, -20, 20])
        cylinder(8, 2, 0.5);

        // Eyes - glowing pits
        color("black")
        translate([-2.5, 3, 5])
        sphere(1.5);

        color("black")
        translate([2.5, 3, 5])
        sphere(1.5);

        // Fangs
        color("white")
        translate([-1.5, 4, 2])
        rotate([0, 20, 0])
        cylinder(3, 0.5, 0);

        color("white")
        translate([1.5, 4, 2])
        rotate([0, 20, 0])
        cylinder(3, 0.5, 0);
    }
}

module wing() {
    // Main wing membrane
    difference() {
        union() {
            // Upper arm bone
            translate([0, 0, 0])
            rotate([0, 0, 20])
            cylinder(12, 1.5, 1);

            // Wing membrane
            translate([0, 0, -2])
            scale([1, 8, 0.5])
            sphere(8);
        }

        // Cut holes for torn wing effect
        for(i = [1:3]) {
            translate([i*3 - 5, i*4 - 2, 0])
            sphere(2);
        }
    }
}

module arm() {
    union() {
        // Upper arm
        cylinder(8, 2, 1.5);

        // Forearm
        translate([0, 0, -8])
        rotate([0, 20, 0])
        cylinder(7, 1.5, 1);

        // Clawed hand
        translate([0, 0, -15])
        rotate([0, 30, 0])
        claw_hand();
    }
}

module leg() {
    union() {
        // Thigh
        cylinder(9, 2.5, 2);

        // Lower leg
        translate([0, 0, -9])
        rotate([0, 30, 0])
        cylinder(8, 2, 1.5);

        // Taloned foot
        translate([0, 0, -17])
        rotate([0, 45, 0])
        talon_foot();
    }
}

module claw_hand() {
    union() {
        // Palm
        sphere(2);

        // Three claws
        for(i = [-1:1]) {
            translate([i*1.5, 0, -2])
            rotate([0, -20, 0])
            cylinder(3, 0.8, 0.2);
        }
    }
}

module talon_foot() {
    union() {
        // Foot base
        scale([2, 1, 3])
        sphere(1.5);

        // Talons
        for(i = [-1.5, 0, 1.5]) {
            translate([i, 0, -3])
            cylinder(2.5, 0.7, 0.3);
        }

        // Back talon
        translate([0, -1, -3])
        cylinder(2, 0.7, 0.3);
    }
}

module tail() {
    union() {
        // Segments for flexibility
        for(i = [0:3]) {
            translate([0, 0, -i*4])
            scale([1 - i*0.15, 1 - i*0.15, 1])
            sphere(3);
        }

        // Barbed tail tip
        translate([0, 0, -14])
        difference() {
            cone(5, 2, 0);

            // Barbs
            for(i = [0:3]) {
                rotate([0, 0, i*90])
                translate([1.5, 0, 0])
                cube([2, 0.5, 1], center=true);
            }
        }
    }
}

module blood_sac() {
    difference() {
        // Main sac
        scale([1.5, 1, 1])
        sphere(3);

        // Dripping blood drops
        for(i = [0:2]) {
            color("darkred")
            translate([i - 1, 3, -i*0.5])
            sphere(0.8);
        }
    }
}

