// Skullwretch - Mythical Undead Raven Creature
// Realistic style, medium complexity

// Color definitions
$fn = 64;  // Smooth curves

module skullwretch() {
    // Main body - skeletal raven torso
    color("silver") {
        // Ribcage
        union() {
            // Spine
            for(i = [0:1:6]) {
                translate([0, 0, i*3]) {
                    rotate([0, 90, 0]) {
                        cylinder(h = 2, r1 = 3-i*0.3, r2 = 2.5-i*0.2);
                    }
                }
            }

            // Ribs
            for(i = [1:1:5]) {
                for(j = [0:30:330]) {
                    rotate([j, 0, 0]) {
                        translate([0, 0, i*3]) {
                            cylinder(h = 6-i*0.5, r = 0.5);
                        }
                    }
                }
            }
        }
    }

    // Skull head (raven-like)
    color("lightgray") {
        union() {
            // Main skull
            scale([1.2, 1, 1]) {
                sphere(r = 8);
            }

            // Beak
            translate([9, 0, 0]) {
                rotate([0, 90, 0]) {
                    cylinder(h = 12, r1 = 3, r2 = 0.5);
                }
            }

            // Eye sockets
            difference() {
                sphere(r = 8);
                translate([5, 4, 3]) {
                    sphere(r = 2.5);
                }
                translate([5, -4, 3]) {
                    sphere(r = 2.5);
                }
            }

            // Cracks in skull
            for(i = [1:1:3]) {
                rotate([0, 0, i*45]) {
                    translate([0, 0, 4]) {
                        scale([0.2, 1, 4]) {
                            cube([10, 0.5, 1]);
                        }
                    }
                }
            }
        }
    }

    // Wings - skeletal
    color("darkgray") {
        // Left wing
        translate([-5, 10, 5]) {
            rotate([0, 30, 0]) {
                wing(true);
            }
        }

        // Right wing
        translate([-5, -10, 5]) {
            rotate([0, 30, 0]) {
                mirror([0, 1, 0]) {
                    wing(false);
                }
            }
        }
    }

    // Tail - bone segments
    color("gray") {
        translate([-15, 0, -5]) {
            for(i = [0:1:4]) {
                rotate([0, 0, i*10-20]) {
                    translate([i*-2, 0, -i*1.5]) {
                        scale([1.5, 0.3, 0.5]) {
                            cube([3, 2, 1]);
                        }
                    }
                }
            }
        }
    }

    // Magical corruption - glowing cracks
    color("purple") {
        // Head corruption
        translate([5, 0, 0]) {
            sphere(r = 1.5);
        }

        // Wing tips
        translate([-8, 15, 5]) {
            sphere(r = 1);
        }
        translate([-8, -15, 5]) {
            sphere(r = 1);
        }
    }
}

module wing(is_left) {
    // Main wing bone
    rotate([0, 0, -30]) {
        cylinder(h = 20, r = 0.8);
    }

    // Wing phalanges
    for(i = [0:1:3]) {
        rotate([0, 0, i*-15]) {
            translate([i*5, 0, 0]) {
                difference() {
                    cylinder(h = 8-i*1.5, r = 0.5);
                    // Bone texture
                    for(j = [1:1:3]) {
                        translate([0, 0, j*2]) {
                            rotate([0, 90, 0]) {
                                cylinder(h = 0.5, r = 0.6);
                            }
                        }
                    }
                }
            }
        }
    }

    // Secondary feathers
    for(i = [1:1:4]) {
        rotate([0, 0, -i*10-20]) {
            translate([i*4, 0, -2]) {
                cylinder(h = 6-i, r = 0.3);
            }
        }
    }
}

// Assembly
translate([0, 0, 0]) {
    scale(0.7) {
        skullwretch();
    }
}

// Base for 3D printing
color("darkgray") {
    translate([0, 0, -8]) {
        difference() {
            cylinder(h = 2, r = 20);
            cylinder(h = 3, r = 18);
        }
    }
}

// Aetherion - Mythical Sky Guardian
// Wingspan: 80mm, Height: 60mm, Length: 70mm

$fn = 32;

// Main colors
color_aether = [0.6, 0.8, 1.0, 0.9]; // Light blue glow
color_body = [0.3, 0.4, 0.6, 1.0]; // Deep blue scales
color_horns = [0.8, 0.8, 0.9, 1.0]; // Ivory horns
color_crystal = [0.4, 0.9, 1.0, 0.7]; // Energy crystal

module wing() {
    difference() {
        // Main wing membrane
        scale([1, 0.1, 0.6])
        rotate([0, -20, 0])
        sphere(15);

        // Wing holes for ethereal look
        for(i = [1:3]) {
            translate([i*3 - 6, 0, 5])
            scale([1.5, 0.5, 1.5])
            sphere(3);
        }
    }
}

module horn() {
    // Curved horn structure
    rotate([0, -15, 0])
    cylinder(h1 = 8, h2 = 0, r1 = 2, r2 = 0.5);
}

module tail_segment(pos) {
    translate([pos, 0, 0])
    scale([1 - pos*0.01, 1 - pos*0.01, 1 - pos*0.01])
    sphere(3);
}

module aetherion_body() {
    union() {
        // Main torso
