                // Eyes - glowing orbs
                color("white") {
                    translate([head_size/3, head_size/3, head_size/4])
                        sphere(d=3);
                    translate([head_size/3, -head_size/3, head_size/4])
                        sphere(d=3);
                }
            }

            // Neck and body
            hull() {
                translate([body_length/2 - 10, 0, body_diameter/2])
                    sphere(d=body_diameter * 1.2);
                translate([-body_length/2, 0, body_diameter/2])
                    sphere(d=body_diameter * 0.8);
            }

            // Body segments
            for(i = [0:5]) {
                translate([body_length/2 - 15 - i*8, 0, body_diameter/2]) {
