                            rotate([0, 30, 0])
                                cylinder(h=wing_span/3, d=3);

                            // Wing membrane
                            translate([wing_span/3, 0, 0]) {
                                rotate([0, 30, 0])
                                    linear_extrude(height=0.5)
                                        polygon(points=[
                                            [0, 0],
                                            [wing_span/2, wing_span/4],
                                            [wing_span/3, wing_span/2],
                                            [wing_span/4, wing_span/3],
                                            [0, wing_span/4]
                                        ]);
                            }
                        }
                    }
                }
            }

            // Legs
            for(side = [-1, 1]) {
                mirror([0, side, 0]) {
                    // Front legs
                    translate([body_length/3, 0, 0]) {
                        rotate([0, 0, -30])
                            cylinder(h=leg_height, d=4);
