// Thalorion - Mythical winged serpent-dragon of the skies
// Realistic style, medium complexity

// Main colors
color_body = "forestgreen";
color_wings = "darkslateblue";
color_scales = "darkolivegreen";
color_eyes = "crimson";
color_horns = "goldenrod";

// Overall dimensions
scale_factor = 1;
body_length = 60 * scale_factor;
body_diameter = 15 * scale_factor;

module thalorion_body() {
    // Serpentine body segments
    color(color_body)
    union() {
        // Head
        translate([body_length/2, 0, 0])
            rotate([0, 90, 0])
            cylinder(h=15, d1=12, d2=8, $fn=16);

        // Neck
        translate([body_length/2 - 10, 0, 0])
            rotate([0, 90, 0])
            cylinder(h=15, d1=8, d2=10, $fn=16);

        // Main body
        for(i=[0:4]) {
            translate([body_length/2 - 25 - i*8, 0, 0])
                rotate([0, 90, 0])
                cylinder(h=10, d=body_diameter - i*2, $fn=16);
        }
    }
}

module thalorion_wings() {
    color(color_wings)
    union() {
        // Left wing
        translate([5, -10, 8])
            rotate([0, 0, -20])
            difference() {
                // Wing membrane
                scale([1, 1.5, 0.5])
                sphere(d=25, $fn=8);

                // Wing holes
                for(i=[1:3]) {
                    translate([i*3 - 6, 0, 0])
                        scale([0.3, 0.3, 2])
                        sphere(d=5, $fn=8);
                }
            }

        // Right wing
        translate([5, 10, 8])
            rotate([0, 0, 20])
            mirror([0, 1, 0])
            difference() {
                scale([1, 1.5, 0.5])
                sphere(d=25, $fn=8);

                for(i=[1:3]) {
                    translate([i*3 - 6, 0, 0])
                        scale([0.3, 0.3, 2])
                        sphere(d=5, $fn=8);
                }
            }
    }
}

module thalorion_scales() {
    color(color_scales)
    for(i=[0:8]) {
        angle = i * 45;
        radius = body_diameter/2 - 2;

        // Scale rows along body
        for(j=[0:4]) {
            pos = body_length/2 - 15 - j*10;
            translate([pos, 
                      cos(angle) * radius, 
                      sin(angle) * radius])
                rotate([0, 90, angle])
                scale([1, 0.8, 0.3])
                sphere(d=3, $fn=8);
        }
    }
}

module thalorion_horns() {
    color(color_horns)
    union() {
        // Left horn
        translate([body_length/2 + 5, -5, 4])
            rotate([0, 30, -30])
            cylinder(h=10, d1=2, d2=1, $fn=8);

        // Right horn
        translate([body_length/2 + 5, 5, 4])
            rotate([0, 30, 30])
            cylinder(h=10, d1=2, d2=1, $fn=8);

        // Central horn
        translate([body_length/2 + 7, 0, 6])
            rotate([0, 0, 0])
            cylinder(h=12, d1=3, d2=1, $fn=8);
    }
}

module thalorion_eyes() {
    color(color_eyes)
    union() {
        // Left eye
        translate([body_length/2 + 3, -3, 4])
            sphere(d=2, $fn=16);

        // Right eye
        translate([body_length/2 + 3, 3, 4])
            sphere(d=2, $fn=16);
    }
}

module thalorion_tail() {
    color(color_body)
    union() {
        // Tail segments
        for(i=[0:5]) {
            tail_pos = -body_length/2 - 5 - i*5;
            tail_diameter = max(body_diameter - i*2, 2);

            translate([tail_pos, 0, 0])
                rotate([0, 90, 0])
                cylinder(h=5, d=tail_diameter, $fn=16);
        }
    }
}

// Assemble the complete Thalorion
union() {
    thalorion_body();
    thalorion_wings();
    thalorion_scales();
    thalorion_horns();
    thalorion_eyes();
    thalorion_tail();
}

