// Main body module
module umbrion_body() {
    // Central body
    color(color_body)
    union() {
        // Main torso
        translate([0, 0, 10])
            scale([1.2, 0.8, 1.5])
            sphere(r=8, $fn=32);

        // Neck
        translate([0, 0, 25])
            rotate([10, 0, 0])
            scale([0.6, 0.5, 1.8])
            cylinder(r1=6, r2=4, h=15, $fn=16);
    }
}

// Head module
module umbrion_head() {
    color(color_body)
    union() {
        // Main head shape
        translate([0, 0, 40])
            rotate([0, 10, 0])
            scale([0.8, 0.7, 1])
            sphere(r=7, $fn=24);

        // Snout
        translate([0, 0, 47])
            rotate([0, 20, 0])
            scale([0.4, 0.3, 1.2])
            cylinder(r1=3, r2=2, h=8, $fn=12);

        // Eyes (glowing orbs)
        color(color_eyes)
        translate([3, 4, 42])
            sphere(r=1.5, $fn=12);
        translate([-3, 4, 42])
            sphere(r=1.5, $fn=12);
    }
}

// Horn module
module horn(height, angle) {
    color(color_accents)
    rotate([0, angle, 0])
    cylinder(r1=1.5, r2=0.5, h=height, $fn=8);
}

// Horns array
module umbrion_horns() {
    // Back horns
    for(i = [0:30:150]) {
        rotate([0, 0, i-75])
        translate([0, 0, 35])
        horn(10, 15);
    }

    // Side horns
    translate([6, 0, 38])
        rotate([0, 0, 45])
        horn(8, 20);
    translate([-6, 0, 38])
        rotate([0, 0, -45])
        horn(8, 20);
}

// Wing module
module wing() {
    color(color_wing_membrane)
    union() {
        // Wing bone structure
        color(color_body)
        translate([0, 0, 0])
            rotate([0, 0, 0])
            cylinder(r=1, h=20, $fn=8);

        // Wing membrane
        color(color_wing_membrane)
        hull() {
            translate([0, 0, 0])
                sphere(r=1, $fn=12);
            translate([15, 0, 5])
                sphere(r=0.5, $fn=12);
            translate([20, 0, 15])
                sphere(r=0.5, $fn=12);
            translate([15, 0, 20])
                sphere(r=0.5, $fn=12);
            translate([0, 0, 18])
                sphere(r=1, $fn=12);
        }
    }
}

// Wings assembly
module umbrion_wings() {
    // Left wing
    translate([0, -8, 25])
        rotate([0, 10, 45])
        wing();

    // Right wing
    translate([0, 8, 25])
        rotate([0, 10, -45])
        mirror([0,1,0])
        wing();
}

// Tail module
module umbrion_tail() {
    color(color_body)
    union() {
        for(i = [0:1:10]) {
            translate([0, 0, 10 - i*2])
                scale([1 - i*0.08, 1 - i*0.08, 1])
                sphere(r=6 - i*0.5, $fn=20);
        }

        // Tail spike
        color(color_accents)
        translate([0, 0, -10])
            rotate([0, 180, 0])
            cylinder(r1=2, r2=0, h=5, $fn=6);
    }
}

// Leg module
module leg() {
    color(color_body)
    union() {
        // Upper leg
        translate([0, 0, 0])
            rotate([0, 0, 0])
            cylinder(r=2, h=8, $fn=8);

        // Lower leg
        translate([0, 0, -8])
            rotate([0, 20, 0])
            cylinder(r=1.5, h=10, $fn=8);

        // Foot
        translate([0, 0, -19])
            rotate([0, 90, 0])
            scale([2, 1, 1])
            cylinder(r=1.5, h=3, $fn=6);
    }
}

// Legs assembly
module umbrion_legs() {
    // Front left
    translate([5, -4, 15])
        rotate([0, 0, -30])
        leg();

    // Front right
    translate([5, 4, 15])
        rotate([0, 0, 30])
        leg();

    // Back left
    translate([-5, -4, 8])
        rotate([0, 0, -30])
        leg();

    // Back right
    translate([-5, 4, 8])
        rotate([0, 0, 30])
        leg();
}

// Main assembly
umbrion_body();
umbrion_head();
umbrion_horns();
umbrion_wings();
umbrion_tail();
umbrion_legs();

// Velithra - Serpentine Sky Guardian
// A mystical winged serpent with crystalline horns and ethereal powers

// Main body dimensions
body_length = 60;
body_diameter = 12;

// Wings
wing_span = 45;
wing_thickness = 2;

// Colors
body_color = [0.3, 0.7, 0.9, 0.8]; // Ethereal blue
horn_color = [0.8, 0.9, 1.0, 0.9]; // Crystalline white
eye_color = [1, 0.3, 0.3, 1]; // Fiery red

module velithra() {
    union() {
        // Main serpentine body
        color(body_color)
        for(i = [0:5]) {
            translate([i * 10, 0, sin(i * 30) * 3])
            rotate([0, 0, i * 15])
            scale([1 - i * 0.1, 1 - i * 0.1, 1 - i * 0.05])
            sphere(d = body_diameter - i * 1.5, $fn = 20);
        }

        // Head
        translate([65, 0, 5])
        union() {
            // Snout
            color(body_color)
            scale([1.2, 0.8, 0.8])
            sphere(d = 10, $fn = 20);

            // Eyes
            translate([3, 2, 2])
            color(eye_color)
            sphere(d = 2, $fn = 10);

            translate([3, -2, 2])
            color(eye_color)
            sphere(d = 2, $fn = 10);

            // Crystalline horns
            for(i = [0:3]) {
                rotate([0, 0, i * 90])
                translate([0, 3, 5])
                rotate([30, 0, 0])
                color(horn_color)
                cylinder(h = 8, d1 = 3, d2 = 1, $fn = 6);
            }
        }

        // Wings
        module wing() {
            difference() {
                // Main wing membrane
                translate([0, 0, 0])
                rotate([0, 0, 0])
                scale([1.5, 1, 0.1])
                sphere(d = wing_span, $fn = 20);

                // Wing holes for ethereal effect
                for(i = [0:3]) {
                    translate([10 + i * 5, -5 + i * 3, 0])
                    scale([1, 0.5, 1])
                    sphere(d = 4, $fn = 10);
                }
            }
        }

        // Left wing
        translate([30, -6, 8])
        rotate([0, 0, -20])
        color(body_color)
        wing();

        // Right wing
        translate([30, 6, 8])
        rotate([0, 0, 20])
        mirror([0, 1, 0])
        color(body_color)
        wing();

        // Tail segments
        for(i = [0:4]) {
            translate([-5 - i * 5, 0, -sin(i * 45) * 2])
            rotate([0, 0, -i * 20])
            scale([1 - i * 0.15, 1 - i * 0.15, 1 - i * 0.05])
            color(body_color)
            sphere(d = body_diameter - i * 2, $fn = 15);
        }

        // Ethereal fins along spine
        for(i = [0:6]) {
            translate([i * 10, 0, 7])
            rotate([90, 0, 0])
            color([0.4, 0.8, 1, 0.6])
            cylinder(h = 4, d1 = 1, d2 = 3, $fn = 8);
        }

        // Mystical energy orbs
        translate([25, 0, 0])
        color([1, 1, 1, 0.7])
        sphere(d = 3, $fn = 15);

        translate([45, 0, 2])
        color([1, 1, 1, 0.7])
        sphere(d = 2.5, $fn = 15);
    }
}

// Generate the creature
velithra();

// Zephyrel - Wind Spirit of the Western Sky
// A mythical creature with avian and draconic features, guardian of gentle breezes

$fn = 32;

// Main module for the complete zephyrel
module zephyrel() {
    color("LightSkyBlue") {
        // Body - elongated teardrop shape
        rotate([0, 90, 0]) 
            scale([1, 1.2, 1.5]) 
                sphere(12);

        // Head with beak-like snout
        translate([0, 15, 0]) {
            // Main head sphere
            sphere(8);

            // Beak/snout
            translate([0, 8, 0])
                rotate([0, 90, 0])
                    scale([0.6, 0.6, 1.2])
                        cylinder(h=8, r1=6, r2=2);

            // Eyes
            translate([-4, 4, 5]) sphere(2);
            translate([4, 4, 5]) sphere(2);
        }

        // Wings - feathered and swept back
        translate([0, 0, 5]) {
            // Left wing
            translate([-15, 0, 0]) {
                rotate([0, 20, -30]) {
                    // Main wing structure
                    scale([1, 2.5, 0.2]) sphere(8);

                    // Wing feathers
                    for(i = [0:3]) {
                        translate([-i*2, -5-i*3, -1])
                            rotate([0, 0, -10-i*3])
                                scale([0.3, 1-i*0.1, 0.1])
                                    cylinder(h=8, r1=3, r2=1);
                    }
                }
            }

            // Right wing (mirrored)
            mirror([1, 0, 0]) {
                translate([-15, 0, 0]) {
                    rotate([0, 20, -30]) {
                        scale([1, 2.5, 0.2]) sphere(8);
                        for(i = [0:3]) {
                            translate([-i*2, -5-i*3, -1])
                                rotate([0, 0, -10-i*3])
                                    scale([0.3, 1-i*0.1, 0.1])
                                        cylinder(h=8, r1=3, r2=1);
                        }
                    }
                }
            }
        }

        // Tail - flowing wind-like ribbons
        translate([0, -20, 0]) {
            rotate([0, -30, 0]) {
                scale([1, 3, 0.3]) sphere(8);

                // Tail streamers
                for(i = [0:2]) {
                    rotate([0, i*15, 0]) {
                        translate([0, -8, 0])
                            rotate([0, i*10, 0])
                                scale([0.4, 2-i*0.3, 0.1])
                                    cylinder(h=12, r1=2, r2=0.5);
                    }
                }
            }
        }

        // Legs - taloned with backward knees
        translate([0, 0, -10]) {
            // Left leg
            translate([-5, 0, 0]) {
                rotate([0, 0, -10]) {
                    // Upper leg
                    cylinder(h=8, r1=2, r2=1.5);

                    // Lower leg
                    translate([0, 0, -9])
                        rotate([30, 0, 0])
                            cylinder(h=6, r1=1.5, r2=1);

                    // Talon
                    translate([0, 0, -16])
                        union() {
                            sphere(1.5);
                            // Toes
                            for(angle = [0:120:240]) {
                                rotate([0, 0, angle])
                                    translate([1.5, 0, 0])
                                        rotate([90, 0, 0])
                                            cylinder(h=3, r1=0.5, r2=0.3);
                            }
                        }
                }
            }

            // Right leg (mirrored)
            translate([5, 0, 0]) {
                rotate([0, 0, 10]) {
                    cylinder(h=8, r1=2, r2=1.5);
                    translate([0, 0, -9])
                        rotate([30, 0, 0])
                            cylinder(h=6, r1=1.5, r2=1);
                    translate([0, 0, -16])
                        union() {
                            sphere(1.5);
                            for(angle = [0:120:240]) {
                                rotate([0, 0, angle])
                                    translate([1.5, 0, 0])
                                        rotate([90, 0, 0])
                                            cylinder(h=3, r1=0.5, r2=0.3);
                            }
                        }
                }
            }
        }

        // Horns - spiral wind horns
        translate([0, 10, 8]) {
            // Left horn
            translate([-6, 0, 0]) {
                rotate([0, -20, -30])
                    scale([1, 1, 2])
                        cylinder(h=6, r1=1.5, r2=0.5);
            }

            // Right horn
            translate([6, 0, 0]) {
                rotate([0, -20, 30])
                    scale([1, 1, 2])
                        cylinder(h=6, r1=1.5, r2=0.5);
            }
        }

        // Wind wisps - ethereal details
        for(i = [0:5]) {
            rotate([0, i*60, 0]) {
                translate([0, 5+i*2, 2])
                    rotate([0, 0, i*45])
                        scale([0.2, 0.2, 1])
                            sphere(3);
            }
        }
    }
}

// Scale to printable size (80mm wingspan)
scale([0.7, 0.7, 0.7])
    zephyrel();

// Aelion - Mythical Winged Lion Creature
// Medium complexity, realistic style, 3D printable

// Parameters
$fn = 32;
scale_factor = 1;
body_length = 40 * scale_factor;
body_height = 25 * scale_factor;
leg_height = 15 * scale_factor;
wing_span = 60 * scale_factor;
head_size = 18 * scale_factor;

// Main assembly
union() {
    // Body
    color("Sienna") {
        translate([0, 0, leg_height]) {
            // Main torso
            difference() {
                scale([1.2, 0.8, 0.7]) sphere(d = body_length);
                translate([0, 0, -body_height/2]) cube([body_length*1.5, body_length*1.5, body_height], center=true);
            }

            // Chest mane
            translate([body_length/2.5, 0, -body_height/4]) {
                rotate([0, 90, 0]) {
                    for(i = [0:15:360]) {
                        rotate([0, 0, i]) {
                            translate([0, 5, 0]) cylinder(h = 8, d1 = 4, d2 = 2);
                        }
                    }
                }
            }
        }
    }

    // Head
    color("Goldenrod") {
        translate([body_length/1.5, 0, leg_height + body_height/2]) {
            // Lion head
            sphere(d = head_size);

            // Muzzle
            translate([head_size/2, 0, -head_size/4]) {
                scale([1.2, 0.8, 0.8]) sphere(d = head_size/1.5);
            }

            // Eyes
            translate([head_size/3, head_size/3, head_size/4]) {
                color("Black") sphere(d = 2);
            }
            translate([head_size/3, -head_size/3, head_size/4]) {
                color("Black") sphere(d = 2);
            }

            // Nose
            translate([head_size/1.2, 0, -head_size/6]) {
                color("DarkGoldenrod") sphere(d = 3);
            }
        }
    }

    // Mane
    color("DarkGoldenrod") {
        translate([body_length/2, 0, leg_height + body_height/2]) {
            for(angle = [0:30:359]) {
                rotate([0, 0, angle]) {
                    translate([head_size/2, 0, 0]) {
                        rotate([90, 0, 0]) {
                            cylinder(h = head_size/2, d1 = 3, d2 = 1);
                        }
                    }
                }
            }
        }
    }

    // Legs
    module leg(x_pos, y_pos, rotation) {
        translate([x_pos, y_pos, 0]) {
            rotate([0, 0, rotation]) {
                color("Sienna") {
                    // Upper leg
                    translate([0, 0, leg_height/2]) {
                        cylinder(h = leg_height, d1 = 8, d2 = 6);
                    }

                    // Paw
                    translate([0, 0, leg_height - 2]) {
                        sphere(d = 10);
                    }
                }
            }
        }
    }

    // Position legs
    leg(-body_length/3, body_length/3, 0);
    leg(-body_length/3, -body_length/3, 0);
    leg(body_length/4, body_length/3, 0);
    leg(body_length/4, -body_length/3, 0);

    // Wings
    module wing(side) {
        translate([0, side * body_length/2, leg_height + body_height/2]) {
            rotate([0, side * -20, side * -30]) {
                color(["LightBlue", "LightSteelBlue"][abs(side)]) {
                    // Main wing
                    scale([1.5, 0.1, 1]) {
                        difference() {
                            sphere(d = wing_span/2);
                            translate([0, -wing_span/4, 0]) cube([wing_span, wing_span/2, wing_span], center=true);
                        }
                    }

                    // Wing feathers
                    for(i = [1:3]) {
                        translate([wing_span/3 - i*8, side * 2, -i*3]) {
                            rotate([0, side * -10, 0]) {
                                scale([0.8, 0.05, 0.3]) sphere(d = wing_span/3);
                            }
                        }
                    }
                }
            }
        }
    }

    wing(1);
    wing(-1);

    // Tail
    color("Sienna") {
        translate([-body_length/2, 0, leg_height + body_height/3]) {
            rotate([0, 20, 0]) {
                cylinder(h = body_length/2, d1 = 8, d2 = 4);

                // Tail tuft
                translate([0, 0, -body_length/2 - 2]) {
                    sphere(d = 10);

                    // Magical flame
                    color("Orange") {
                        translate([0, 0, -5]) {
                            sphere(d = 6);
                        }
                    }
                }
            }
        }
    }

    // Horns
    color("Ivory") {
        translate([body_length/1.5 + head_size/2, head_size/3, leg_height + body_height/2 + head_size/3]) {
            rotate([0, -20, 20]) {
                cylinder(h = 8, d1 = 4, d2 = 2);
            }
        }
        translate([body_length/1.5 + head_size/2, -head_size/3, leg_height + body_height/2 + head_size/3]) {
            rotate([0, -20, -20]) {
                cylinder(h = 8, d1 = 4, d2 = 2);
            }
        }
    }
}

// Astrophel - Celestial Stag of the Stars (Medium Complexity)
// Wings, star patterns, celestial crown, and ethereal tail

use <colors.scad>

// Main body parameters
scale_factor = 0.8;
body_length = 60 * scale_factor;
body_height = 35 * scale_factor;
body_width = 25 * scale_factor;

module astrophel_body() {
    color("silver") {
        // Main torso
        translate([0, 0, body_height/2])
            scale([1.2, 0.8, 1])
            sphere(d=body_height);

        // Chest section
        translate([body_length/4, 0, body_height/2])
            scale([0.8, 0.7, 1.1])
            sphere(d=body_height*0.9);

        // Hindquarters
        translate([-body_length/4, 0, body_height/2])
            scale([0.9, 0.85, 1])
            sphere(d=body_height*0.95);
    }
}

module celestial_crown() {
    color("gold") {
        // Base ring
        rotate([90, 0, 0])
            cylinder(h=3, r1=15*scale_factor, r2=12*scale_factor);

        // Star points
        for(i = [0:30:330]) {
            rotate([0, 0, i])
                translate([13*scale_factor, 0, 2*scale_factor])
                    scale([0.2, 0.2, 1])
                    cylinder(h=8*scale_factor, r1=3*scale_factor, r2=0);
        }
    }
}

module wing() {
    color("lightblue") {
        // Main wing membrane
        translate([0, 0, 5*scale_factor])
            rotate([0, -20, 0])
                linear_extrude(height=1)
                    polygon(points=[
                        [0, 0],
                        [30*scale_factor, 15*scale_factor],
                        [45*scale_factor, 0],
                        [40*scale_factor, -15*scale_factor],
                        [25*scale_factor, -10*scale_factor],
                        [10*scale_factor, -5*scale_factor],
                        [0, 0]
                    ]);

        // Wing bones
        for(pos = [[0, 0], [15*scale_factor, 7*scale_factor], [30*scale_factor, 0]]) {
            translate([pos[0], pos[1], 5*scale_factor])
                rotate([0, 90, 0])
                    cylinder(h=2*scale_factor, r=1*scale_factor);
        }
    }
}

module head() {
    color("silver") {
        // Main head
        translate([body_length/2 + 10*scale_factor, 0, body_height*0.8])
            scale([1.2, 0.8, 1])
            sphere(d=20*scale_factor);

        // Muzzle
        translate([body_length/2 + 20*scale_factor, 0, body_height*0.7])
            scale([1.5, 0.6, 0.6])
            sphere(d=15*scale_factor);

        // Celestial crown placement
        translate([body_length/2 + 5*scale_factor, 0, body_height*1.2])
            celestial_crown();

        // Eyes (star gems)
        color("cyan") {
            translate([body_length/2 + 15*scale_factor, 7*scale_factor, body_height*0.9])
                sphere(d=4*scale_factor);
            translate([body_length/2 + 15*scale_factor, -7*scale_factor, body_height*0.9])
                sphere(d=4*scale_factor);
        }
    }
}

module legs() {
    color("silver") {
        // Front legs
        for(side = [-1, 1]) {
            translate([body_length/4, side*body_width/2, 0])
                cylinder(h=body_height*0.7, r=4*scale_factor);
            translate([body_length/4, side*body_width/2, body_height*0.7])
                sphere(d=5*scale_factor);
        }

        // Back legs
        for(side = [-1, 1]) {
            translate([-body_length/4, side*body_width/2, 0])
                cylinder(h=body_height*0.7, r=4*scale_factor);
            translate([-body_length/4, side*body_width/2, body_height*0.7])
                sphere(d=5*scale_factor);
        }
    }
}

module ethereal_tail() {
    color("lightblue") {
        // Tail segments with decreasing size
        for(i = [0:5]) {
            translate([-(body_length/2 + i*8*scale_factor), 0, body_height*0.5 - i*2*scale_factor])
                sphere(d=12*scale_factor - i*1.5*scale_factor);
        }

        // Star trail effect
        for(i = [1:3:15]) {
            color("white") {
                translate([-(body_length/2 + i*3*scale_factor), 0, body_height*0.5 - i*0.5*scale_factor])
                    sphere(d=2*scale_factor);
            }
        }
    }
}

module star_patterns() {
    color("white") {
        // Stars along body
        for(i = [0:45:360]) {
            rotate([0, 0, i])
                translate([0, 0, body_height*0.8])
                    sphere(d=1.5*scale_factor);
        }

        // Additional star clusters
        for(pos = [[body_length/3, 0], [-body_length/3, 0], [0, body_width/3], [0, -body_width/3]]) {
            translate([pos[0], pos[1], body_height*0.9])
                sphere(d=2*scale_factor);
        }
    }
}

// Assembly
union() {
    astrophel_body();
    head();
    legs();
    ethereal_tail();

    // Wings - positioned on back
    translate([0, body_width/2, body_height*0.9])
        rotate([0, 0, 30])
        wing();
    translate([0, -body_width/2, body_height*0.9])
        rotate([0, 0, -30])
        mirror([0, 1, 0])
        wing();

    // Star patterns on body
    star_patterns();
}

