            translate([3, 3, 32])
                sphere(r=1.2, $fn=12);
            translate([-3, 3, 32])
                sphere(r=1.2, $fn=12);

            // Crest feathers
            for(i=[-30:30]) {
                color(color_wing)
                translate([0, -2, 38 + abs(i)/10])
                    rotate([0, 0, i])
                    translate([0, 0, 2])
                    cylinder(h=abs(i)/2 + 3, r1=0.8, r2=0.2, $fn=6);
            }

            // Wings
            module wing(side) {
                mirror([side, 0, 0]) {
                    // Wing base
                    color(color_wing)
                    translate([10, -3, 25])
                        rotate([0, -20, -30])
                        scale([1, 0.3, 1])
                        sphere(r=12, $fn=24);

                    // Primary feathers
                    for(i=[0:4]) {
                        color(color_wing)
                        translate([18 - i*2, -2, 22 - i])
                            rotate([0, 0, -30 - i*5])
                            scale([0.5, 0.1, 2])
                            cylinder(h=8 - i*1.2, r1=1.5, r2=0.5, $fn=6);
