                    for(j=[0:3]) {
                        color(color_wing)
                        translate([12 - j*1.5, -1.5, 28 - j*1.5])
                            rotate([0, 10, -20])
                            scale([0.4, 0.1, 1.5])
                            cylinder(h=6 - j, r1=1, r2=0.3, $fn=6);
