                translate([0, 0, 12])
                    rotate([0, 0, k])
                    translate([0, -4 - abs(k)/10, -5 - abs(k)/5])
                    scale([0.3, 0.1, 1 + abs(k)/30])
                    cylinder(h=15 + abs(k)/3, r1=1.5, r2=0.3, $fn=6);
            }

