            module leg(side) {
                color(color_emby)
                translate([side*3, -3, 8])
                    cylinder(h=7, r=1.5, $fn=12);

                // Feet
                color("#FF8C00")
                translate([side*3, -4, 1])
                    scale([1, 2, 0.5])
                    sphere(r=2, $fn=12);

                // Toes
                for(t=[-1:1]) {
