color_wings = "#FFD23F";     // Golden yellow
color_crystal = "#FFE066";   // Bright crystal
color_eyes = "#FF1744";      // Deep red

// Overall scale for 3D printing
scale_factor = 0.8;

module heliora() {
    scale([scale_factor, scale_factor, scale_factor]) {
        // Main body assembly
        union() {
            // Head with solar crest
            translate([0, 0, 30]) head();

            // Neck connecting to body
            translate([0, 0, 20]) neck();

            // Main torso with crystalline segments
            body();

            // Wings with solar membrane
            translate([-10, 0, 15]) wings();

            // Tail with molten segments
            translate([0, 0, -30]) tail();

            // Four legs
            translate([-8, -5, 0]) leg();
            translate([-8, 5, 0]) leg();
            translate([8, -5, 0]) leg();
            translate([8, 5, 0]) leg();
        }
    }
}

module head() {
    color(color_body) {
        // Main head shape
        difference() {
            sphere(d=12);
            // Eye sockets
            translate([4, 3, 2]) sphere(d=3);
            translate([4, -3, 2]) sphere(d=3);
        }

        // Snout
        translate([5, 0, -2]) scale([1.5, 0.8, 0.8]) sphere(d=6);

        // Solar crest with crystalline structure
        color(color_crystal) {
            for(i = [0:3]) {
                rotate([0, 0, i * 90]) 
                translate([0, 4, 6]) 
                scale([0.5, 0.5, 2]) 
                cylinder(d=2, h=4);
            }
        }

        // Eyes
        color(color_eyes) {
            translate([5.5, 2.5, 2]) sphere(d=1.5);
            translate([5.5, -2.5, 2]) sphere(d=1.5);
        }
    }
}

module neck() {
    color(color_body) {
        // Tapered neck with scale texture
        cylinder(d1=8, d2=6, h=12);

        // Scale rings
        for(i = [1:3]) {
            translate([0, 0, i * 3]) 
            difference() {
                cylinder(d=7.5, h=1);
                cylinder(d=6, h=1.1);
            }
        }
    }
}

module body() {
    color(color_body) {
        // Main torso
        scale([1.2, 0.8, 1]) sphere(d=20);

        // Crystalline spine segments
        color(color_crystal) {
            for(i = [-4:2:4]) {
                translate([i, 0, 8]) 
                scale([1, 0.5, 0.3]) 
                sphere(d=6);
            }
        }

        // Solar core in chest
        color(color_wings) {
            translate([0, 0, 2]) 
            sphere(d=4);
        }
    }
}

module wings() {
    color(color_wings) {
        // Left wing
        translate([0, -15, 0]) {
            // Main wing membrane
            difference() {
                scale([2, 1.5, 0.1]) sphere(d=15);
                translate([15, 0, -1]) cube([30, 30, 2], center=true);
            }

            // Wing bones
            color(color_body) {
                for(i = [0:30:120]) {
                    rotate([0, 0, i]) 
                    translate([7.5, 0, 0]) 
                    scale([15, 0.5, 0.5]) 
                    cylinder(d=1, h=1);
                }
            }

            // Crystalline wing tips
            color(color_crystal) {
                translate([20, 0, 0]) 
                scale([1, 0.5, 0.5]) 
                sphere(d=3);
            }
        }

        // Right wing (mirrored)
        mirror([0, 1, 0]) 
        translate([0, -15, 0]) {
            difference() {
                scale([2, 1.5, 0.1]) sphere(d=15);
                translate([15, 0, -1]) cube([30, 30, 2], center=true);
            }

            color(color_body) {
                for(i = [0:30:120]) {
                    rotate([0, 0, i]) 
                    translate([7.5, 0, 0]) 
                    scale([15, 0.5, 0.5]) 
                    cylinder(d=1, h=1);
                }
            }

            color(color_crystal) {
                translate([20, 0, 0]) 
                scale([1, 0.5, 0.5]) 
                sphere(d=3);
            }
        }
    }
}

module tail() {
    color(color_body) {
        // Segmented tail with molten glow
        for(i = [0:3]) {
            translate([0, 0, -i * 8 - 5]) {
                // Each segment smaller than last
                scale([1 - i * 0.15, 1 - i * 0.15, 1]) 
                sphere(d=8 - i * 2);

                // Molten ring between segments
                if(i < 3) {
                    color(color_wings) {
                        translate([0, 0, 4 - i]) 
                        cylinder(d=6 - i * 1.5, h=1);
                    }
                }
            }
        }

        // Tail flame tip
        color(color_crystal) {
            translate([0, 0, -30]) 
            scale([0.3, 0.3, 2]) 
            cylinder(d=3, h=5);
        }
    }
}

module leg() {
    color(color_body) {
        // Upper leg
        translate([0, 0, -5]) 
        cylinder(d=4, h=8);

        // Lower leg
        translate([0, 0, -10]) 
        cylinder(d=3, h=6);

        // Foot with talons
        translate([0, 0, -14]) {
            // Main foot
            scale([1.5, 0.8, 0.5]) sphere(d=4);

            // Three talons
            for(i = [-1:1]) {
                translate([i * 1.5, 2, -1]) 
                scale([0.3, 0.3, 1]) 
                cylinder(d=1, h=2);
            }
        }
    }
}

// Render the complete heliora
heliora();

// Ilior - Winged Serpent of the Celestial Depths
// A medium-complexity realistic 3D printable model

$fn = 32;

module ilior_body() {
    // Main serpentine body
    color("darkgreen")
    union() {
        // Head
        translate([0, 0, 25])
            scale([1.2, 0.8, 1])
            sphere(8);

        // Snout
        translate([0, 6, 25])
            rotate([90, 0, 0])
            cylinder(4, 2, 0);

        // Neck
        translate([0, 0, 20])
            scale([0.8, 0.8, 1.5])
            sphere(5);

        // Body segments
        for(i = [0:5]) {
            translate([0, 0, 20 - i*3])
                scale([1 - i*0.1, 1 - i*0.1, 1])
                sphere(6 - i*0.5);
        }

        // Tail
        translate([0, 0, 2])
            rotate([30, 0, 0])
            scale([0.3, 0.3, 1])
            cylinder(15, 2, 0.5);
    }
}

module ilior_wings() {
    color("forestgreen")
    union() {
        // Left wing membrane
        translate([-12, 0, 20])
            rotate([0, 0, 30])
            scale([1, 0.5, 0.1])
            sphere(15);

        // Right wing membrane
        translate([12, 0, 20])
            rotate([0, 0, -30])
            scale([1, 0.5, 0.1])
            sphere(15);

        // Wing bones
        for(side = [-1, 1]) {
            color("darkgreen")
            translate([side*8, 0, 22])
                rotate([0, 0, side*45])
                cylinder(12, 1, 0.5);

            translate([side*6, 0, 18])
                rotate([0, 0, side*30])
                cylinder(10, 0.8, 0.5);
        }
    }
}

module ilior_horns() {
    color("darkslategray")
    union() {
        // Main horns
        translate([-3, -3, 32])
            rotate([0, 30, 45])
            cylinder(6, 1.5, 0.5);

        translate([3, -3, 32])
            rotate([0, 30, -45])
            cylinder(6, 1.5, 0.5);

        // Smaller horns
        for(i = [0:1]) {
            translate([i*2 - 1, -2, 30])
                rotate([0, 20, 0])
                cylinder(3, 1, 0.3);
        }
    }
}

module ilior_scales() {
    color("forestgreen")
    difference() {
        // Scale pattern on body
        for(i = [0:8]) {
            translate([sin(i*45)*3, cos(i*45)*3, 20 - i*2])
                scale([0.5, 0.5, 0.3])
                sphere(2);
        }

        // Carve out scale details
        for(i = [0:8]) {
            translate([sin(i*45)*3, cos(i*45)*3, 19.8 - i*2])
                scale([0.4, 0.4, 0.4])
                sphere(1.8);
        }
    }
}

module ilior_eyes() {
    color("gold")
    union() {
        // Left eye
        translate([-2.5, 4, 27])
            sphere(1);

        // Right eye
        translate([2.5, 4, 27])
            sphere(1);
    }
}

module ilior_fin() {
    color("darkgreen")
    // Dorsal fin
    for(i = [0:3]) {
        translate([0, 0, 18 - i*3])
            rotate([0, 0, 0])
            scale([0.3, 0.1, 1])
            cube([3, 1, 2], center=true);
    }
}

// Assembly
union() {
    ilior_body();
    ilior_wings();
    ilior_horns();
    ilior_scales();
    ilior_eyes();
    ilior_fin();
}

// Base for printing stability
color("darkgreen")
translate([0, 0, -1])
    cylinder(1, 15, 15);

// Lunaris - Mythical Moon Dragon
// Realistic style, medium complexity

// Colors
color_body = "DarkSlateBlue";
color_wings = "MidnightBlue";
color_accents = "Silver";

// Main body
module lunaris_body() {
    union() {
        // Torso
        color(color_body)
        translate([0, 0, 20])
        scale([1, 0.6, 1.2])
        sphere(15, $fn=32);

        // Chest plating
        color(color_accents)
        translate([0, -8, 20])
        rotate([15, 0, 0])
        scale([1.2, 0.3, 0.8])
        cube([18, 2, 12], center=true);

        // Hip section
        color(color_body)
        translate([0, 0, 8])
        scale([1.1, 0.7, 0.9])
        sphere(12, $fn=32);
    }
}

// Head with crescent moon features
module lunaris_head() {
    union() {
        // Main head
        color(color_body)
        translate([0, -25, 32])
        rotate([-20, 0, 0])
        scale([0.8, 1.2, 0.9])
        sphere(12, $fn=32);

        // Snout
        color(color_body)
        translate([0, -35, 28])
        rotate([-30, 0, 0])
        scale([0.4, 0.8, 0.5])
        cylinder(8, 8, 5, $fn=16);

        // Crescent moon horn
        color(color_accents)
        translate([0, -30, 42])
        rotate([90, 0, 0])
        linear_extrude(height=2)
        difference() {
            circle(10, $fn=64);
            translate([0, 5, 0])
            circle(8, $fn=64);
        }

        // Eyes
        color("Cyan")
        translate([-5, -28, 32])
        sphere(2, $fn=16);
        translate([5, -28, 32])
        sphere(2, $fn=16);
    }
}

// Wings with star patterns
module lunaris_wings() {
    module wing_half(side) {
        color(color_wings)
        translate([side * 15, 0, 25])
        rotate([0, 0, side * 15])
        scale([1, 0.1, 1])
        difference() {
            // Main wing shape
            translate([0, 0, 0])
            rotate([0, 30, 0])
            scale([1.5, 1, 1])
            sphere(20, $fn=32);

            // Wing membrane cuts
            for(i = [1:3]) {
                translate([side * i * 4, 0, i * 3])
                rotate([0, 10 * i, 0])
                scale([0.8, 1, 0.6])
                sphere(8, $fn=16);
            }
        }

        // Wing support bones
        color(color_accents)
        for(i = [0:2]) {
            translate([side * (10 + i * 5), 0, 22 + i * 4])
            rotate([0, 30 - i * 10, side * (15 + i * 10)])
            cylinder(15, 1, 0.5, $fn=8);
        }
    }

    wing_half(1);
    wing_half(-1);
}

// Tail with lunar segments
module lunaris_tail() {
    color(color_body)
    hull() {
        translate([0, 8, 15])
        sphere(8, $fn=16);
        translate([0, 25, 10])
        sphere(6, $fn=16);
        translate([0, 40, 12])
        sphere(4, $fn=16);
    }

    // Lunar segments
    color(color_accents)
    for(i = [1:3]) {
        translate([0, 10 + i * 10, 14 - i])
        rotate([0, 0, i * 45])
        difference() {
            cylinder(3, 5, 5, $fn=32);
            translate([0, 2, 0])
            cube([4, 4, 3.1], center=true);
        }
    }
}

// Legs with moon claws
module lunaris_legs() {
    module leg(side) {
        color(color_body)
        translate([side * 8, 0, 10])
        rotate([0, side * 10, 0])
        cylinder(12, 4, 3, $fn=16);

        // Foot
        color(color_body)
        translate([side * 10, 0, 0])
        scale([1.5, 0.8, 0.5])
        sphere(5, $fn=16);

        // Moon claws
        color(color_accents)
        for(i = [-1:1]) {
            translate([side * (10 + i * 2), 0, -2])
            rotate([0, 0, side * 30])
            cylinder(3, 1, 0.5, $fn=8);
        }
    }

    leg(1);
    leg(-1);
}

// Assemble the Lunaris
union() {
    lunaris_body();
    lunaris_head();
    lunaris_wings();
    lunaris_tail();
    lunaris_legs();
}

// Nebulon - Mythical Space Dragon Creature
// Medium complexity realistic style

// Main colors
$fa = 2;
$fs = 0.5;

// Root module
module nebulon_dragon() {
    // Body
    union() {
        // Main torso
        color("RoyalBlue") {
            translate([0, 0, 10]) {
                scale([1.5, 0.8, 1.2]) {
                    sphere(d=30);
                }
            }

            // Neck
            translate([0, 15, 25]) {
                rotate([30, 0, 0]) {
                    cylinder(h=20, d1=15, d2=8, $fn=8);
                }
            }

            // Head
            translate([0, 25, 35]) {
                scale([0.8, 1.2, 0.9]) {
                    sphere(d=12);
                }
            }
        }

        // Wings
        color("MidnightBlue") {
            // Left wing
            translate([-15, 5, 20]) {
                rotate([0, 0, -45]) {
                    rotate([0, 30, 0]) {
                        difference() {
                            scale([1, 2.5, 0.1]) {
                                sphere(d=25);
                            }
                            translate([5, 10, 0]) {
                                sphere(d=20);
                            }
                        }
                    }
                }
            }

            // Right wing
            mirror([1, 0, 0]) {
                translate([-15, 5, 20]) {
                    rotate([0, 0, -45]) {
                        rotate([0, 30, 0]) {
                            difference() {
                                scale([1, 2.5, 0.1]) {
                                    sphere(d=25);
                                }
                                translate([5, 10, 0]) {
                                    sphere(d=20);
                                }
                            }
                        }
                    }
                }
            }
        }

        // Tail
        color("Navy") {
            translate([0, -20, 15]) {
                rotate([20, 0, 0]) {
                    for(i = [0:3]) {
                        translate([0, -i*8, -i*3]) {
                            rotate([i*5, 0, 0]) {
                                scale([1 - i*0.15, 1 - i*0.15, 1 - i*0.15]) {
                                    sphere(d=12 - i*2);
                                }
                            }
                        }
                    }
                }
            }
        }

        // Legs
        color("RoyalBlue") {
            // Front left leg
            translate([-8, 8, 5]) {
                rotate([0, 15, 0]) {
                    cylinder(h=15, d=6, $fn=6);
                    translate([0, 0, -15]) {
                        sphere(d=8);
                    }
                }
            }

            // Front right leg
            mirror([1, 0, 0]) {
                translate([-8, 8, 5]) {
                    rotate([0, 15, 0]) {
                        cylinder(h=15, d=6, $fn=6);
                        translate([0, 0, -15]) {
                            sphere(d=8);
                        }
                    }
                }
            }

            // Back legs
            translate([-10, -15, 5]) {
                rotate([0, -15, 0]) {
                    cylinder(h=18, d=7, $fn=6);
                    translate([0, 0, -18]) {
                        sphere(d=9);
                    }
                }
            }

            mirror([1, 0, 0]) {
                translate([-10, -15, 5]) {
                    rotate([0, -15, 0]) {
                        cylinder(h=18, d=7, $fn=6);
                        translate([0, 0, -18]) {
                            sphere(d=9);
                        }
                    }
                }
            }
        }

        // Horns
        color("DarkSlateGray") {
            // Left horn
            translate([-3, 28, 38]) {
                rotate([30, 0, -20]) {
                    cylinder(h=8, d1=3, d2=1, $fn=6);
                }
            }

            // Right horn
            translate([3, 28, 38]) {
                rotate([30, 0, 20]) {
                    cylinder(h=8, d1=3, d2=1, $fn=6);
                }
            }
        }

        // Eyes
        color("Cyan") {
            translate([-3, 30, 38]) {
                sphere(d=2);
            }
            translate([3, 30, 38]) {
                sphere(d=2);
            }
        }

        // Star spots on wings
        color("LightSteelBlue") {
            for(i = [1:5]) {
                wing_star(-20 + i*3, 10 + i*4, 22 - i, 1.5);
                wing_star(20 - i*3, 10 + i*4, 22 - i, 1.5);
            }
        }

        // Nebula cloud base
        color("Indigo") {
            translate([0, 0, -5]) {
                scale([2, 3, 0.5]) {
                    sphere(d=40);
                }
            }
        }
    }
}

// Star spots module
module wing_star(x, y, z, size) {
    translate([x, y, z]) {
        sphere(d=size);
    }
}

// Render the creature
nebulon_dragon();

// Oriael - Seraphic Griffin of the Dawn
// A regal hybrid: lion body, eagle head, six radiant wings, and golden fire horns

scale_factor = 0.7;     // Overall model fits ~70 mm
$fn = 36;               // Smooth curves for printing

module oriael() {
    color("gold") union() {

        // Eagle head
        translate([0, -15, 20]) rotate([0, 0, 0]) union() {
            // Beak
            difference() {
                cylinder(h = 12, r1 = 6, r2 = 2, center = true);
                translate([0, 0, 3]) cube([12, 12, 8], center = true);
            }
            // Head sphere
            sphere(8);
            // Eyes
            color("black") {
                translate([3, -4, 2]) sphere(1.5);
                translate([-3, -4, 2]) sphere(1.5);
            }
        }

        // Mane / neck
        translate([0, -8, 14]) rotate([20, 0, 0])
            cylinder(h = 12, r1 = 9, r2 = 7, center = true);

        // Lion body
        translate([0, 10, 12]) rotate([0, 0, 0])
            scale([1.2, 1.5, 1])
            sphere(12);

        // Front legs
        for (x = [-8, 8]) {
            translate([x, 5, 0]) rotate([0, 0, 0])
                cylinder(h = 22, r = 4, center = false);
            translate([x, 5, -9]) sphere(4.5);
        }

        // Rear legs
        for (x = [-7, 7]) {
            translate([x, 20, 0]) rotate([0, 0, 0])
                cylinder(h = 24, r = 4.5, center = false);
            translate([x, 20, -10]) sphere(5);
        }

        // Tail with feather tuft
        translate([0, 28, 12]) rotate([0, 0, 0])
            cylinder(h = 22, r = 2, center = false);
        translate([0, 45, 12]) scale([1, 0.8, 0.4])
            sphere(6);

        // Wings (3 pairs stacked)
        for (i = [0:1:2]) {
            color("ivory") {
                // Left wing
                translate([-15 + 2*i, 6 + 6*i, 22 - 4*i]) rotate([0, 0, 30 - 10*i]) {
                    difference() {
                        scale([1.5, 1, 0.1])
                            cylinder(h = 25, r1 = 8, r2 = 2, center = false);
                        translate([0, -4, 0]) cube([20, 8, 2], center = true);
                    }
                }
                // Right wing
                translate([15 - 2*i, 6 + 6*i, 22 - 4*i]) mirror([1, 0, 0]) rotate([0, 0, 30 - 10*i]) {
                    difference() {
                        scale([1.5, 1, 0.1])
                            cylinder(h = 25, r1 = 8, r2 = 2, center = false);
                        translate([0, -4, 0]) cube([20, 8, 2], center = true);
                    }
                }
            }
        }

        // Fire horns
        color("orangered") {
            // Central horn
            translate([0, -12, 28]) rotate([0, 0, 0])
                cylinder(h = 12, r1 = 2, r2 = 1, center = false);
            // Side horns
            translate([4, -10, 26]) rotate([10, 0, 20])
                cylinder(h = 10, r1 = 1.8, r2 = 1, center = false);
            translate([-4, -10, 26]) rotate([10, 0, -20])
                cylinder(h = 10, r1 = 1.8, r2 = 1, center = false);
        }

        // Chest sun emblem
        color("yellow") {
            translate([0, 2, 18]) rotate([90, 0, 0])
                cylinder(h = 1, r = 4, center = true);
            for (a = [0:30:360]) {
                rotate([90, 0, a])
                    translate([0, 3, 0])
                        cube([0.5, 2, 1], center = true);
            }
        }
    }
}

scale(scale_factor) oriael();

