// Flame mane - individual flame wisps
module flame_wisp(x, y, z, angle) {
    color([1, 0.3 + rand(x*y)/5, 0]) {
        translate([x, y, z])
        rotate([0, 0, angle])
        scale([0.5, 0.3, 1.5])
        sphere(r=1.5);
    }
}

