// Quoriel - Mythical winged serpent-dragon
// Medium complexity realistic style

// Color definitions
color_body = "DarkSlateBlue";
color_wings = "MidnightBlue";
color_scales = "RoyalBlue";
color_eyes = "Gold";
color_claws = "Silver";
color_spikes = "DarkViolet";

// Scale factor for 3D printing
scale_factor = 0.4;

module body_segment(radius, length, segments = 8) {
    // Create a cylindrical segment with scales
    union() {
        // Main body cylinder
        color(color_body)
            cylinder(h = length, r = radius, $fn = segments);

        // Scales along the body
        for (i = [0:segments-1]) {
            rotate([0, 0, i * 360/segments])
                translate([radius * 0.8, 0, length * 0.3])
                    color(color_scales)
                        scale([0.3, 0.1, 0.2])
                            sphere(r = radius * 0.5);
        }
    }
}

module wing() {
    // Main wing structure
    color(color_wings)
    union() {
        // Upper wing bone
        translate([0, 0, -2])
            rotate([90, 0, 0])
                cylinder(h = 25, r1 = 2, r2 = 1);

        // Lower wing bone
        translate([15, 0, -2])
            rotate([90, 0, 45])
                cylinder(h = 20, r1 = 1.5, r2 = 0.8);

        // Wing membrane
        translate([7.5, -1, 0])
            rotate([0, 90, 0])
                scale([1, 0.05, 0.7])
                    cylinder(h = 20, r1 = 12, r2 = 5);
    }
}

module head() {
    color(color_body)
    union() {
        // Main head
        translate([0, 0, 5])
            sphere(r = 5);

        // Snout
        translate([0, 0, 8])
            scale([1.5, 1, 1])
                sphere(r = 3);

        // Horns
        color("DarkViolet")
        for (i = [-1, 1]) {
            translate([i * 3, 3, 8])
                rotate([0, 30 * i, 0])
                    cylinder(h = 8, r1 = 1.5, r2 = 0.5);
        }

        // Eyes
        color(color_eyes)
        for (i = [-1, 1]) {
            translate([i * 2, 4, 6])
                sphere(r = 1);
        }

        // Spikes along head
        color(color_spikes)
        for (i = [0:3]) {
            rotate([0, 0, i * 90])
                translate([0, 4.5, 5])
                    cylinder(h = 2, r1 = 0.8, r2 = 0.2);
        }
    }
}

module tail_segment(size, pos) {
    // Create segmented tail
    color(color_body)
    translate([0, 0, pos])
        scale([size, size, size * 2])
            sphere(r = 3);
}

module claws() {
    color(color_claws)
    union() {
        // Front claws
        for (i = [-1, 1]) {
            translate([i * 3, 2, -8])
                for (j = [0:2]) {
                    rotate([0, 0, j * 20 - 20])
                        translate([0, 1.5, 0])
                            cylinder(h = 3, r1 = 0.8, r2 = 0.3);
                }
        }

        // Rear claws
        for (i = [-1, 1]) {
            translate([i * 2, -2, -15])
                for (j = [0:2]) {
                    rotate([0, 0, j * 20 - 20])
                        translate([0, 1, 0])
                            cylinder(h = 2.5, r1 = 0.7, r2 = 0.3);
                }
        }
    }
}

// Main creature assembly
scale([scale_factor, scale_factor, scale_factor])
union() {
    // Head
    translate([0, 0, 20])
        head();

    // Neck
    translate([0, 0, 15])
        body_segment(4, 5);

    // Body segments
    translate([0, 0, 10])
        body_segment(5, 5);

    translate([0, 0, 5])
        body_segment(6, 5);

    body_segment(6.5, 5);

    translate([0, 0, -5])
        body_segment(6, 5);

    translate([0, 0, -10])
        body_segment(5, 5);

    // Tail
    for (i = [0:4]) {
        tail_segment(1 - i * 0.15, -15 - i * 6);
    }

    // Wings
    translate([0, 0, 5])
    for (side = [-1, 1]) {
        translate([0, side * 5, 0])
            rotate([0, side * 30, 0])
                wing();
    }

    // Legs
    // Front legs
    translate([3, 0, 0])
        rotate([90, 0, 0])
            cylinder(h = 8, r = 2);

    translate([-3, 0, 0])
        rotate([90, 0, 0])
            cylinder(h = 8, r = 2);

    // Rear legs
    translate([2, 0, -8])
        rotate([90, 0, 0])
            cylinder(h = 7, r = 2);

    translate([-2, 0, -8])
        rotate([90, 0, 0])
            cylinder(h = 7, r = 2);

    // Claws
    claws();
}

// Sideriel - Celestial Star Dragon
// A serpentine dragon with starlight patterns and celestial wings

$fn = 32;

// Main color scheme
color([0.8, 0.9, 1.0, 0.9]) {

// Body module - serpentine shape with star patterns
module body() {
    union() {
        // Main body segments
        for(i = [0:8]) {
            translate([i*5, sin(i*30)*3, cos(i*20)*2]) 
                rotate([0, i*10, 0])
                scale([1, 0.8, 0.8])
                sphere(d=8);
        }

        // Star patterns along body
        for(i = [0:15]) {
            rotate([0, i*25, 0])
            translate([i*2.5, sin(i*30)*3+3, cos(i*20)*2])
            scale([0.3, 0.3, 0.1])
            sphere(d=3);
        }
    }
}

// Head with horns and starlight eyes
module head() {
    union() {
        // Main head shape
        translate([-5, 0, 0])
        scale([1.2, 0.8, 0.8])
        sphere(d=10);

        // Horns
        for(side = [-1, 1]) {
            translate([-8, side*3, 3]) {
                rotate([side*20, -30, side*10])
                cylinder(h=8, d1=2, d2=1);
            }
        }

        // Eyes - glowing starlight
        color([1, 1, 0.8, 0.8])
        for(side = [-1, 1]) {
            translate([-12, side*2.5, 1])
            scale([0.5, 0.5, 0.3])
            sphere(d=3);
        }

        // Snout
        translate([-15, 0, -1])
        scale([1, 0.6, 0.5])
        sphere(d=4);
    }
}

// Celestial wings with star patterns
module wings() {
    for(side = [-1, 1]) {
        translate([-5, side*8, 3]) {
            rotate([0, side*20, 0])
            scale([1, side, 1])

            // Wing membrane
            translate([0, 0, 0])
            linear_extrude(height=0.5)
            polygon(points=[
                [0, 0], [15, 10], [20, 5], [18, -5], [10, -8], [0, 0]
            ]);

            // Wing bones
            for(i = [1:4]) {
                rotate([0, 0, i*15-30])
                translate([5, 0, 0.3])
                scale([0.3, 0.1, 1])
                cylinder(h=12, d=1);
            }

            // Star patterns on wings
            color([1, 1, 1, 0.6])
            for(i = [1:5]) {
                translate([i*3, sin(i*45)*4, 0.3])
                scale([0.2, 0.2, 0.1])
                sphere(d=2);
            }
        }
    }
}

// Tail with star trail
module tail() {
    for(i = [0:12]) {
        translate([i*3+45, sin(i*30)*2, cos(i*25)*1.5]) {
            scale([1-i*0.05, 0.8-i*0.05, 0.8-i*0.05])
            sphere(d=7-i*0.3);

            // Star trail particles
            if(i % 2 == 0) {
                color([1, 1, 1, 0.5])
                translate([0, 0, 2])
                scale([0.2, 0.2, 0.1])
                sphere(d=1.5);
            }
        }
    }
}

// Legs with celestial claws
module legs() {
    for(side = [-1, 1]) {
        translate([10, side*5, -3]) {
            // Upper leg
            rotate([30, 0, 0])
            cylinder(h=8, d=3);

            // Lower leg
            translate([0, 0, 8])
            rotate([-60, 0, 0])
            cylinder(h=6, d=2);

            // Foot/claw
            translate([0, -2, 6])
            rotate([0, 0, 0])
            scale([1, 0.3, 2])
            sphere(d=4);
        }
    }
}

// Assemble the Sideriel
union() {
    body();
    head();
    wings();
    tail();
    legs();
}

}

