// Stellara Dragon - Realistic Style, Medium Complexity
// Mythical star-breathing celestial dragon with constellation wings

// Basic parameters
dragon_length = 80;
dragon_height = 35;
dragon_width = 30;
wall_thickness = 2;

// Main dragon body
module dragon_body() {
    // Central torso with star pattern
    union() {
        // Main body segment
        difference() {
            scale([1.2, 0.8, 0.6]) sphere(d=dragon_length/2);
            translate([0, 0, -dragon_height/4]) 
                cube([dragon_length/1.5, dragon_width/1.5, dragon_height/2], center=true);
        }

        // Star scales along spine
        for(i=[-dragon_length/4:dragon_length/12:dragon_length/4]) {
            translate([i, 0, dragon_height/3])
                rotate([0, 0, i*30])
                scale([0.8, 0.8, 0.2])
                color("gold") star(5, 3, 2);
        }
    }
}

// Star primitive for constellation patterns
module star(points=5, outer=3, inner=2) {
    difference() {
        cylinder(h=1, r=outer, $fn=points*2);
        for(i=[0:points-1]) {
            rotate([0, 0, i*360/points + 180/points])
                translate([inner, 0, -0.5])
                    cube([inner*2, 1, 2], center=true);
        }
    }
}

// Dragon head with celestial features
module dragon_head() {
    union() {
        // Main head shape
        translate([dragon_length/2.2, 0, dragon_height/4])
            rotate([0, -10, 0])
            scale([1.3, 0.9, 0.9])
            sphere(d=dragon_length/4);

        // Snout
        translate([dragon_length/1.8, 0, dragon_height/4])
            rotate([0, -10, 0])
            scale([1, 0.7, 0.6])
            cylinder(h=dragon_length/8, r1=dragon_length/10, r2=dragon_length/15);

        // Horns with star tips
        for(side=[-1, 1]) {
            translate([dragon_length/2.3, side*dragon_width/6, dragon_height/3])
                rotate([0, side*15, 0])
                cylinder(h=dragon_height/4, r1=dragon_width/15, r2=dragon_width/25);
            translate([dragon_length/2.3, side*dragon_width/6, dragon_height/3 + dragon_height/4])
                color("silver")
                star(6, 2.5, 1.5);
        }

        // Eyes - glowing orbs
        for(side=[-1, 1]) {
            translate([dragon_length/1.9, side*dragon_width/8, dragon_height/4 + dragon_height/12])
                color("cyan") sphere(d=dragon_width/12);
        }
    }
}

// Constellation wings
module wings() {
    union() {
        // Left wing
        translate([0, -dragon_width/2, dragon_height/3])
            rotate([0, 0, -20])
            constellation_wing();

        // Right wing
        translate([0, dragon_width/2, dragon_height/3])
            mirror([0, 1, 0])
            rotate([0, 0, -20])
            constellation_wing();
    }
}

module constellation_wing() {
    // Main wing membrane
    color("navy") {
        translate([dragon_length/8, -dragon_width/2, 0])
            rotate([0, 90, 0])
            scale([1.5, 1, 0.1])
            cylinder(h=dragon_width*1.2, r1=dragon_length/3, r2=dragon_length/6);
    }

    // Wing bones with star joints
    for(i=[0:30:150]) {
        rotate([0, 0, i])
            translate([0, 0, 0])
            color("slategray")
            cylinder(h=dragon_length/2.5, r=dragon_width/25);

        // Star joints
        translate([cos(i)*dragon_length/3.5, -sin(i)*dragon_length/3.5, 0])
            color("lightblue") star(6, 2, 1);
    }
}

// Celestial tail with star trail
module dragon_tail() {
    // Main tail segments
    for(i=[0:6]) {
        translate([-dragon_length/4 - i*dragon_length/15, 0, dragon_height/6 - i*dragon_height/20])
            rotate([0, 0, -i*10])
            scale([1 - i*0.1, 1 - i*0.1, 1 - i*0.1])
            sphere(d=dragon_length/5 - i*dragon_length/20);

        // Star trail
        if(i % 2 == 0) {
            translate([-dragon_length/4 - i*dragon_length/15, 0, dragon_height/6 - i*dragon_height/20])
                color("yellow") star(5, 2, 1);
        }
    }
}

// Legs with star claws
module dragon_legs() {
    for(side=[-1, 1]) {
        // Front legs
        translate([dragon_length/4, side*dragon_width/3, -dragon_height/4])
            rotate([90, 0, side*10])
            cylinder(h=dragon_height/3, r=dragon_width/15);

        // Rear legs
        translate([-dragon_length/6, side*dragon_width/3, -dragon_height/4])
            rotate([90, 0, side*10])
            cylinder(h=dragon_height/3, r=dragon_width/15);

        // Star claws
        for(j=[0:4]) {
            translate([dragon_length/4, side*dragon_width/3, -dragon_height/4 - dragon_height/3])
                rotate([0, 0, j*72])
                translate([dragon_width/20, 0, 0])
                color("gold") cylinder(h=dragon_height/12, r=dragon_width/40);
        }
    }
}

// Assemble the complete Stellara dragon
union() {
    color("slateblue") dragon_body();
    dragon_head();
    color("navy") wings();
    color("slateblue") dragon_tail();
    color("slategray") dragon_legs();
}

// Base for 3D printing stability
translate([0, 0, -dragon_height/3])
    color("black")
    cube([dragon_length*1.2, dragon_width*1.5, wall_thickness], center=true);

// Thalor - Mythical Dragon-Horse Hybrid
// Realistic style, medium complexity

$fn = 32;

// Main colors
color_dragon = [0.2, 0.4, 0.1];
color_wings = [0.8, 0.7, 0.9];
color_horns = [0.9, 0.8, 0.6];
color_eyes = [1, 0, 0];
color_fire = [1, 0.5, 0];

// Main module
module thalor() {
    union() {
        // Body - muscular horse-like torso
        color(color_dragon) {
            translate([0, 0, 20]) {
                scale([1.5, 0.8, 1.2]) sphere(d=30);
            }

            // Neck
            translate([0, -15, 35]) {
                rotate([0, 15, 0]) scale([0.7, 0.7, 1.5]) sphere(d=20);
            }

            // Head - dragon-like with elongated snout
            translate([0, -30, 45]) {
                union() {
                    scale([0.8, 1.5, 0.8]) sphere(d=20);
                    translate([0, -8, -2]) scale([0.6, 0.8, 0.5]) sphere(d=15);
                }
            }
        }

        // Wings - feathered dragon wings
        color(color_wings) {
            // Left wing
            translate([-20, 0, 35]) {
                rotate([0, 0, 20]) {
                    for (i = [0:3]) {
                        translate([i*3, 0, -i*2]) {
                            rotate([0, 10*i, 0]) 
                            scale([1, 0.1, 0.8]) 
                            cube([8, 0.5, 12], center=true);
                        }
                    }
                    // Wing membrane
                    translate([6, 0, -4]) 
                    rotate([0, 30, 0]) 
                    scale([1, 0.1, 1.5]) 
                    cube([20, 0.5, 20], center=true);
                }
            }

            // Right wing (mirrored)
            mirror([1, 0, 0]) {
                translate([-20, 0, 35]) {
                    rotate([0, 0, 20]) {
                        for (i = [0:3]) {
                            translate([i*3, 0, -i*2]) {
                                rotate([0, 10*i, 0]) 
                                scale([1, 0.1, 0.8]) 
                                cube([8, 0.5, 12], center=true);
                            }
                        }
                        translate([6, 0, -4]) 
                        rotate([0, 30, 0]) 
                        scale([1, 0.1, 1.5]) 
                        cube([20, 0.5, 20], center=true);
                    }
                }
            }
        }

        // Legs - horse legs with dragon claws
        color(color_dragon) {
            for (x = [-15, 15]) {
                for (y = [5, -15]) {
                    translate([x, y, 0]) {
                        // Upper leg
                        cylinder(h=25, d1=8, d2=6);
                        // Lower leg
                        translate([0, 0, 12]) {
                            rotate([30, 0, 0]) cylinder(h=15, d1=6, d2=4);
                        }
                        // Hoof/claw
                        translate([0, 2, 23]) {
                            sphere(d=6);
                            translate([0, 0, -3]) cylinder(h=3, d=8);
                        }
                    }
                }
            }
        }

        // Tail - serpentine dragon tail
        color(color_dragon) {
            for (i = [0:10]) {
                translate([0, 15 + i*3, 20 - i*1.5]) {
                    rotate([0, -5*i, 5*sin(i*30)]) {
                        sphere(d=10 - i*0.7);
                    }
                }
            }
        }

        // Horns - curved dragon horns
        color(color_horns) {
            for (angle = [-30, 30]) {
                translate([0, -25, 50]) {
                    rotate([0, 0, angle]) {
                        rotate([30, 0, 0]) {
                            for (i = [0:8]) {
                                translate([0, 0, i*1.5]) {
                                    rotate([0, 5*i, 0]) {
                                        sphere(d=4 - i*0.3);
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }

        // Eyes - glowing red dragon eyes
        color(color_eyes) {
            for (x = [-5, 5]) {
                translate([x, -25, 47]) {
                    sphere(d=3);
                }
            }
        }

        // Scales detail on body
        color(color_dragon) {
            for (i = [0:5]) {
                for (j = [0:8]) {
                    translate([
                        15*sin(i*60 + j*20), 
                        -10 + j*3, 
                        25 + 5*cos(i*60)
                    ]) {
                        sphere(d=2.5);
                    }
                }
            }
        }

        // Fire breath effect (optional for display)
        color(color_fire) {
            translate([0, -40, 45]) {
                for (i = [0:5]) {
                    translate([0, -i*2, -i*1.5]) {
                        sphere(d=4 - i*0.5);
                    }
                }
            }
        }
    }
}

// Generate the model
thalor();

// UMBRAEL - Shadow Dragon of the Void
// A medium-complexity 3D printable mythical creature

$fn = 32;  // Smooth curves for better print quality

// Main assembly
module umbrael_creature() {
    color("DarkSlateGray") {
        union() {
            // Body - central mass
            translate([0, 0, 15]) scale([1.2, 1, 1]) sphere(10);

            // Head assembly
            translate([0, 18, 25]) rotate([0, 0, 0]) head();

            // Wing assembly - two pairs
            translate([0, -5, 25]) rotate([0, 20, 0]) left_wing();
            translate([0, -5, 25]) rotate([0, -20, 0]) mirror([1,0,0]) left_wing();

            // Tail segments
            translate([0, -20, 15]) rotate([0, 0, 0]) tail();

            // Leg assembly
            translate([5, 5, 0]) rotate([0, 0, 15]) leg();
            translate([-5, 5, 0]) rotate([0, 0, -15]) mirror([1,0,0]) leg();

            // Void energy orbs
            translate([8, 12, 28]) scale([1.2,1.2,1.2]) void_orb();
            translate([-8, 12, 28]) scale([1.2,1.2,1.2]) void_orb();
        }
    }
}

// Head module with horns and features
module head() {
    union() {
        // Main head shape
        scale([0.8, 1.2, 1]) sphere(8);

        // Snout
        translate([0, 8, -2]) scale([0.6, 1, 0.7]) sphere(5);

        // Horns - curved back
        for(i = [0:1]) {
            translate([i*6-3, 2, 6]) rotate([i*10-5, 0, 0]) 
                cylinder(h=12, r1=2, r2=0.5);
            translate([i*6-3, 2, 6]) rotate([i*10-5, 0, 30]) 
                cylinder(h=10, r1=1.5, r2=0.5);
        }

        // Eyes - glowing void
        color("Purple") {
            translate([3, 7, 2]) sphere(1.5);
            translate([-3, 7, 2]) sphere(1.5);
        }
    }
}

// Wing membrane with bone structure
module left_wing() {
    difference() {
        // Main wing membrane
        translate([0, 0, 0]) rotate([0, 0, -30]) 
            scale([1, 2.5, 0.1]) sphere(10);

        // Membrane holes for void energy flow
        for(i = [0:2]) {
            translate([0, i*8-8, -1]) rotate([0, 0, i*15-15]) 
                scale([0.3, 0.8, 1]) sphere(4);
        }
    }

    // Wing bone structure
    color("DimGray") {
        for(i = [0:3]) {
            translate([0, i*5-7.5, 0]) rotate([0, 0, i*10-15]) 
                cylinder(h=15, r=0.8);
        }
    }
}

// Segmented tail with void spikes
module tail() {
    union() {
        for(i = [0:4]) {
            translate([0, -i*6-5, -i*2]) 
                scale([1-i*0.1, 1-i*0.1, 1-i*0.1]) sphere(5-i);

            // Void spikes
            if(i % 2 == 0) {
                color("DarkViolet") {
                    translate([3, -i*6-5, -i*2]) rotate([0, 90, 0]) 
                        scale([0.3, 0.3, 1]) cylinder(h=3, r=1);
                    translate([-3, -i*6-5, -i*2]) rotate([0, -90, 0]) 
                        scale([0.3, 0.3, 1]) cylinder(h=3, r=1);
                }
            }
        }
    }
}

// Leg with claws
module leg() {
    union() {
        // Upper leg
        translate([0, 0, 7.5]) rotate([90, 0, 0]) 
            cylinder(h=15, r=2.5);

        // Lower leg
        translate([0, 0, 0]) rotate([90, 0, 0]) 
            cylinder(h=12, r=2);

        // Foot with claws
        translate([0, -6, -2]) scale([1.5, 2, 0.8]) sphere(3);

        // Claws
        for(i = [0:2]) {
            translate([i*2-2, -8, -2]) rotate([0, 0, 0]) 
                scale([0.3, 0.3, 1]) cylinder(h=3, r=1);
        }
    }
}

// Void energy orb with swirl pattern
module void_orb() {
    color("Indigo") {
        difference() {
            sphere(3);
            for(i = [0:3]) {
                rotate([0, i*45, 0]) 
                    translate([1, 0, 0]) rotate([90, 0, 0]) 
                        cylinder(h=6, r=0.5);
            }
        }
    }
}

// Create the creature scaled for 3D printing
