// Xelion - Mythical Crystal Lion Creature
// Realistic style with medium complexity

// Overall dimensions: ~80mm length x ~60mm width x ~70mm height

$fn = 32;
body_color = [0.7, 0.8, 0.9];  // Crystalline blue-white
mane_color = [0.9, 0.9, 1.0];  // Pure white
horn_color = [0.8, 0.9, 1.0];  // Light blue crystal
eye_color = [0.1, 0.3, 0.8];   // Deep blue

module xelion_creature() {
    // Main body
    color(body_color)
    union() {
        // Body core
        translate([0, 0, 15])
        scale([1.5, 1, 1.2])
        sphere(r = 20);

        // Chest
        translate([-15, 0, 10])
        scale([1.2, 0.8, 0.9])
        sphere(r = 18);

        // Hindquarters
        translate([20, 0, 12])
        scale([1.3, 1.1, 1.1])
        sphere(r = 22);
    }

    // Head
    color(body_color)
    translate([-35, 0, 35])
    scale([0.8, 0.7, 0.9])
    sphere(r = 25);

    // Mane - crystalline spikes
    color(mane_color)
    for(i = [0:15]) {
        rotate([0, 0, i * 22.5])
        translate([-38, 0, 40])
        rotate([90, 0, 0])
        scale([0.8, 0.8, 1.2])
        cylinder(r1 = 3, r2 = 1, h = 15);
    }

    // Crystal horns
    color(horn_color)
    union() {
        // Main horn
        translate([-45, 0, 55])
        rotate([15, 0, 0])
        cylinder(r1 = 4, r2 = 0, h = 20);

        // Side horns
        translate([-40, 8, 50])
        rotate([20, 10, 0])
        cylinder(r1 = 3, r2 = 0, h = 15);

        translate([-40, -8, 50])
        rotate([20, -10, 0])
        cylinder(r1 = 3, r2 = 0, h = 15);
    }

    // Eyes
    color(eye_color)
    union() {
        translate([-42, 8, 35])
        sphere(r = 2.5);
        translate([-42, -8, 35])
        sphere(r = 2.5);
    }

    // Legs
    color(body_color)
    union() {
        // Front legs
        translate([-20, 15, 0])
        rotate([0, 5, 0])
        cylinder(r = 8, h = 25);

        translate([-20, -15, 0])
        rotate([0, 5, 0])
        cylinder(r = 8, h = 25);

        // Hind legs
        translate([20, 15, 0])
        rotate([0, -5, 0])
        cylinder(r = 10, h = 30);

        translate([20, -15, 0])
        rotate([0, -5, 0])
        cylinder(r = 10, h = 30);
    }

    // Paws
    color(body_color)
    for(x = [-20, 20]) {
        for(y = [15, -15]) {
            translate([x, y, 0])
            scale([1.2, 1.2, 0.6])
            sphere(r = 10);
        }
    }

    // Wings - crystalline formations
    color(mane_color)
    union() {
        // Left wing
        translate([5, 25, 30])
        rotate([0, 0, -20])
        scale([0.3, 1, 0.8])
        sphere(r = 20);

        // Wing spikes
        for(i = [0:4]) {
            translate([5 + i*3, 35 + i*3, 35])
            rotate([0, 0, -30 - i*5])
            scale([0.5, 0.5, 1])
            cylinder(r1 = 2, r2 = 0, h = 8);
        }

        // Right wing
        translate([5, -25, 30])
        rotate([0, 0, 20])
        scale([0.3, 1, 0.8])
        sphere(r = 20);

        // Wing spikes
        for(i = [0:4]) {
            translate([5 + i*3, -35 - i*3, 35])
            rotate([0, 0, 30 + i*5])
            scale([0.5, 0.5, 1])
            cylinder(r1 = 2, r2 = 0, h = 8);
        }
    }

    // Tail
    color(body_color)
    union() {
        translate([35, 0, 20])
        rotate([0, 0, -30])
        scale([0.8, 0.8, 1])
        cylinder(r = 5, h = 30);

        // Tail crystal tip
        translate([55, -12, 10])
        scale([0.6, 0.6, 1.5])
        sphere(r = 8);
    }

    // Crystal formations on back
    color(horn_color)
    for(i = [0:3]) {
        translate([5 + i*10, 0, 40 + i*2])
        rotate([0, 0, i*15])
        scale([0.5, 0.5, 1])
        cylinder(r1 = 3, r2 = 0, h = 10);
    }
}

