// Zephirael - Wind Seraph of the Celestial Realm
// Medium complexity realistic style

// Colors
wing_color = [0.8, 0.9, 1];      // Icy blue-white
body_color = [0.9, 0.95, 1];     // Pearl white
horn_color = [0.9, 0.8, 0.6];    // Gold
eye_color = [0.3, 0.6, 1];       // Sky blue

// Module for horn with spiral texture
module horn(height=12, radius=2) {
    color(horn_color)
    union() {
        cylinder(h=height, r1=radius, r2=radius*0.3, $fn=16);
        // Spiral ridges
        for(i=[0:30:360]) {
            rotate([0, 0, i])
            translate([radius*0.7, 0, i/30])
            rotate([90, 0, 0])
            cylinder(h=height/12, r1=0.3, r2=0.1, $fn=8);
        }
    }
}

// Module for feathered wing
module wing(span=30) {
    color(wing_color) {
        translate([0, 0, span/4])
        rotate([0, 90, 0])
        difference() {
            // Main wing shape
            scale([1, 2, 0.5])
            sphere(d=span, $fn=32);

            // Wing cutout
            translate([span/4, span/4, 0])
            sphere(d=span*0.8, $fn=32);
        }

        // Primary feathers
        for(i=[0:3]) {
            rotate([0, 0, i*15-30])
            translate([span/2, 0, i*3-6])
            scale([0.1, 1, 0.05])
            sphere(d=span*0.6, $fn=8);
        }

        // Secondary feathers
        for(i=[0:2]) {
            rotate([0, 0, i*20-20])
            translate([span/3, 0, i*4-4])
            scale([0.08, 0.8, 0.04])
            sphere(d=span*0.4, $fn=8);
        }
    }
}

// Module for serpentine body segment
module body_segment(length=15, radius=8) {
    color(body_color) {
        difference() {
            // Main segment
            cylinder(h=length, r=radius, $fn=32);

            // Scale indents
            for(i=[0:3:length-3]) {
                translate([0, 0, i])
                cylinder(h=1.5, r=radius*0.9, $fn=32);
            }
        }

        // Side fins
        translate([radius, 0, length/2])
        rotate([0, 90, 0])
        scale([0.5, 1, 0.2])
        cylinder(h=radius*1.5, r1=radius*0.5, r2=0, $fn=16);

        translate([-radius, 0, length/2])
        rotate([0, -90, 0])
        scale([0.5, 1, 0.2])
        cylinder(h=radius*1.5, r1=radius*0.5, r2=0, $fn=16);
    }
}

// Module for tail segment
module tail_segment(length=10, radius=5) {
    color(body_color) {
        cylinder(h=length, r1=radius, r2=radius*0.8, $fn=24);

        // Tail fins
        translate([0, radius*0.8, length/2])
        rotate([90, 0, 0])
        scale([0.3, 1, 0.1])
        cylinder(h=radius*2, r1=radius, r2=0, $fn=16);

        translate([0, -radius*0.8, length/2])
        rotate([-90, 0, 0])
        scale([0.3, 1, 0.1])
        cylinder(h=radius*2, r1=radius, r2=0, $fn=16);
    }
}

// Module for head
module head() {
    color(body_color) {
        // Main head
        scale([1.2, 1, 1])
        sphere(d=20, $fn=32);

        // Snout
        translate([10, 0, 0])
        scale([1.5, 0.8, 0.6])
        sphere(d=12, $fn=32);

        // Eyes
        color(eye_color) {
            translate([8, 5, 3])
            sphere(d=3, $fn=16);
            translate([8, -5, 3])
            sphere(d=3, $fn=16);
        }

        // Eye shine
        translate([9.5, 5.5, 4])
        scale([0.3, 0.3, 0.1])
        sphere(d=1, $fn=8);
        translate([9.5, -5.5, 4])
        scale([0.3, 0.3, 0.1])
        sphere(d=1, $fn=8);

        // Horns
        translate([0, 8, 8])
        rotate([30, 0, 30])
        horn(12, 2);
        translate([0, -8, 8])
        rotate([30, 0, -30])
        horn(12, 2);
    }
}

// Main creature assembly
union() {
    // Head
    translate([0, 0, 40])
    head();

    // Body segments
    translate([0, 0, 25])
    body_segment(15, 8);
    translate([0, 0, 10])
    body_segment(15, 7);
    translate([0, 0, -5])
    body_segment(15, 6);

    // Tail segments
    translate([0, 0, -20])
    tail_segment(10, 5);
    translate([0, 0, -30])
    tail_segment(10, 4);
    translate([0, 0, -40])
    tail_segment(10, 3);

    // Wings
    translate([0, 0, 30])
    rotate([0, 0, 20])
    wing(25);
    translate([0, 0, 30])
    rotate([0, 0, -20])
    mirror([0, 1, 0])
    wing(25);

    // Celestial aura
    color([0.8, 0.9, 1, 0.3])
    translate([0, 0, 0])
    sphere(d=60, $fn=32);
}

// Aetheron - Mythical Sky Dragon of Pure Energy
// Wings, horns, flowing mane, and crystalline core

// Overall scale factor for easy resizing
scale_factor = 0.4;

// Main body
color([0.8, 0.9, 1.0]) 
union() {
    scale([scale_factor, scale_factor, scale_factor]) {

        // Central crystalline core
        translate([0, 0, 30]) {
            color([0.5, 0.7, 1.0]) sphere(r=12, $fn=32);
            difference() {
                color([0.6, 0.8, 1.0]) sphere(r=11, $fn=32);
                for(i=[0:3]) {
                    rotate([0, 0, i*90]) 
                        translate([0, 8, 0]) cube([3, 16, 22], center=true);
                }
            }
        }

        // Serpentine body
        union() {
            // Main torso
            translate([0, 0, 15]) 
                scale([1.2, 1, 1.8]) sphere(r=15, $fn=32);

            // Neck
            translate([0, -15, 35]) 
                rotate([-45, 0, 0]) 
                    scale([0.8, 0.8, 1.5]) cylinder(r1=8, r2=12, h=25, $fn=24);

            // Tail
            translate([0, 18, 10]) 
                rotate([60, 0, 0]) 
                    scale([1, 1, 0.8]) cylinder(r1=12, r2=4, h=40, $fn=24);
        }

        // Head
        translate([0, -25, 45]) {
            color([0.85, 0.9, 0.95]) {
                // Snout
                rotate([-45, 0, 0]) 
                    scale([0.8, 0.6, 1.2]) cylinder(r1=6, r2=4, h=20, $fn=24);

                // Main head
                sphere(r=10, $fn=32);

                // Horns - curved energy horns
                for(side=[-1, 1]) {
                    translate([side*8, -5, 6]) {
                        rotate([0, side*15, 0]) {
                            rotate_extrude(angle=90, $fn=16) 
                                translate([5, 0, 0]) 
                                    circle(r=2, $fn=8);
                        }
                    }
                }
            }
        }

        // Wings - ethereal membrane wings
        module wing() {
            color([0.7, 0.85, 1.0, 0.7]) {
                difference() {
                    // Main wing shape
                    translate([0, -30, 20]) {
                        rotate([0, 0, -15]) {
                            linear_extrude(height=1.5) {
                                polygon(points=[
                                    [0, 0], [25, -5], [35, -25], [30, -45], 
                                    [15, -55], [0, -50], [-5, -30], [-2, -10]
                                ]);
                            }
                        }
                    }
                    // Wing holes for ethereal effect
                    for(i=[0:2]) {
                        translate([15+i*8, -25-i*8, 21]) 
                            rotate([0, 0, 45]) 
                                scale([1, 1.5, 1]) cylinder(r=3, h=3, $fn=12);
                    }
                }
            }
        }

        // Left wing
        translate([-15, 0, 25]) rotate([30, 0, -30]) wing();

        // Right wing
        mirror([1, 0, 0]) translate([-15, 0, 25]) rotate([30, 0, -30]) wing();

        // Legs
        for(side=[-1, 1]) {
            translate([side*10, 0, 5]) {
                color([0.75, 0.85, 0.95]) {
                    // Upper leg
                    rotate([60, side*10, 0]) 
                        cylinder(r=3, h=15, $fn=12);

                    // Lower leg
                    translate([0, 0, 15]) 
                        rotate([30, 0, 0]) 
                            cylinder(r=2.5, h=12, $fn=12);

                    // Paw
                    translate([0, 0, 27]) {
                        sphere(r=4, $fn=12);
                        for(claw=[-1, 0, 1]) {
                            translate([claw*2, 0, 2]) {
                                rotate([90, 0, 0]) 
                                    cylinder(r=0.8, h=3, $fn=6);
                            }
                        }
                    }
                }
            }
        }

        // Energy mane along neck and back
        color([0.6, 0.8, 1.0, 0.8]) {
            for(i=[0:8]) {
                translate([sin(i*25)*3, -20+i*4, 40-i*2]) {
                    rotate([0, 0, cos(i*25)*10]) {
                        scale([0.3, 1, 0.3]) sphere(r=4, $fn=8);
                    }
                }
            }
        }

        // Tail energy spikes
        color([0.5, 0.7, 1.0]) {
            for(i=[0:5]) {
                translate([sin(i*30)*2, 20+i*6, 8-i*3]) {
                    rotate([0, 0, i*20]) {
                        cylinder(r1=2, r2=0.5, h=6, $fn=6);
                    }
                }
            }
        }
    }
}

