                    cylinder(h1 = 20, h2 = 0, r1 = 2.5, r2 = 0.5, $fn = 8);
            }

            // Eyes
            color("#FFFFFF") {
                for(x = [-6, 6]) {
                    translate([x, 5, 3]) sphere(d = 4);
                }
            }
            color("#000033") {
                for(x = [-6, 6]) {
                    translate([x, 6, 3]) sphere(d = 2);
                }
            }
        }

        // Crystalline wings
        for(side = [-1, 1]) {
            color(Crystal) {
                translate([0, side * 15, 15]) rotate([0, 90, side * 15]) {
                    // Main wing
                    difference() {
                        scale([1, 1.5, 0.1]) sphere(d = 40);
                        translate([0, 0, -5]) cube([50, 50, 10], center = true);
