                    }
                }
            }
        }

        // Tail segments
        color(Body) {
            for(i = [1:5]) {
                translate([0, 0, -i * 8 - 15]) {
                    rotate([0, 0, i * 15]) {
                        scale([1 - i * 0.1, 1 - i * 0.1, 1]) {
                            sphere(d = 12 - i * 1.5);
                        }
                    }
                }
            }
        }

        // Cosmic energy orbs around body
        color(Accent) {
            for(angle = [0:60:359]) {
                rotate([0, 0, angle]) {
                    translate([20, 0, sin(angle) * 5]) {
                        sphere(d = 3);
                    }
                }
            }
        }

        // Legs
        for(side = [-1, 1]) {
            color(Body) {
                translate([side * 8, 0, -20]) {
                    rotate([side * 15, 0, 0]) {
                        cylinder(h = 15, r = 3, $fn = 8);
                        translate([0, 0, 15]) sphere(d = 5);
                    }
                }
            }
        }
    }
