scale_factor = 0.5;  // Scale to ~75mm length
wall_thickness = 1.2;  // For 3D printing

module divioneth() {
    color("forestgreen")
    union() {
        // Serpentine body
        body();

        // Crystalline wings
        left_wing();
        right_wing();

        // Dragon-like head
        head();

        // Mystical tail with crystal tip
        tail();

        // Protective scales along spine
        spine_scales();
    }
}

module body() {
    // Main serpentine body with gentle curves
    for(i = [0:30]) {
        angle = i * 12;
        radius = 20 - (i * 0.3);
        translate([sin(angle) * 8, cos(angle) * 6, i * 1.5])
            scale([1 - (i * 0.01), 1 - (i * 0.01), 1])
            rotate([0, 0, angle])
            sphere(d=8, $fn=12);
    }
}

module head() {
    color("darkgreen")
    translate([-8, 0, 45])
    union() {
        // Snout
        cylinder(h=6, d1=6, d2=4, $fn=8);

        // Head base
        translate([0, 0, 3])
            sphere(d=8, $fn=12);

        // Eyes - glowing
        color("lime")
        translate([3, 3, 5])
            sphere(d=2, $fn=8);
        translate([3, -3, 5])
            sphere(d=2, $fn=8);

        // Horns
        color("darkolivegreen")
        translate([0, 2, 8])
            rotate([30, 0, 20])
            cylinder(h=6, d1=3, d2=1, $fn=6);
        translate([0, -2, 8])
            rotate([30, 0, -20])
            cylinder(h=6, d1=3, d2=1, $fn=6);
    }
}

module tail() {
    color("forestgreen")
    translate([0, 0, -5])
    union() {
        // Tapering tail
        for(i = [0:15]) {
            scale = 1 - (i * 0.05);
            translate([0, 0, -i * 1.2])
                sphere(d=8 * scale, $fn=10);
        }

        // Crystal tail tip
        color("lightblue")
        translate([0, 0, -18])
            scale([0.8, 0.8, 1.5])
            sphere(d=4, $fn=8);
    }
}

module left_wing() {
    color("lightblue")
    translate([4, 8, 35])
    rotate([0, 30, -20])
    union() {
        // Wing membrane with crystalline structure
        difference() {
            scale([1, 2, 0.3])
                sphere(d=12, $fn=6);

            // Cut crystalline pattern
            for(i = [1:3]) {
                rotate([0, 0, i * 30])
                    translate([0, -2, 0])
                    scale([0.2, 1, 1])
                    cube([20, 4, 2], center=true);
            }
        }

        // Wing support struts
        color("teal")
        translate([0, 6, 0])
            rotate([0, 0, 20])
            cylinder(h=8, d=1, $fn=6);
    }
}

module right_wing() {
    // Mirror of left wing
    mirror([0, 1, 0])
        left_wing();
}

module spine_scales() {
    color("darkgreen")
    for(i = [0:25]) {
        angle = i * 12;
        radius = 20 - (i * 0.3);
        translate([sin(angle) * 8, cos(angle) * 6, i * 1.5 + 4])
            rotate([0, 0, angle])
            scale([0.8, 0.2, 1.5])
            sphere(d=4, $fn=5);
    }
}

// Main render
scale(scale_factor)
    divioneth();

// Elestreon - A mythical serpent-dragon with celestial wings and starlight horns
// Designed for 3D printing with no overhangs and 2mm wall thickness

$fn = 32;

// Main body
module body() {
    color("darkgreen")
    union() {
        // Torso
        translate([0, 0, 10])
            scale([1, 0.6, 1])
            sphere(8);

        // Abdomen segments
        for(i = [1:3]) {
            translate([0, 0, 10 - i*6])
                scale([1 - i*0.15, 0.5 - i*0.08, 0.8])
                sphere(6);
        }

        // Chest plate scales
        for(i = [0:4]) {
            translate([-3 + i*1.5, 8, 10])
                rotate([90, 0, 0])
                scale([1, 0.3, 1])
                cylinder(h=1.5, r1=1.5, r2=0.5);
        }
    }
}

// Celestial wings with star patterns
module wings() {
    color("royalblue")
    union() {
        // Left wing
        translate([-10, 0, 15])
            rotate([0, 30, -30])
            scale([1, 0.2, 1])
            sphere(12);

        // Right wing
        translate([10, 0, 15])
            rotate([0, 30, 30])
            scale([1, 0.2, 1])
            sphere(12);

        // Wing membranes
        for(i = [0:3]) {
            // Left wing membrane
            translate([-8 - i*2, 0, 15 - i*2])
                scale([0.1, 0.5 - i*0.1, 1 - i*0.2])
                sphere(8);

            // Right wing membrane
            translate([8 + i*2, 0, 15 - i*2])
                scale([0.1, 0.5 - i*0.1, 1 - i*0.2])
                sphere(8);
        }
    }
}

// Starlight horns
module horns() {
    color("gold")
    union() {
        // Main horn
        translate([0, -5, 20])
            rotate([0, 0, 0])
            cylinder(h=12, r1=2, r2=0.5);

        // Side horns
        for(i = [-1:1]) {
            if(i != 0) {
                translate([i*3, -3, 18])
                    rotate([i*10, 0, 0])
                    cylinder(h=8, r1=1.5, r2=0.3);
            }
        }

        // Horn tips with star pattern
        translate([0, -5, 32])
            sphere(1);
    }
}

// Serpentine tail
module tail() {
    color("forestgreen")
    union() {
        // Tail segments
        for(i = [0:5]) {
            translate([0, 0, 4 - i*5])
                scale([1 - i*0.15, 1 - i*0.15, 1])
                sphere(5 - i*0.5);
        }

        // Tail fin
        translate([0, 0, -22])
            rotate([0, 0, 0])
            scale([0.3, 1, 0.5])
            sphere(4);
    }
}

// Head with reptilian features
module head() {
    color("darkgreen")
    union() {
        // Snout
        translate([0, 5, 15])
            scale([0.8, 1.2, 0.8])
            sphere(6);

        // Eyes
        color("yellow")
        for(i = [-1:1]) {
            if(i != 0) {
                translate([i*2.5, 6, 16])
                    sphere(1.5);

                // Eye slit
                color("black")
                translate([i*2.5, 6.5, 16.5])
                    scale([0.2, 1, 0.1])
                    sphere(1);
            }
        }

        // Nostrils
        color("black")
        for(i = [-0.5:0.5]) {
            if(i != 0) {
                translate([i, 7, 14])
                    cylinder(h=1, r=0.5);
            }
        }
    }
}

// Legs with claws
module legs() {
    color("darkgreen")
    union() {
        // Front legs
        for(side = [-1:1]) {
            if(side != 0) {
                // Leg
                translate([side*5, 0, 8])
                    rotate([0, 0, side*20])
                    cylinder(h=5, r=1.5);

                // Foot
                translate([side*6, -2, 3])
                    scale([1, 0.5, 0.3])
                    sphere(3);

                // Claws
                color("gray")
                for(claw = [-1:1]) {
                    translate([side*6 + claw*0.8, -4, 2])
                        rotate([0, 0, 0])
                        cylinder(h=1.5, r1=0.5, r2=0.1);
                }
            }
        }

        // Back legs
        for(side = [-1:1]) {
            if(side != 0) {
                // Leg
                translate([side*4, 0, -5])
                    rotate([0, 0, side*15])
                    cylinder(h=4, r=1.2);

                // Foot
                translate([side*5, -1.5, -8])
                    scale([0.8, 0.4, 0.3])
                    sphere(2.5);

                // Claws
                color("gray")
                for(claw = [-0.5:0.5]) {
                    translate([side*5 + claw*0.6, -2.5, -9])
                        cylinder(h=1.2, r1=0.4, r2=0.1);
                }
            }
        }
    }
}

// Scale pattern on body
module scales() {
    color("olive")
    for(i = [0:3]) {
        for(j = [0:5]) {
            translate([-5 + j*2, 0, 14 - i*3])
                scale([1, 0.1, 0.5])
                sphere(1);
        }
    }
}

// Main assembly
union() {
    body();
    wings();
    horns();
    tail();
    head();
    legs();
    scales();
}

// Empyreth - Mythical fire-creature with phoenix and dragon traits
// Realistic style, medium complexity

$fn = 30;
scale_factor = 0.4;
overall_scale = 90;

module empyreth() {
    scale([scale_factor, scale_factor, scale_factor]) {
        // Main body
        color("darkorange") {
            union() {
                // Torso - oval fire core
                translate([0, 0, 20]) {
                    scale([1.2, 1, 1.1]) sphere(r = 25);
                }

                // Chest armor plates
                for(i = [0:3]) {
                    rotate([0, 0, i * 90]) {
                        translate([0, 25, 25]) {
                            scale([0.8, 0.2, 0.6]) sphere(r = 15);
                        }
                    }
                }

                // Head
                translate([0, -35, 45]) {
                    // Skull base
                    color("orangered") {
                        scale([0.8, 1.2, 1]) sphere(r = 18);
                    }

                    // Beak-like snout
                    color("red") {
                        translate([0, -15, -5]) {
                            rotate([30, 0, 0]) {
                                scale([0.4, 1, 0.3]) cylinder(h = 20, r1 = 8, r2 = 3);
                            }
                        }
                    }

                    // Horns - curved back
                    color("darkred") {
                        for(side = [-1, 1]) {
                            translate([side * 12, -10, 8]) {
                                rotate([0, side * 20, 0]) {
                                    rotate([0, 0, -30]) {
                                        scale([0.3, 0.3, 1]) cylinder(h = 25, r1 = 5, r2 = 2);
                                    }
                                }
                            }
                        }
                    }

                    // Eyes - glowing slits
                    color("yellow") {
                        for(side = [-1, 1]) {
                            translate([side * 8, -12, 5]) {
                                scale([0.3, 0.1, 2]) sphere(r = 3);
                            }
                        }
                    }
                }

                // Wings - feathered fire wings
                color("orange") {
                    for(side = [-1, 1]) {
                        translate([side * 30, 0, 40]) {
                            rotate([0, 0, side * 20]) {
                                rotate([0, side * -20, 0]) {
                                    // Main wing structure
                                    difference() {
                                        scale([0.2, 1.8, 0.8]) sphere(r = 35);
                                        translate([0, 20, 0]) {
                                            scale([0.15, 1.5, 0.6]) sphere(r = 30);
                                        }
                                    }

                                    // Wing feathers
                                    for(i = [0:4]) {
                                        rotate([0, 0, i * 15 - 30]) {
                                            translate([0, -5 - i * 8, 5 + i * 3]) {
                                                scale([0.1, 0.6, 0.05]) sphere(r = 15);
                                            }
                                        }
                                    }
                                }
                            }
                        }
                    }
                }

                // Tail - serpentine flame tail
                color("red") {
                    for(i = [0:5]) {
                        translate([0, 25 + i * 8, 20 - i * 2]) {
                            scale([1 - i * 0.1, 1 - i * 0.05, 1 - i * 0.1]) sphere(r = 12 - i * 1.5);
                        }
                    }

                    // Tail flame tip
                    color("yellow") {
                        translate([0, 65, 8]) {
                            scale([0.5, 0.5, 2]) sphere(r = 5);
                        }
                    }
                }

                // Legs - digitigrade stance
                color("orangered") {
                    for(side = [-1, 1]) {
                        translate([side * 15, 10, 0]) {
                            // Upper leg
                            rotate([0, 0, side * 15]) {
                                cylinder(h = 20, r1 = 6, r2 = 5);
                            }

                            // Lower leg
                            translate([side * 2, 15, -8]) {
                                rotate([30, 0, side * 10]) {
                                    cylinder(h = 18, r1 = 5, r2 = 4);
                                }
                            }

                            // Foot with claws
                            translate([side * 3, 28, -15]) {
                                color("darkred") {
                                    scale([1.5, 2, 0.5]) sphere(r = 4);

                                    // Claws
                                    for(claw = [-1, 0, 1]) {
                                        translate([claw * 2, 6, -2]) {
                                            rotate([45, 0, 0]) {
                                                scale([0.3, 0.3, 1]) cylinder(h = 5, r1 = 1.5, r2 = 0.5);
                                            }
                                        }
                                    }
                                }
                            }
                        }
                    }
                }

                // Arms - humanoid but scaled
                color("orangered") {
                    for(side = [-1, 1]) {
                        translate([side * 25, -5, 35]) {
                            rotate([0, 0, side * -20]) {
                                cylinder(h = 18, r1 = 4, r2 = 3);

                                // Forearm
                                translate([0, -15, -3]) {
                                    rotate([20, 0, 0]) {
                                        cylinder(h = 15, r1 = 3, r2 = 2.5);
                                    }
                                }

                                // Hand
                                translate([0, -25, -6]) {
                                    scale([1, 1.5, 0.5]) sphere(r = 3);

                                    // Talons
                                    for(talon = [-1, 0, 1]) {
                                        translate([talon * 1.5, -4, -1]) {
                                            rotate([30, 0, 0]) {
                                                scale([0.3, 0.3, 1]) cylinder(h = 4, r1 = 1, r2 = 0.3);
                                            }
                                        }
                                    }
                                }
                            }
                        }
                    }
                }

                // Back spikes
                color("red") {
                    for(i = [0:4]) {
                        translate([0, -5 + i * 8, 30 + i * 2]) {
                            rotate([0, 0, 180]) {
                                scale([0.5, 0.5, 1]) cylinder(h = 8 - i, r1 = 3, r2 = 0.5);
                            }
                        }
                    }
                }

                // Magical fire aura - base only for printing
                color("yellow") {
                    translate([0, 20, 10]) {
                        scale([1.5, 1.8, 0.2]) sphere(r = 30);
                    }
                }
            }
        }
    }
}

// Generate the complete model
translate([0, 0, 0]) {
    scale([overall_scale/100, overall_scale/100, overall_scale/100]) {
        empyreth();
    }
}

// Galathon - Mythical Guardian Beast
// A realistic medium-complexity OpenSCAD model

module galathon() {
    color("DarkGoldenrod") {
        // Main body - muscular quadruped
        translate([0, 0, 15]) 
            scale([1.2, 0.8, 1]) 
                sphere(12, $fn=32);

        // Chest and shoulders
        translate([0, -8, 12]) 
            scale([1.4, 1, 0.8]) 
                sphere(10, $fn=24);
    }

    // Neck
    color("Goldenrod") {
        translate([0, -15, 22]) 
            rotate([30, 0, 0]) 
                cylinder(h=15, r1=6, r2=4, $fn=20);
    }

    // Head
    color("DarkGoldenrod") {
        translate([0, -25, 28]) {
            // Main head shape
            scale([0.8, 1.4, 0.9]) 
                sphere(8, $fn=24);

            // Snout
            translate([0, -8, -2]) 
                scale([0.6, 1, 0.7]) 
                    sphere(6, $fn=16);

            // Horns - curved backwards
            for(i = [-1, 1]) {
                translate([i*4, -2, 4]) {
                    rotate([0, i*15, 30]) 
                        cylinder(h=10, r1=1.5, r2=0.5, $fn=8);
                    translate([0, 0, 8]) 
                        rotate([0, 40, 0]) 
                            cylinder(h=5, r1=0.5, r2=1, $fn=6);
                }
            }

            // Eyes
            color("FireBrick") {
                for(i = [-1, 1]) {
                    translate([i*3, -3, 1]) 
                        sphere(1.5, $fn=12);
                }
            }

            // Nostrils
            color("Black") {
                for(i = [-1, 1]) {
                    translate([i*1.5, -11, -1]) 
                        sphere(0.8, $fn=8);
                }
            }
        }
    }

    // Wings - feathered style
    color("SlateGray") {
        for(side = [-1, 1]) {
            translate([side*12, -5, 25]) {
                rotate([0, 0, side*30]) {
                    // Wing base
                    rotate([0, side*30, 0]) 
                        scale([2, 0.5, 1]) 
                            cylinder(h=15, r=3, $fn=12);

                    // Primary feathers
                    for(i = [0:4]) {
                        translate([15 + i*2, 0, -i*1.5]) {
                            rotate([0, 0, side*(20 - i*5)]) 
                                scale([3, 0.2, 1]) 
                                    cylinder(h=8, r=1, $fn=4);
                        }
                    }
                }
            }
        }
    }

    // Legs
    color("DarkGoldenrod") {
        for(side = [-1, 1]) {
            // Front legs
            translate([side*8, -8, 0]) {
                // Upper leg
                rotate([0, side*10, 0]) 
                    cylinder(h=12, r=3, $fn=16);

                // Lower leg
                translate([0, 0, -12]) {
                    rotate([0, side*-20, 0]) 
                        cylinder(h=10, r=2.5, $fn=12);

                    // Paw
                    translate([0, 0, -10]) {
                        scale([1.2, 1, 0.6]) 
                            sphere(3, $fn=16);

                        // Toes
                        for(toe = [-1, 0, 1]) {
                            translate([toe*1.5, 0, -2]) {
                                rotate([0, toe*10, 0]) 
                                    cylinder(h=3, r=0.8, $fn=8);
                            }
                        }
                    }
                }
            }

            // Hind legs
            translate([side*6, 10, 0]) {
                // Upper leg - more muscular
                rotate([0, side*-15, 20]) 
                    scale([1.2, 1, 1.2]) 
                        cylinder(h=15, r=3.5, $fn=16);

                // Lower leg
                translate([0, 0, -15]) {
                    rotate([0, side*25, 0]) 
                        cylinder(h=12, r=2.8, $fn=12);

                    // Paw
                    translate([0, 0, -12]) {
                        scale([1.3, 1.1, 0.7]) 
                            sphere(3.5, $fn=16);

                        // Toes
                        for(toe = [-1.2, 0, 1.2]) {
                            translate([toe*1.6, 0, -2.5]) {
                                rotate([0, toe*8, 0]) 
                                    cylinder(h=3.5, r=0.9, $fn=8);
                            }
                        }
                    }
                }
            }
        }
    }

    // Tail - serpentine with scales
    color("DarkGoldenrod") {
        for(seg = [0:5]) {
            translate([0, 15 + seg*3, 15 - seg*1.5]) {
                scale([1 - seg*0.1, 1 - seg*0.05, 1 - seg*0.1]) 
                    sphere(6 - seg, $fn=20);
            }
        }

        // Tail tip
        translate([0, 32, 7]) {
            scale([0.5, 0.5, 2]) 
                sphere(2, $fn=16);
        }
    }

    // Scales pattern
    color("Goldenrod") {
        for(y = [-10:5:25]) {
            for(x = [-8:4:8]) {
                translate([x, y, 14 + sin(y*0.5)*2]) {
                    rotate([90, 0, 0]) 
                        cylinder(h=0.5, r=1.5, $fn=6);
                }
            }
        }
    }

    // Magical aura gems
    color("RoyalBlue") {
        // Chest gem
        translate([0, -5, 20]) 
            sphere(2, $fn=12);

        // Wing gems
        for(side = [-1, 1]) {
            translate([side*15, -5, 25]) 
                sphere(1.5, $fn=12);
        }
    }
}

