// Helioneth - Mythical Solar Phoenix Creature
// Based on ancient Egyptian solar deity Bennu bird

$fn = 32;

// Main creature assembly
module helioneth() {
    union() {
        // Golden body with solar energy core
        color("gold") body();

        // Radiant wings with feather details
        color([1, 0.8, 0]) wings();

        // Solar crest and horns
        color("goldenrod") crest();

        // Fiery tail plume
        color("orangered") tail();

        // Eagle-like head with beak
        color("gold") head();

        // Clawed feet
        color("darkgoldenrod") feet();

        // Solar disk halo
        color("yellow") solar_disk();
    }
}

// Central body with solar gem
module body() {
    translate([0, 0, 15]) {
        difference() {
            // Main torso
            scale([1.2, 0.8, 1]) sphere(12);

            // Solar gem cavity
            translate([0, 0, -5]) 
                scale([0.3, 0.3, 0.4]) sphere(10);
        }

        // Solar gem core
        color("yellow") 
            translate([0, 0, 6]) 
            sphere(3);
    }
}

// Radiant solar wings
module wings() {
    // Left wing
    translate([-8, 0, 20]) {
        rotate([0, 0, 20]) {
            // Upper wing segment
            scale([1.5, 0.2, 0.8]) sphere(8);

            // Lower wing segment with feathers
            translate([-5, 0, -8]) {
                scale([1.2, 0.15, 0.6]) sphere(6);

                // Wing feathers
                for(i = [0:4]) {
                    translate([-10 - i*2, 0, -5 - i*1.5]) {
                        rotate([0, 10 + i*5, 0]) 
                            scale([0.5, 0.1, 0.3]) cylinder(8, 1, 0.5);
                    }
                }
            }
        }
    }

    // Right wing (mirrored)
    mirror([1, 0, 0]) {
        translate([-8, 0, 20]) {
            rotate([0, 0, 20]) {
                scale([1.5, 0.2, 0.8]) sphere(8);

                translate([-5, 0, -8]) {
                    scale([1.2, 0.15, 0.6]) sphere(6);

                    for(i = [0:4]) {
                        translate([-10 - i*2, 0, -5 - i*1.5]) {
                            rotate([0, 10 + i*5, 0]) 
                                scale([0.5, 0.1, 0.3]) cylinder(8, 1, 0.5);
                        }
                    }
                }
            }
        }
    }
}

// Solar crest and horns
module crest() {
    translate([0, 0, 28]) {
        // Central crest
        cylinder(8, 3, 1);

        // Side horns
        for(angle = [-30, 30]) {
            rotate([0, 0, angle]) {
                translate([3, 0, 2]) {
                    rotate([0, 20, 0]) 
                        scale([0.3, 0.3, 1]) cylinder(6, 1, 0.5);
                }
            }
        }
    }
}

// Fiery tail plume
module tail() {
    translate([0, 0, 5]) {
        rotate([30, 0, 0]) {
            // Main tail
            scale([0.5, 0.5, 1.2]) cylinder(15, 4, 1);

            // Tail feathers
            for(i = [0:6]) {
                rotate([0, 0, i*60]) {
                    translate([0, 3, -5 - i*2]) {
                        rotate([0, 30, 0]) 
                            scale([0.3, 0.1, 0.8]) cylinder(8, 1, 0.3);
                    }
                }
            }
        }
    }
}

// Eagle-like head
module head() {
    translate([0, 0, 25]) {
        // Main head shape
        scale([0.8, 0.6, 1]) sphere(8);

        // Beak
        translate([0, 0, 8]) {
            rotate([0, 0, 0]) 
                scale([0.3, 0.2, 1]) cylinder(6, 2, 0);
        }

        // Eyes
        for(side = [-1, 1]) {
            translate([side*3, 3, 2]) {
                color("red") sphere(1);
            }
        }
    }
}

// Clawed feet
module feet() {
    // Left foot
    translate([-5, 0, 0]) {
        // Leg
        translate([0, 0, 8]) 
            cylinder(8, 1.5, 1.5);

        // Foot base
        translate([0, 0, 0]) {
