// Stellarethon - A star-born mythical dragon
// Features: Crystal wings, horned crest, scaled tail, and star patterns

// Overall scale factor for 3D printing
scale_factor = 1.5;

// Main body colors
body_color = "#4a5568";      // Slate gray
crystal_color = "#e6e6fa";   // Lavender
accent_color = "#ffd700";    // Gold

// Helper module for star patterns
module star_pattern(radius, height, count = 5) {
    for(i = [0:count-1]) {
        rotate([0, 0, i * 360/count])
        translate([radius, 0, 0])
        rotate([90, 0, 0])
        cylinder(h = height, r1 = 1.5, r2 = 0.5, $fn = 5);
    }
}

// Dragon head with horns and snout
module dragon_head() {
    color(body_color)
    union() {
        // Main head structure
        translate([0, 0, 10])
        scale([1.2, 1, 1.1])
        sphere(r = 8, $fn = 32);

        // Snout
        translate([10, 0, 8])
        scale([1.5, 0.8, 0.8])
        sphere(r = 6, $fn = 32);

        // Horn crest
        for(i = [-1, 0, 1]) {
            translate([i * 3, 0, 18])
            rotate([0, i * 10, 0])
            cylinder(h = 12, r1 = 2, r2 = 0.5, $fn = 8);
        }

        // Lower jaw
        translate([5, 0, 2])
        scale([1.3, 0.7, 0.4])
        sphere(r = 6, $fn = 32);

        // Eyes with star pattern
        translate([8, 5, 12])
        color(crystal_color)
        sphere(r = 2, $fn = 16);

        translate([8, -5, 12])
        color(crystal_color)
        sphere(r = 2, $fn = 16);
    }
}

// Crystal wing structure
module crystal_wing(side = 1) {
    color(crystal_color, 0.8)
    mirror([0, side, 0])
    translate([0, 10, 15])
    rotate([0, 0, side * 20])
    union() {
        // Main wing membrane
        linear_extrude(height = 1)
        polygon(points = [
            [0, 0],
            [20, 15],
            [25, 5],
            [15, -5],
            [5, -10]
        ]);

        // Wing supports
        for(i = [0:3]) {
            rotate([0, 0, i * 25])
            translate([5 + i * 5, 0, 0])
            cylinder(h = 1, r = 0.8, $fn = 8);
        }

        // Star patterns on wing
        for(i = [0:2]) {
            translate([15 + i * 5, i * 3 - 3, 0.5])
            star_pattern(2, 0.5);
        }
    }
}

// Scaled body segment
module body_segment(length, radius, scale_factor = 1) {
    color(body_color)
    union() {
        // Main segment
        scale([scale_factor, 1, 1])
        cylinder(h = length, r = radius, $fn = 32);

        // Scales
        for(i = [0:3]) {
            rotate([0, 0, i * 90])
            translate([length/2, radius + 1, 0])
            scale([0.8, 0.3, 0.5])
            sphere(r = 2, $fn = 16);
        }

        // Star accent
        translate([length/2, 0, radius + 0.5])
        color(accent_color)
        star_pattern(3, 1);
    }
}

// Tail with segments
module dragon_tail() {
    translate([-30, 0, 5])
    rotate([0, 10, 0])
    union() {
        // Tail segments decreasing in size
        body_segment(15, 6, 1);
        translate([-15, 0, -1])
        body_segment(12, 5, 0.9);
        translate([-25, 0, -1.5])
        body_segment(10, 4, 0.8);
        translate([-33, 0, -2])
        body_segment(8, 3, 0.7);

        // Tail spike
        translate([-40, 0, -2])
        color(crystal_color)
        rotate([0, 0, 180])
        cylinder(h = 6, r1 = 2, r2 = 0, $fn = 16);
    }
}

// Leg with claws
module dragon_leg(side = 1, front = 1) {
    translate([front * 8, side * 8, 0])
    rotate([0, -side * 15, 0])
    union() {
        // Upper leg
        color(body_color)
        cylinder(h = 8, r = 3, $fn = 16);

        // Lower leg
        translate([0, 0, -9])
        rotate([0, 30, 0])
        cylinder(h = 10, r = 2.5, $fn = 16);

        // Foot
        translate([0, 0, -20])
        rotate([0, 90, 0])
        scale([1.5, 0.8, 1])
        sphere(r = 3, $fn = 16);

        // Claws
        for(i = [-1, 0, 1]) {
            translate([i * 2, -3, 0])
            color(crystal_color)
            rotate([0, 90, 0])
            cylinder(h = 4, r1 = 1, r2 = 0.2, $fn = 8);
        }
    }
}

// Main assembly
