        rotate([0, 0, -20])
        left_wing();

        translate([-body_length/4, 0, leg_length + body_height - 2])
        mirror([0, 1, 0])
        rotate([0, 0, -20])
        left_wing();

        // Tail
        translate([-body_length/2 - 5, 0, leg_length + body_height/2])
        rotate([0, 0, -30])
        tail();

        // Legs
        for(i = [-1, 1]) {
            translate([body_length/4 * i, body_height/3, 0])
            rotate([0, 0, -20 * i])
            leg();
        }
    }
}

// Body segment
module body() {
    color("indigo")
    difference() {
        // Main body
        scale([1.2, 0.8, 0.6])
        sphere(d = body_height);

        // Scale pattern
        for(i = [0:6]) {
            rotate([0, 0, i * 60])
            translate([0, 0, body_height/2 - 2])
            cylinder(h = 4, r1 = body_height/3, r2 = 0, $fn = 6);
        }
    }
}

// Head with horns
module head() {
    color("midnightblue")
    union() {
        // Main head
        sphere(d = head_size);

        // Snout
        translate([head_size/2, 0, -head_size/4])
        scale([1.5, 0.7, 0.7])
        sphere(d = head_size/2);

        // Horns
        for(i = [-1, 1]) {
            translate([head_size/4, i * head_size/3, head_size/3])
            rotate([i * -20, 0, 0])
            cylinder(h = head_size * 1.5, r1 = head_size/8, r2 = 0);

            translate([head_size/3, i * head_size/3, head_size/4])
            rotate([i * -30, 0, 0])
            cylinder(h = head_size * 1.2, r1 = head_size/10, r2 = 0);
        }

        // Eyes
        color("crimson")
        for(i = [-1, 1]) {
            translate([head_size/2, i * head_size/3, head_size/6])
            sphere(d = head_size/5);
        }
    }
}

// Wing membrane
