    difference() {
        // Main wing shape
        scale([1, wing_span/2, 0.5])
        sphere(d = wing_span);

        // Wing holes for membrane structure
        for(i = [1:3]) {
            translate([wing_span/4 * i, 0, 0])
            scale([1, 1 - i * 0.2, 1])
            sphere(d = wing_span/3);
        }
    }
