    color("darkslateblue")
    union() {
        // Wing arm
        translate([0, -wing_span/4, 0])
        rotate([0, 0, -30])
        cylinder(h = wing_span/2, r1 = 2, r2 = 1);

        // Wing membrane
        translate([2, -wing_span/3, 0])
        rotate([0, 0, -45])
        wing_membrane();

        // Wing claws
        for(i = [0:2]) {
            translate([wing_span/4, -wing_span/3 - i * 5, 0])
