// Quintariel - Mythical five-winged sky serpent
// Realistic style, medium complexity, 3D printable

// Main parameters
body_length = 60;
body_radius = 8;
wing_span = 45;
head_size = 12;

// Colors
body_color = [0.2, 0.4, 0.8, 0.9]; // Mystical blue
accent_color = [0.9, 0.8, 0.3, 0.8]; // Gold
eye_color = [0.9, 0.1, 0.1, 1.0]; // Red

// Head module with horns
module head() {
    translate([body_length/2 + head_size/2, 0, 0]) {
        color(body_color) {
            // Main head sphere
            sphere(d = head_size, $fn = 32);

            // Snout
            translate([head_size/2, 0, 0])
                scale([1.5, 0.7, 0.7])
                sphere(d = head_size/2, $fn = 32);

            // Horns - two pairs
            for(side = [-1, 1]) {
                translate([head_size/4, side * head_size/3, head_size/3])
                    rotate([0, side * 20, 0])
                    cylinder(h = head_size/2, r1 = 2, r2 = 0.5, $fn = 8);

                translate([-head_size/4, side * head_size/2.5, head_size/4])
                    rotate([0, side * 30, 0])
                    cylinder(h = head_size/2.5, r1 = 1.5, r2 = 0.3, $fn = 8);
            }

            // Eyes
            color(eye_color) {
                for(side = [-1, 1]) {
                    translate([head_size/3, side * head_size/3, head_size/6])
                        sphere(d = 2, $fn = 16);
                }
            }
        }
    }
}

// Wing module with feather-like structure
module wing(angle, height) {
    rotate([0, 0, angle]) {
        translate([body_length/4, 0, height]) {
            color(body_color) {
                // Main wing membrane
                linear_extrude(height = 1)
                polygon(points = [
                    [0, 0],
                    [wing_span/3, wing_span/4],
                    [wing_span/1.5, wing_span/6],
                    [wing_span/2, 0],
                    [wing_span/1.5, -wing_span/6],
                    [wing_span/3, -wing_span/4]
                ]);

                // Wing bones
                color(accent_color) {
                    for(i = [1:3]) {
                        rotate([0, 0, (i-2) * 30])
                            cylinder(h = wing_span/2, r = 0.8, $fn = 6);
                    }
                }
            }
        }
    }
}

// Body segment with scales
module body_segment(pos, radius) {
    translate([pos, 0, 0]) {
        color(body_color) {
            // Main body cylinder
            cylinder(h = body_length/8, r = radius, center = true, $fn = 16);

            // Scales pattern
            for(angle = [0:45:315]) {
                rotate([90, 0, angle])
                    translate([0, radius, 0])
                    scale([1, 0.5, 0.3])
                    sphere(r = radius/3, $fn = 6);
            }
        }
    }
}

// Tail with magical flame
module tail() {
    color(body_color) {
        // Tapering tail
        for(i = [1:8]) {
            translate([-body_length/2 - i * 5, 0, 0])
                scale([1 - i/10, 1 - i/10, 1 - i/10])
                cylinder(h = 5, r = body_radius * (1 - i/10), center = true, $fn = 12);
        }

        // Magical flame at tip
        color([0.8, 0.3, 1.0, 0.7]) {
            translate([-body_length/2 - 45, 0, 0])
                sphere(d = 6, $fn = 16);
        }
    }
}

// Main assembly
union() {
    // Head
    head();

    // Body segments
    for(i = [0:7]) {
        body_segment(body_length/2 - i * 6, body_radius - i * 0.5);
    }

    // Five wings
    for(i = [0:4]) {
        wing(i * 72, 0);
    }

    // Tail
    tail();

    // Under-body fins
    color(accent_color) {
        for(i = [1:3]) {
            translate([body_length/2 - i * 15, 0, -body_radius])
                rotate([90, 0, 0])
                cylinder(h = body_radius * 2, r1 = 1.5, r2 = 0.5, $fn = 6);
        }
    }
}

