// Jormungandr - The Midgard Serpent
// Realistic style, medium complexity

$fn = 32;

// Main colors
color("darkgreen") {
    // Serpent body - main coil
    module body_coil() {
        union() {
            // Central coil ring
            rotate([90, 0, 0]) 
                difference() {
                    cylinder(h = 8, r1 = 20, r2 = 18);
                    cylinder(h = 10, r1 = 16, r2 = 14);
                }

