                    translate([0, i * 5, 0])
                    scale([1 - i*0.2, 1 - i*0.2, 1])
                        difference() {
                            cylinder(h = 6, r1 = 15 - i*2, r2 = 13 - i*2);
                            cylinder(h = 8, r1 = 12 - i*2, r2 = 10 - i*2);
                        }
            }
        }
    }

    // Scales texture on body
    module scales() {
        for(angle = [0:30:359]) {
            rotate([0, 0, angle]) {
                for(pos = [-20:5:20]) {
                    translate([pos, 0, 0])
                    rotate([90, 0, 0])
                    scale([1, 0.5, 0.3])
                        sphere(r = 1.5);
                }
            }
        }
    }

    // Serpent head
    module head() {
        translate([0, 25, 0]) {
            union() {
                // Main head shape
                scale([1.5, 1, 1])
                    sphere(r = 8);

                // Snout
                translate([10, 0, 0])
                    scale([2, 0.8, 0.8])
                        sphere(r = 5);

                // Horn-like ridges
                color("darkolivegreen") {
                    for(side = [-1, 1]) {
                        translate([5, side * 6, 6])
                            rotate([0, 30 * side, 0])
                            scale([0.5, 0.5, 1])
                                cylinder(h = 8, r1 = 2, r2 = 1);
                    }
                }

                // Eyes
                color("red") {
                    for(side = [-1, 1]) {
                        translate([7, side * 4, 3])
                            sphere(r = 1.5);
                    }
                }

                // Fangs
                color("ivory") {
                    for(side = [-1, 1]) {
                        translate([12, side * 2, -3])
                            rotate([0, 0, side * 30])
                                cylinder(h = 4, r1 = 1, r2 = 0.5);
                    }
                }
            }
        }
    }

    // Tail segment
    module tail_segment(len, r, angle) {
        rotate([0, 0, angle])
            translate([len/2, 0, 0])
                scale([len, r, r])
                    sphere(r = 1);
    }

    // Complete tail
    module tail() {
        color("forestgreen") {
            for(i = [1:15]) {
                tail_segment(
                    len = 15 - i * 0.8,
                    r = 5 - i * 0.3,
                    angle = -i * 15
                );
            }
        }
    }

    // Dorsal fins/spines
    module dorsal_spines() {
        color("darkslategray") {
            for(i = [0:30:330]) {
                rotate([0, 0, i])
                    translate([15, 0, 6])
                        scale([0.5, 0.2, 2])
                            sphere(r = 3);
            }
        }
    }
}

