                rotate([0, 0, 0]) {
                    difference() {
                        cylinder(h=body_height, r1=10, r2=12, $fn=32);
                        cylinder(h=body_height, r1=8, r2=10, $fn=32);
                    }
                }
            }

            // Head
            translate([0, 0, body_height + 5]) {
                sphere(r=head_size, $fn=32);

                // Horns
                for(i = [0, 1]) {
                    rotate([0, 0, i * 180]) {
                        translate([head_size - 2, 0, head_size - 2]) {
                            rotate([45, 0, 0]) {
                                cylinder(h=8, r1=2, r2=0.5, $fn=16);
                            }
