                        sphere(r=1.5, $fn=16);
                    }
                }

                // Beard
                translate([0, 0, -head_size]) {
                    cylinder(h=10, r1=3, r2=0, $fn=16);
                }
            }

            // Arms
            for(side = [0, 1]) {
                mirror([side * 2 - 1, 0, 0]) {
                    translate([10, 0, body_height * 0.7]) {
                        rotate([0, 0, -30]) {
                            // Upper arm
                            cylinder(h=15, r1=3, r2=2.5, $fn=16);

                            // Lower arm
                            translate([0, 12, 0]) {
                                rotate([0, 30, 0]) {
                                    cylinder(h=arm_length - 15, r1=2.5, r2=2, $fn=16);

                                    // Hand
                                    translate([0, arm_length - 15, 0]) {
                                        sphere(r=2, $fn=16);
                                    }
                                }
                            }
                        }
                    }
                }
            }

            // Legs
            for(side = [0, 1]) {
                mirror([side * 2 - 1, 0, 0]) {
                    translate([5, 0, 0]) {
                        // Upper leg
                        cylinder(h=15, r1=3, r2=2.5, $fn=16);

                        // Lower leg
                        translate([0, 0, -15]) {
                            rotate([0, 15, 0]) {
                                cylinder(h=15, r1=2.5, r2=2, $fn=16);

                                // Foot
                                translate([0, 0, -15]) {
                                    scale([2, 1, 0.5]) {
                                        sphere(r=3, $fn=16);
                                    }
                                }
                            }
                        }
                    }
                }
            }

            // Wings
            color("darkgoldenrod") {
                for(side = [0, 1]) {
                    mirror([side * 2 - 1, 0, 0]) {
                        translate([10, 0, body_height * 0.8]) {
                            rotate([0, 0, side * 30]) {
                                // Wing base
                                cylinder(h=5, r1=3, r2=2, $fn=16);

                                // Wing membrane
                                translate([0, 0, 2.5]) {
                                    rotate([0, -20, 0]) {
                                        scale([1, wing_span/20, 0.5]) {
                                            sphere(r=wing_span/2, $fn=32);
                                        }
                                    }
                                }

                                // Wing bones
                                for(i = [0:2]) {
                                    rotate([0, 0, i * 60 - 60]) {
                                        translate([0, 0, 2.5]) {
                                            cylinder(h=wing_span/2, r1=1, r2=0.5, $fn=8);
                                        }
                                    }
                                }
                            }
                        }
                    }
                }
            }

            // Tail
            color("goldenrod") {
                translate([0, -12, 0]) {
                    rotate([0, 0, 180]) {
                        for(i = [0:5]) {
                            translate([0, 0, -i * (tail_length/6)]) {
                                rotate([0, i * 10, 0]) {
                                    scale([1 - i * 0.15, 1 - i * 0.15, 1]) {
                                        sphere(r=3 - i * 0.5, $fn=16);
                                    }
                                }
                            }
                        }
                    }
                }
            }

            // Jewelry/Earrings
            color("silver") {
                for(side = [0, 1]) {
                    mirror([side * 2 - 1, 0, 0]) {
                        translate([head_size - 1, (side * 2 - 1) * 2, head_size * 0.5]) {
                            sphere(r=1, $fn=16);
                        }
                    }
                }
            }

            // Chest amulet
            translate([0, 0, body_height * 0.5]) {
                color("blue") {
                    difference() {
                        sphere(r=4, $fn=32);
                        cube([6, 6, 6], center=true);
                    }
                }

                // Chain
                color("silver") {
                    rotate([90, 0, 0]) {
                        cylinder(h=5, r=0.5, $fn=8);
                    }
                }
            }
        }
    }
}

